--
-- Table structure for table `pakaian_adat`
--

CREATE TABLE `pakaian_adat` (
  `id_pakaian` int(11) NOT NULL,
  `nama_pakaian` varchar(60) NOT NULL,
  `gambar_pakaian` varchar(50) NOT NULL,
  `preview` text NOT NULL,
  `deskripsi` longtext NOT NULL,
  `daerah` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Dumping data for table `pakaian_adat`
--

INSERT INTO `pakaian_adat` (`id_pakaian`, `nama_pakaian`, `gambar_pakaian`, `preview`, `deskripsi`, `daerah`) VALUES
(1, 'Pakaian Ulee Balang', 'uleeBalang.jpeg', 'Nama pakaian adat Aceh dikenal dengan Ulee Balang dan dipakai saat upacara adat. Untuk laki-laki disebut baju Linto Baro dan untuk perempuan disebut baju Daro Baro.\r\n', 'Pakaian adat Ulee Balang awalnya hanya dipakai oleh keluarga raja, sekarang pakaian adat tersebut menjadi pakaian adat Aceh dan dipakai untuk upacara adat. Nama pakaian adat Aceh dikenal dengan Ulee Balang. Di mana baju adat Nanggroe Aceh Darussalam untuk laki laki sering dikenal dengan sebutan baju Linto Baro. Sementara pakaian Aceh yang dikenakan oleh perempuan disebut baju Daro Baro. Konon, pakaian adat tersebut sudah ada sejak masa Kerajaan Samudera Pasai.<br /><br />\r\n\r\nLinto Baro merupakan pakaian tradisional Aceh yang biasa dikenakan oleh laki-laki. Di mana dipakai untuk pengantin laki-laki, tidak hanya itu, tapi juga untuk upacara-upacara adat atau pemerintahan. Pada saat memakai Linto Baro mengenakan baje meukasah (baju jas leher tertutup), cekak musang (jas yang dilengkapi celana panjang), kain sarung (ija lamgugap). Kemudian sebilah rencong atau siwah, meukeu top (bagian kepala ditutupi kopiah yang populer), tengkulok atau tompok.<br /><br />\r\n\r\nDaro Baro merupakan pakaian adat tradisional yang perempuan di Aceh. Pada pakaian Daro Baro terdiri dari baju kurung berlengan panjang, celana cekak musang dan sarung (ija pinggang), dan perhiasan. Desain pada pakaian adat perempuan dipengaruhi oleh budaya dari Melayu, Arab, dan China. Sehingga pakaian adat tersebut tampak longgar.\r\n', 'sumatra (aceh)'),
(2, 'Pakaian Bundo Kanduang', 'bundoKanduang.jpeg', 'Pakaian Bundo Kanduang atau disebut Limpapeh Rumah Nan Gadang merupakan pakaian adat untuk perempuan sudah menikah yang berasal dari Minangkabau, Sumatera Barat. ', 'Pakaian Bundo Kanduang atau disebut Limpapeh Rumah Nan Gadang merupakan pakaian adat yang berasal dari Minangkabau, Sumatera Barat. Pakaian Bundo Kanduang adalah pakaian adat Minangkabau yang umumnya dikenakan oleh perempuan yang telah menikah. Pakaian adat tersebut merupakan simbol dari pentingnya peran seorang ibu dalam sebuah keluarga. Limpapeh sendiri artinya tiang tengah dari bangunan rumat adat Sumatera Barat. Di mana peran limpapeh dalam memperkokoh bangunan rumah gadang adalah analogi dari peran ibu dalam sebuah keluarga. Jika limpapeh rubuh, maka rumah juga akan rubuh. Begitu juga ibu atau perempuan tidak pandai mengatur rumah tangga, maka keluarganya juga tidak akan bertahan lama. <br /><br />\r\n\r\nPakaian adat Bundo Kanduang memiliki keunikan terutama pada bagian kepala. Di mana bagian penutup kepala berbentuk menyerupai tanduk kerbau atau atap rumah gadang yang menjadi ciri khas masyarakat Minangkabau. Pakaian Bundo Kanduang secara umum memiliki desain yang berbeda-beda dari setiap nagari atau sub suku yang ada di Sumatera Barat. Perbedaan diantaranya terletak pada ornamen dan pernik yang digunakan serta perlengkapan lain seperti suntiang (hiasan kepala). Namun, ada beberapa kelengkapan khusus yang ada dalam jenis-jenis pakaian adat tersebut. Perlengkapan tersebut seperti tingkuluak (tengkuluk), baju batabue, minsie, lambak atau sarung, salempang, aksesoris seperti dukuah (kalung), galang (gelang).\r\n', 'sumatera barat (minangkabau)'),
(3, 'Pakaian Ulos', 'ulos.jpeg', 'Pakaian adat di Sumatera Utara adalah Ulos. Di mana pakaian adat tersebut dibuat dengan kain ulos yang berbahan sutra dan diproses dengan cara ditenun. ', 'Sumatera Utara merupakan salah satu provinsi di Indonesia yang berada bagian utara Pulau Sumatera Sumatera Utara dengan ibukota Medan memiliki keragaman budaya yang lengkap. Salah satu keragamaan yang ada di sana adalah pakaian adat. Pakaian adat di Sumatera Utara adalah Ulos. Di mana pakaian adat tersebut dibuat dengan kain ulos yang berbahan sutra dan diproes dengan cara ditenun. Ulos yang menjadi ciri khas bagi masyarakat Sumatera Utara sudah ada sejak lama dan secara turun temurun dikembangan masyarakat Batak. Membicarakan mengenai pakaian adat yang ada di Sumatera Utara disajikan dengan kain tenun yang indah dan unik. Kain Ulos merupakan kain tenun khas suku Batak dan secara turun temurun terus dikembangkan oleh masyarakat suku Batak.<br /><br />\r\n\r\nKain Ulos dibuat secara tradisional atau menggunakan alat tenun bukan mesin. Tempat pembuatan kain tradisiona di Sumatera Utara paling terkenal di daerah Tapanuli Utara. Kain ulos merupakan sebuah benda sacral symbol restu, kasih saying dan persatuang seperti yang ditorehkan dalam pepatah Batak “Ijuk pangihot ni hodong, Ulos pangihot ni holong” yang artinya “Jika ijuk adalah pengikat pelepah pada batangnya, maka Ulos adalah pengikat kasih sayang antara sesama”. Warna yang dominan pada kain Ulos biasanya merah, hita, dan putih yang dihiasi ragam tenunan dari benang emas atau perak. Motif yang khas adalah motif gorga. Di mana prosesnya masih menggunakan cara tradisional menjadikan kain ulos begitu otentik. <br /><br />\r\n\r\nPada pakaian adat, umumnya ulos dijadikan sebagai selempang baju. Di mana laki-laki akan mengenakan jas hitam lengkap dengan kemeja, sedangkan perempuan mengenakan kebaya berwarna merah. Pada laki-laki menggunakan tutup kepala yang disebut sabe-sabe dari Ulos Mangiring. Di bahunya disampirkan Ulos Ragi Hotang dan mengenakan sarung. Sementara untuk perempuan mengenakan Ulos Sadum yang disampirkan di kedua bahunya. Kemudian ulos tersebut dililit dengan Ulos Ragi Hotang dan mengenakan sarung suji.\r\n', 'sumatera utara'),
(4, 'Pakaian Aesan Gede', 'aesanGede.jpeg', 'Aesan Gede merupakan pakaian adat yang berasal dari Palembang, Sumatera Selatan. Aesan Gede merupakan pakaian yang melambangkan kebesaran dan keagungan.', 'Salah satu pakaian adat yang adat di Indonesia adalah Aesan Gede. Aesan Gede merupakan pakaian adat yang berasal dari Palembang, Sumatera Selatan. Aesan Gede merupakan pakaian yang melambangkan kebesaran. Di mana perlambang keagungan kerajaan Sriwijaya pada masa silam. Biasanya pakaian adat Aesan Gede dipergunakan untuk upacara pernikahan. Aesan dalam bahasa Palembang berarti baju atau pakaian. Pakaian adat tersebut merupakan salah satu jenis kain songket yang dulu dipergunakan para kaum bangsawan. <br /><br />\r\n\r\nAesan Gede merupakan pakaian adat Palembang, Sumatera Selatan. Pakaian adat yang dipakai penari gending sriwijaya biasanya seperti pakaian yang dipakai oleh pengatin perempuan baik aesan gede atau paksangko. <br /><br />\r\n\r\nPada pakaian pengantin perempuan yang biasa dikenakan juga oleh penari Gending Sriwija terdiri dari tiga bagian, yaitu kepala, tubuh, dan kaki. Pada bagian kepala memakai mahkota atau kesuhun dilengkapi suri (sisir) pengantin dan cucuk gelung. Di belakang sanggul ditancapkan juga bunga rampe (rampai) dan di bagian belakang kepala menggunakan kelapo setandan. Aksesoris lainnya berupa gandik, sumping, kembang ure, dan anting-anting. Pada bagian bahu, dipakaikan terate (teratai) yaitu penutup bahu dari bahan songket yang berbentuk kelopak teratai mekar. Di atas teratai dipakaikan kalung kebo munggah, di mana dalam teratai dipakaikan selempang sawit. Bagian atas atau baju dipakaikan dodot berbahan songket atau perada, kemudian ditambahkan ikat pinggang, pending bermotif bruk merak. Kain songket digunakan sebagai busana bagian bawah tubuh. Di mana disekitar ikat pinggang juga diselipkan kain pelangi atau jumputan. Untuk bagian lenggan dipakaikan aksesoris kecak bahu, gelang sempuru, gelang kano, gelang gepeng, dan gelang ulo betapo. <br /><br />\r\n\r\nSementara pada mempelai laki-laki harus dipasang kesuun berbentuk seperti mahkota yang berhias melati dan tebeng malu di atasnya. Pada bagian badan mengenakan kalung kebo munggah dan slempang sawir. Untuk tangan memakai gelang kulit bahu, gelang sempurun, gelang gepeng, dan gelang ulo betapo. Untuk kaki pengantin laki-laki mengenakan celano sutra. Celano sutra adalah celana berbahan sutra yang memiliki motif ukel.\r\n', 'sumatera selatan (palembang)'),
(5, 'Pakaian Teluk Belanga - Kebaya Lapuh', 'telukBelanga-kebayaLabuh.jpeg', 'Teluk Belanga merupakan nama pakaian adat pria yang berasal dari Provinsi Kepulauan Riau. Sementara itu pakaian adat untuk wanita adalah Kebaya Labuh.', 'Teluk Belanga merupakan nama pakaian adat pria yang berasal dari Provinsi Kepulauan Riau. Sementara itu pakaian adat untuk wanita di Kepulauan Riau adalah Kebaya Labuh. Kedua pakaian adat tersebut merupakan warisan kebudayaan tersebut sering dikenakan pada saat upacara adat atau pernikahan. <br /><br />\r\n\r\nCiri pakaian adat Ciri khas pakaian adat tersebut, Kebaya Labuh dan Teluk Belangga adalah panjangnya kebaya hingga menutupi lutut dengan bentuk kebaya tampak lebar dan terbuka. Pakaian Teluk Belanga terdiri baju, celana dan kain samping serta penutup kepala. Leher baju berkerah dan berkancing, jumlah lazimnya lima buah dan itu melambangkan rukun Islam. Cokek sama dengan baju cekak musang. Biasanya menggunakan kain songket. Cara memasangnya pun bervariasi, ada yang dilipat sirih didepan dengan bagian kanan sebelah atas. Ada pula yang dipunjut kesamping, tergantung siapa pemakainya. Pada penutup kepala juga bervariasi, berupa songkok, ikat kepala atau juga tanjak. Tanjak atau ikat kepala dibuat dari jenis kain yang sama dengan baju dan celana. Pakaian Teluk Belanga hadir dengan warna polos, seperti hitam, abu-abu, atau warna lain yang netral. <br /><br />\r\n\r\nSementara pakaian Kebaya Labuh sering disebut juga kebaya panjang. Di mana belah labuh atau belah dada juga terdiri atau baju kain dan selendang. Panjang lengan Kebaya Labuh kira-kira dua jari dari pergelangan tangan. Sehingga gelang yang dipakai akan terlihat. Untuk lebar lengan kira-kira tiga jari dari permukaan lengan. Di mana kedalaman baju bervariasi, ada yang sampai ke betis atau sedikit keatas. Baju agak longgar dan tidak boleh diraut (dikecilkan) di bagian yang dapat menunjukkan ukuran dan bentuk pinggang serta gaya pinggul. Kebaya Labuh sering dipadukan dengan kain batik seperti kain cual. Untuk pernikahan, mempelai pria mengenakan penutup kepala yang disebut Tanjak. Dilansir dari situs Kementerian Pendidikan dan Kebudayaan (kemdikbud), kaum perempuan Melayu Lingga mempunyai berbagai pakaian Melayu yang kenal dikenal sejak lama. Kebaya Labuh termasuk pakaian tradisional perempuan Melayu Lingga, Kepulauan Riau.\r\n', 'sumatera (kepulauan riau)'),
(6, 'Pakaian Kurung Tanggung', 'kurungTanggung.jpeg', 'Pakaian adat Jambi yang dikenakan saat upacara pernikahan, disebut dengan Baju Kurung Tanggung. ini merupakan pakaian adat masyarakat Melayu.', 'Pakaian adat Jambi tampak pada busana yang dikenakan saat upacara pernikahan, yang disebut dengan Baju Kurung Tanggung. Baju Kurung sendiri merupakan pakaian adat masyarakat Melayu yang tersebar di Brunei Darussalam, Indonesia, Malaysia, maupun Singapura. Baju Kurung untuk pernikahan ini disebut dengan Baju Kurung Tanggung.<br /><br /> \r\n\r\nPakaian Adat Pria Jambi Disebut Pakaian Adat Kurung Tanggung lantaran baju ini hanya memiliki panjang lengan sampai bawah siku dan tidak sampai pergelangan tangan. Baju Kurung Tanggung didesain demikian karena masyarakat berharap laki-laki Jambi harus tangkas dan cekatan dalam mengerjakan sesuatu. Umumnya, pakaian adat pria Jambi ini terbuat dari beludru berwarna dominan merah. Baju itu kemudian dihiasi dengan sulaman benang emas bermotif tagapo atau bunga tabur, melati, dan bunga berantai. Adapun sulaman berwarna emas sendiri melambangkan kesuburan dan kekayaan tanah Melayu. Pria yang mengenakan pakaian adat akan dilengkapi dengan beberapa aksesori, seperti lacak, yaitu penutup kepala khas Jambi. Lacak yang dikenakan biasanya memiliki bahan dan warna yang senada dengan baju, lengkap dengan sulaman emas. Selain lacak, seorang pria juga akan celana berbahan beludru dengan sulaman emas yang senada dengan bajunya. Aksesori berikutnya berupa sarung songket yang dililitkan dengan ikat pinggang atau sabuk yang terbuat dari lempengan tembaga. Tak lupa sebuah keris yang diselipkan di pinggang, serta alas kaki berupa selop dari bahan dan warna yang sama dengan baju. <br /><br />\r\n\r\nPakaian Adat Wanita Jambi Sama seperti pria, wanita Jambi juga mengenakan baju kurung tanggung lengkap dengan hiasan emasnya. Perbedaan terletak pada tutup kepala dan sejumlah aksesori yang dipakai oleh kaum wanita Jambi. Jika pria mengenakan lacak, wanita Jambi akan mengenakan mahkota yang terbuat dari kain beludru dan lempengan tembaga yang disusun 5-7 berbentuk duri pandan. Tak hanya itu, di bagian kepala ini juga akan mengenakan sanggul lipat pandan yang berfungsi untuk menusukkan hiasan kepala. Aksesoris wanita jauh lebih banyak daripada pria, yaitu berupa anting, kelung, cincin, gelang, dan sebagainya. Anting yang dikenakan kaum wanita umumnya bermotif kupu-kupu atau gelang banjar. Untuk kalung ada tiga jenis, yaitu kalung tapak, kalung bertingkat, dan kalung rantai sembilan. Bagian jari juga dilengkapi dengan dua jenis cincin, yaitu pacat kenyang dan cincin kijang atau capung. Gelang yang dikenakan pun cukup banyak, seperti gelang kilat bahu, gelang kano, gelang ceper, hingga gelang buku beban yang semuanya dipasang di lengan. Pakaian adat wanita Jambi juga dilengkapi dengan ikat pinggang dari lempengan tembaga, alas kaki berupa selop, dan gelang kaki yang disebut gelang nago betapo.', 'sumatra timur (jambi)'),
(7, 'Pakaian Paksian', 'paksian.jpeg', 'Pakaian adat Paksian berasal dari Provinsi Bangka Belitung. Laki-laki memakai jubah panjang sebatas betis dan perempuan memakai baju kurung berbahan beludru warna merah.', 'Pakaian adat Paksian berasal dari Provinsi Bangka Belitung. Laki-laki memakai jubah panjang sebatas betis, selempang, penutup kepala, celana, pending, serta selop. Pending adalah hiasan dada atau ikat pinggang yang terbuat dari lempeng emas atau perak dan berkerawang atau sebagainya.<br /><br />\r\n\r\nAdapun untuk perempuan, memakai baju kurung yang berbahan beledu (beludru) warna merah. Selain itu, busana Paksian perempuan juga dilengkapi penutup dada dan kain cual yang merupakan produk tenun asli Bangka dan terbuat dari mentok.<br /><br />\r\n\r\nSebagai pelengkap, baju perempuan juga ditambah dengan hiasan kepala, kalung, gelang, anting, pending, kembang cempaka dan goyang, kuntum cempaka, daun bambu, sapit udang, sari bulan, pagar tenggalung, dan tutup sanggul.<br /><br />\r\n\r\nPakaian adat Bangka Belitung adalah perpaduan dari adat Arab dan Cina. Jubah panjang yang dipakai oleh pengantin pria Bangka Belitung memang jubah panjang khas jubah Arab. Selop yang dikenakan juga dikenal sebagai sandal Arab. Pada pengantin wanita, ornamen khusus pada mahkotanya dinamakan Paksian. Pada bajunya juga dihias dengan ronce bunga melati.<br /><br />\r\n', 'sumatera (bangka belitung)'),
(8, 'Pakaian Rejang Lebong', 'rejangLebong.jpeg', 'Pakaian adat dari Bengkulu adalah baju Rejang Lebong. Pakaian ini biasanya digunakan untuk pernikahan, maupun untuk festival dan penyambutan acara khusus.', 'Salah satu pakaian adat dari Bengkulu adalah baju Rejang Lebong. Baju Rejang Lebong memiliki campuran budaya dari Melayu Jambi, Riau, Deli, palembang, dan Lampung. Sehingga pakaian adat Bengkulu memiliki keunikan dan ciri khas. Pakaian ini biasanya digunakan untuk busana pengantin dalam pernikahan, maupun sebagai busana tari tradisional untuk festival dan penyambutan acara khusus. <br /><br />\r\n\r\nPakaian Rejang Lebong pria Untuk pengantin maupun penari pria dengan busana Rejang Lebong biasanya terdiri dari atasan berbentuk jas tertutup dengan lengan panjang, biasanya menggunakan warna hitam, merah tua, atau biru tua. Celana panjang berbahan satin yang warnanya senada dengan atasan. Cek\'ulew sebagai penutup kepala berbentuk runcing ke atas. Selempang dari kanan ke kiri Songket yang dililitkan pada pinggang, biasanya bermotif emas. Pending yang berfungsi sebagai ikat pinggang untuk mengokohkan songket. Keris, biasanya diselipkan dipinggang sebagai pelengkap busana Rejang Lebong pada pria. <br /><br />\r\n\r\nUntuk pakaian pengantin pada pria, bedanya hanya pada baju atau jas tertutup bertabur hiasan seperti lempengan koin. Selain itu juga mengenakan gelang, kalung sribulan, dan kalung emping. Bahkan untuk beberapa adat, pengantin pria tidak mengenakan topi melainkan mahkota atau sunting untuk pria. <br /><br />\r\n\r\nPakaian Rejang Lebong untuk wanita, yaitu Baju kurung lengan panjang berbahan kain beludru bertabur lempeng logam warna kuning emas. Umumnya warna baju yang dipilih adalah merah tua, lembayung, biru, dan hitam. Songket berbahan sutera bermotif emas dan digunakan sebagai bawahan. Pending sebagai ikay pinggang Konde yang dilengkap dengan tusuk konde dan mahkota dengan hiasan sungting atau kembang goyang. Mengenakan gelang, anting, dan kalung\r\n', 'sumatera selatan (bengkulu)'),
(9, 'Tulang Bawang', 'tulangBawang.jpeg', 'Pakaian adat di Provinsi Lampung bernama Tulang Bawang. Pakaian ini menjadi salah satu peninggalan budaya nasional. Digunakaan untuk pesta pernikahan maupun tari untuk simbol penghormatan budaya asli.', 'Pakaian adat di Provinsi Lampung bernama Tulang Bawang. Pakaian ini menjadi salah satu peninggalan budaya nasional. Baju adat Tulang Bawang masih digunakan hingga saat ini baik untuk pesta pernikahan maupun sebagai busana tari untuk simbol penghormatan budaya asli. Dalam buku Pakaian dan Perhiasan Pengantin Tradisional Lampung (2014) oleh Al Buchari dan kawan-kawan, baju adat Lampung dibedakan menjadi Lampung Pepadun dan Lampung Saubatin atau Pesisir. Secara umum baju adat keduanya cukup sama, hanya saja yang membedakan pada warna. Jika Lampung Pepadun identik dengan warna putih, sedangkan Lampung Saubatin pada warna merah. Pengantin pria maupun perempuan juga dilengkapi dengan pernak-pernik yang digunakan. <br /><br />\r\n\r\nPakaian adat Tulang Bawang untuk laki-laki cukup sederhana dibandingkan pakaian wanita. Pengantin pria menggunakan baju lengah panjang berwarna putih atau merah, celana panjang hitam atau senada dengan warna atasan. Sarung tumpal yang merupakan kain sarung khas Lampung dililitkan pada celana sepanjang lulut kaki. Kemudian ditambahkan dengan khikat akhir atau selendang bujur sangkat yang digunakan melingkar pada pundah untuk menutupi bahu. <br /><br />\r\n\r\nBaju adat Lampung perempuan Berdasarkan jurnal Kajian Makna pada Aksesori Pakaian Adat Lampung Pepadun (2018) oleh Roveneldo, untuk perempuan mengenakan baju Selappai yaitu baju tanpa lengan dengan hiasan rumbai ringgit di bawahnya. Dilengkapi dengan baju Bebe yang merupakan sulaman benang satin berbentuk bunga teratai. \r\n\r\n', 'sumatera (lampung)'),
(10, 'Pakaian Pangsi', 'pangsi.jpeg', 'Baju Pangsi dikenakan oleh masyarakat Banten dalam kesehariannya. Dan dipadukan dengan celana komprang. Selain itu kerap dipakai latihan silat tradisional / debus', 'Baju Pangsi dikenakan oleh masyarakat Banten dalam kesehariannya. Baju ini dipadukan dengan celana komprang. Selain itu, pakaian ini kerap dipakai dalam latihan silat tradisional atau debus yang kerap digelar masyarakat Banten.<br /><br />\r\n\r\nPangsi merupakan singkatan dari Pangeusi “Numpang ka Sisi”, yakni pakaian penutup badan yang cara pemakaiannya dibelitkan dengan cara menumpang, seperti menggunakan sarung. Pangsi terdiri dari tiga susunan, yaitu Nangtung, Tangtung, dan Samping.', 'jawa (banten)'),
(11, 'Pakaian Baju Sadaria - Kebaya Encim', 'bajuSadaria-kebayaEncim.jpeg', 'Pakaian adat betawi yaitu Baju Sadaria untuk para laki-laki dan dipasangkan dengan Kebaya Encim untuk wanita. Pakaian ini sering digunakan dalam festival Abang none.', 'Baju Sadaria digunakan oleh para laki-laki Betawi dan seringkali dipasangkan dengan Kebaya Encim. Pakaian ini sering digunakan dalam festival Abang None dan juga Pekan Raya Jakarta. Penampilan pakaian yang sederhana namun bersahaja ini tentu familiar bagi Grameds semua.<br /><br />\r\n\r\nBaju Sadaria ini berupa baju taqwa atau baju koko yang berkerah Shanghai (kerah tertutup) setinggi 3-4 cm. Umumnya pakaian ini berwarna putih dan berlengan panjang. Jika dilihat dari sejarah, pakaian ini banyak terinspirasi oleh budaya China yang para lelakinya banyak mengenakan baju koko. Disebut baju koko karena pakaian ini banyak dipakai oleh para koko (kakak laki-laki dalam bahasa Mandarin).<br /><br />\r\n\r\nSementara Kebaya Encim untuk wanita. Baik dari gadis remaja, perempuan muda, hingga perempuan setengah baya Betawi menggemari kebaya yang simpel, sederhana, namun tetap bisa menampilkan kesan keanggunan ini.<br /><br />\r\n\r\nPakaian adat Betawi satu ini kerapkali digunakan saat momen-momen Pekan Raya Jakarta, seragam karyawati instansi pemerintah dan swasta, peringatan hari besar, menerima tamu istimewa, pentas seni budaya, dan acara-acara lainnya.', 'jawa (betawi)'),
(12, 'Pakaian Baju Bedahan - Kebaya Sunda', 'bajuBedahan-kebayaSunda.jpeg', 'Pakaian adat sunda yaitu Baju Bedahan untuk para laki-laki dan Kebaya Sunda untuk para wanita. Pakaian ini digunakan oleh Suku Sunda yang memiliki status sosial menengah.', 'Baju Bedahan merupakan salah satu pakaian adat Jawa Barat. Baju ini digunakan oleh Suku Sunda yang memiliki status sosial menengah. Baju Bedahan berbentuk baju atasan untuk kaum pria. Di masa penjajahan Belanda, baju ini biasanya dipakai pegawai negeri dan pamongpraja. Baju bedahan berwarna putih dipadukan dengan kain kebat batik, sabuk, ikat kepala, arloji berantai emas, alas kaki dan ikat pinggang. Saat dipakai, baju ini akan terlihat seperti jas takwa dengan kerah di leher. <br /><br />\r\n\r\nKaum wanita mengenakan Kebaya Sunda. Kebaya ini berfungsi sebagai atasan, layaknya kebaya dari daerah lain. Beda antara Kebaya Jawa dan Sunda tertekat pada desain kerahnya. Jika kebaya Jawa berkerah V (V-neck), maka Kebaya Sunda berdesain kerah U (U-neck). Kebaya Sunda juga didesain panjang hingga menutupi pinggul dan paha. Bahkan di beberapa desain bisa lebih panjang dari itu. Warna Kebaya Sunda umumnya cerah, seperti merah, ungu muda, atau putih. Pengguna kebaya ini merata dari strata sosial rendah hingga menengah.\r\n', 'jawa barat (sunda)'),
(13, 'Pakaian Kesatriaan Ageng', 'kesatriaanAgeng.jpeg', 'Pakaian adat Yogyakarta tersebut diberi nama dengan istilah Kesatriaan Ageng. pakaian ini terbuat dari bahan laken atau bisa dibilang serupa dengan kain beludru.', 'Pakaian adat Yogyakarta tersebut diberi nama dengan istilah Kesatriaan Ageng. Pakaian ini terdiri dari beberapa bagian baju, yaitu surjan sebagai atasan, celana panjang hitam, kain batik yang di pinggang yang dililitkan sampai atas lutut, dan juga hiasan kepala. Dengan seluruh perpaduan pakaian tersebut, tentunya akan menghasilkan tampilan yang berwibawa.<br /><br />\r\n\r\njenis pakaian Surjan Kesatrian Ageng yang terbuat dari bahan laken atau bisa dibilang serupa dengan kain beludru. Untuk warnanya sendiri lebih dominan ke hitam polos dan dihiasi oleh bordiran keemasan. Motif tersebut biasanya dihiasi dengan model daun keluwih dan sulur. Masing masing motif melambangkan beberapa arti yang cukup simbolis.<br /><br />\r\n\r\nUntuk daun kluwih, maka diartikan sebagai keselamatan dan perlindungan keraton. Sedangkan daun sulur melambangkan kehidupan yang berjalan secara terus menerus agar tetap tumbuh. Karena makna simbolis tersebut, baju tersebut sering dipakai oleh pejabat keraton, pengantin, sampai dengan prajurit yang bekerja di keraton untuk mempertahankan kehidupan di keraton.<br /><br />\r\n\r\nBaju Kesatrian Ageng juga menjadi salah satu pakaian adat Jogja yang dilengkapi oleh topi. Ini melambangkan watak ksatria yang tidak akan pernah pantang mundur selangkah pun ketika menghadapi musuh untuk mencapai tujuan. Selain itu, pakaian ini juga melambangkan kemampuan diri sendiri agar dapat mengabdi dengan hati yang bersih.<br /><br />\r\n\r\nKesatrian Ageng juga sering diartikan sebagai pakaian yang mencerminkan keberanian dengan warna hitam sebagai ciri khasnya. Sedangkan warna celana serta topi diambil dari warna alam untuk melambangkan keberanian dan juga keabadian. Adapun warna emas serta kuning pada hiasan surjan melambangkan keagungan serta keluhuran.', 'jawa tengah (yogyakarta)'),
(14, 'Pakaian Kebaya Jawa', 'kebayaJawa.jpeg', 'Kebaya menjadi salah satu adat Jawa Tengah yang populer dan sering digunakan. Juga bisa terbuat dari beragam jenis bahan beludru, sutra, katun, hingga nilon.', 'Pakaian adat setiap daerah memiliki ciri khas tersendiri, termasuk dari provinsi Jawa Tengah. Pakaian tersebut biasanya digunakan dalam acara kesenian, pernikahan hingga upacara adat.<br /><br />\r\n\r\nKebaya menjadi salah satu adat Jawa Tengah yang populer dan sering digunakan masyarakat. Kebaya Jawa Tengah bisa terbuat dari beragam jenis bahan beludru, sutra, katun, hingga nilon. Perpaduan bahan itu membuat baju kebaya menjadi lebih tampak cantik saat digunakan.<br /><br />\r\n\r\nDalam filosofi Jawa, pakaian adat kebaya memiliki makna kesabaran. Potongan kebaya yang mengikuti bentuk tubuh bermaknna perempuan harus bisa menyesuaikan diri dengan keadaan dan menjaga diri sendiri. Selain itu, kebaya juga sarat mengandung makna bahwa perempuan itu memiliki sifat lemah lembut dan sabar.', 'jawa tengah '),
(15, 'Pakaian Pesa\'an', 'pesaan.jpeg', 'Pesa\'an adalah baju adat khas dari Madura, provinsi Jawa Timur. Baju Pesa\'an menjadi salah satu simbol utama yang menjadi wakil budaya baju adat Jawa Timur di Nusantara. ', 'Pesa\'an adalah baju adat khas dari Madura, provinsi Jawa Timur. Baju Pesa\'an menjadi salah satu simbol utama yang menjadi wakil budaya baju adat Jawa Timur di Nusantara. Baju Pesa\'an ini bisa digunakan pada acara-acara penting masyarakat Madura seperti acara upacara pernikahan ataupun acara penting lainnya. Namun, di masa lalu orang-orang Madura juga bisa menggunakan pakaian Pesa\'an ini sebagai busana sehari-hari. Walaupun sering digunakan oleh penjual sate, pakaian ini mempunyai makna tersendiri. Pakaian adat Madura ini terdiri atas busana wanita dan pria. Bagi para pria terdiri atas celana longgar dan kaos bergaris merah putih yang cukup sederhana. Sedangkan untuk para wanita menggunakan kebaday dengan warna cerah yang mencolok sebagai pasangan dari busana pria. Penggunaan warna yang cerah dan terang yang kuat pada pakaian adat ini mencerminkan karakter masyarakat Madura dikenal akan keberaniannya, sikap tegas, tidak kenal ragu, serta bersikap terbuka dalam menyampaikan isi pikirannya kepada orang lain.<br /><br />\r\n\r\nBaju Pesa\'an ini cukup dikenal di seluruh penjuru Indonesia. Hal ini dikarenakan pakaian Pesa\'an ini cukup mencolok dalam hal ciri khas yang dimiliki sehingga membuatnya mudah dibedakan dengan baju adat daerah lain. Baju Pesa\'an sebenarnya merupakan baju keseharian yang biasa dikenakan hanya oleh orang-orang Madura dan sebagian pesisir utara Jawa Timur. Namun sering kali karena keunikan dan ciri khas yang dimilikinya, baju inilah yang justru menjadi ikon utama yang mewakili Timur di kancah Nasional.<br /><br />\r\n\r\nPakaian Pesa\'an untuk bangsawan Madura biasanya memiliki beberapa tambahan. Jas tutup polos dan kain panjang biasanya ditambahkan pada penggunaan pakaian adat ini untuk pria. Hal ini ditambahi pula dengan tutup kepala yang sering disebut dengan odheng yang merupakan perlambang dari derajat kebangsawanan seseorang', 'jawa timur (madura)'),
(16, 'Pakaian Kebaya Bali - Baju Safari', 'kebayaBali-bajuSafari.jpeg', 'Pakaian adat bali adalah Kebaya Bali yang dikenakan oleh perempuan-perempuan Bali Dan Baju Safari yang dikenakan oleh masyarakat pria bali.', 'Kebaya Bali adalah pakaian adat yang dikenakan oleh perempuan-perempuan Bali. Kebaya Bali ini sebetulnya bisa dibuat dari berbagai jenis bahan, namun menambahkan renda adalah salah satu favorit para perempuan Bali.<br /><br />\r\n\r\nBiasanya, kebaya Bali akan dikenakan dengan korset. Korset ini umumnya dikenakan di bagian bawah dan dipakai oleh perempuan Bali yang lebih tua. Umumnya warnanya beragam dan cukup mencolok mata.<br /><br />\r\n\r\nKebaya Bali identik dengan warna-warna cerah, hal ini memiliki nilai filosofi yaitu untuk menggambarkan keceriaan sekaligus keanggunan perempuan di Bali. Masyarakat Bali memiliki banyak sekali upacara adat dan juga keagamaan.<br /><br />\r\n\r\nJika perempuan Bali cenderung mengenakan kebaya, masyarakat pria Bali mengenakan baju safari. Baju safari ini sebetulnya nampak seperti kemeja pada umumnya. Namun, baju tradisional yang menjadi pakaian adat Bali Ini memiliki makna yang mendalam. Setiap pria di Bali yang mengenakan baju safari ini harus tetap menjaga kebersihan, kerapihan dan kesopanan.<br /><br />\r\n\r\nKemeja safari umumnya berwarna putih, dengan kerah dan kancing, dilengkapi dengan saku di bagian dada serta di bagian bawah. Warna putih pada baju safari ini melambangkan kesucian dan kesakralan.', 'nusa tenggara (bali)'),
(17, 'Pakaian Pegon', 'pegon.jpeg', 'Pakaian adat suku sasak disebut Pegon yang mendapat pengaruh dari busana eropa. Pegon menggunakan kain biasa berwarna gelap dan   merupakan akulturasi dengan tradisi Jawa.', 'Pakaian adat suku sasak disebut Pegon yang mendapat pengaruh dari busana eropa. Berbeda dengan pakaian Sasak lainnya yang terbuat dari kain songket, pegon menggunakan kain biasa berwarna gelap, kemungkinan pegon merupakan hasil akulturasi dengan tradisi Jawa.<br /><br />\r\n\r\nPegon digunakan khusus untuk upacara adat dan bangsawan suku Sasak. Bagi masyarakat biasa, pria Sasak memakai baju lengan panjang yang terbuat dari kain pelung berwarna terang.<br /><br />\r\n\r\nUntuk bawahannya, pria Sasak menggunakan wiron atau imut. Wiron berbahan batik Jawa dengan motif jaket nangka atau kain pelung hitam.<br /><br />\r\n\r\nCara menggunakannya sama seperti kain di Jawa atau Sunda yang menjuntai sampai mata kaki. Namun, untuk penggunaan wiron tidak diperkenankan menggunakan kain polos berwarna merah atau putih.<br /><br />\r\n\r\nBisa juga digunakan kain tenun dengan motif tapo kemalo dan songket dengan motif serat penginang. Hindari penggunaan kain putih dan merah polos. Wiron atau Imut yang ujungnya lurus sampai ke kaki bumi berarti tawadduk atau sederhana.<br /><br />\r\n\r\nSebagai hiasan, para pria sasak biasanya menggunakan sapuk atau mahkota. Aksesoris ini merupakan simbol untuk Tuhan Yang Masa Esa dan menjaga pikiran dari hal-hal kotor atau tidak baik.', 'nusa tenggara barat (lombok)'),
(18, 'Pakaian Adat NTT', 'nttRote.jpeg', 'Pakaian adat suku Rote terpilih menjadi simbol pakaian adat di provinsi NTT di tingkat nasional. Memiliki model yang khas, sejarah, keunikan, dan nilai filosofis', 'Memiliki model yang khas , sejarah , keunikan dan nilai filosofis , pakaian adat suku Rote terpilih menjadi simbol pakaian adat di provinsi NTT di tingkat nasional .\r\nPakaian adat ini dilengkapi dengan topi Ti\'i Langga , yang memiliki bentuk mirip dengan topi khas Meksiko. Topi ini merupakan sebuah penutup kepala yang terbuat dari daun lontar kering yang menjadi simbol kewibawaan dan kepercayaan diri bagi kaum laki - laki di Suku Rote.<br /><br />\r\n\r\nTopi yang menjadi aksesori utama pakaian adat Rote disebut pakaian Tenun Ikat yang\r\nterbuat dari kain tenun . Pakaian itu merupakan kombinasi dari kemeja putih berlengan panjang dan sarung tenun ikat berwarna gelap pada bagian bawah .\r\nUntuk penutup dada , digunakan sebuah selendang kain dengan motif yang sama di bahu. Sementara kaum perempuannya memakai kebaya dengan bawahan berbentuk tenunan\r\ntangan.', 'nusa tenggara timur'),
(19, 'Pakaian King Baba - King Bibine', 'kingBaba-kingBibine.jpeg', 'Pakaian adat Kalimantan Barat untuk kaum pria suku Dayak disebut king baba dan king bibine untuk wanita. King itu pakaian, baba  itu laki-laki dan bibine berarti perempuan.', 'Pakaian adat Kalimantan Barat untuk kaum laki-laki suku Dayak disebut king baba. Dalam bahasa Dayak, king berarti pakaian dan baba berarti laki-laki.<br /><br />\r\n\r\nPakaian ini terbuat dari kulit kayu dari tumbuhan endemik Kalimantan yang mengandung serat tinggi. Untuk membuat king baba, kulit kayu dipukul-pukul menggunakan palu bulat di dalam air, sehingga hanya tertinggal seratnya saja. Setelah lentur, bahan serat tersebut kemudian dijemur, dan dihias dengan lukisan-lukisan etnik khas Dayak. Pewarna yang digunakan berbahan alami yang diambil dari tumbuhan atau sumber alam lainnya. Setelah itu, bahan serat tadi dibentuk sedemikian rupa hingga menyerupai rompi tanpa lengan, juga celana panjang. Biasanya pakaian ini dikenakan bersama senjata bernama mandau dan sebuah perisai, seakan-akan hendak pergi berperang.<br /><br />\r\n\r\nPakaian adat suku Dayak untuk perempuan disebut King Bibinge, juga kurang lebih dibuat dengan cara dan bahan yang sama dengan pakaian adat kaum laki-laki.<br /><br />\r\n\r\nBedanya, bentuk pakaian perempuan lebih tertutup dan sopan, terdiri dari penutup dada, stagen dan bagian bawah berupa rok. Aksesoris yang digunakan pun lebih bervariasi seperti kalung, manik-manik, ikat kepala dengan hiasan sehali bulu burung enggang.<br /><br />\r\n\r\nBaik king baba dan king bibinge berbahan kulit kayu, sudah tidak pernah dibuat lagi semenjak orang-orang Dayak mulai mengenal kain.', 'kalimantan barat (dayak)'),
(20, 'Pakaian Upak Nyamu', 'upakNyamu.jpeg', 'Baju upak nyamu adalah jenis pakaian adat yang berasal dari Kalimantan tengah, pada dasarnya jenis pakaian adat ini memiliki bahan yang berasal dari kulit nyamu.', 'Baju upak nyamu adalah jenis pakaian adat yang berasal dari Kalimantan tengah, pada dasarnya jenis pakaian adat ini memiliki bahan yang sama dengan pakaian rompi sangkarut. Untuk bahanya sendiri berasal dari kulit nyamu.<br /><br />\r\n\r\nSerta memiliki aksesoris tambahan yakni yang berupa punutup kemaluan. Sedangkan untuk perbedaanya dengan pakaian adat sangakrut yakni jenis pakaian adat ini tidak memiliki gambaran atau lukisan tempelan lain atau dapat dikatakan jenis pakaian adat ini hanya berupa rompi polos tanpa adanya lengan.', 'kalimantan tengah'),
(21, 'Pakaian Ta\'a - Sapei Sapaq', 'taa-sapeiSapaq.jpeg', 'Ta’a dan sapei sapaq merupakan pakaian tradisional Suku Dayak Kenyah yang menempati wilayah Kalimantan Utara. Sering digunakan dalam perayaan resmi Suku Dayak.', 'Ta’a dan sapei sapaq merupakan pakaian tradisional Suku Dayak Kenyah yang menempati wilayah Kalimantan Timur dan Kalimantan Utara. Pakaian Ta’a dan Sapei Sapaq sering digunakan dalam perayaan resmi Suku Dayak. Seperti Upacara adat, penyambut tamu agung, hingga upacara perkawinan.<br /><br />\r\n\r\nTa’a merupakan pakaian adat yang digunakan oleh para perempuan. Pakaian Ta’a mempunyai ciri khas berwarna hitam dan terbuat dari kain beludru. Untuk mempercantik  pakaian ini dipadukan dengan hiasan dan manik-manik<br /><br />\r\n\r\nPakaian ta’a terdiri dari sapei inoq yaitu atasan menyerupai rompi tanpa lengan. Pakaian dipadukan dengan rok bermotif biasa disebut sebagai Ta’a. Untuk mempercantik tampilan dilengkapi dengan penutup kepala yang terbuat dari anyaman daun pandan yang berhiaskan manik-manik, taring macan, dan bulu burung enggang yang menjadi endemik Kalimantan.<br /><br />\r\n\r\nPakaian adat Sapei Sapaq yang digunakan oleh kaum lelaki suku dayak kenyah. Pada mulanya pakaian Sapei Sapaq pada bagian bawahannya berupa gulungan selendang yang dililitkan hingga mirip seperti celana dalam. Semakin berkembangnya zaman, bawahan yang digunakan adalah celana pendek.<br /><br />\r\n\r\nPakaian ini dilengkapi rompi dengan aksesoris mencolok dan motifnya yang beragam. Pada bagian pinggang, digantungkan mandau yaitu senjata tradisionalnya yang diukir. Ditambahkan lagi dengan talawang yaitu tameng suku dayak kenyah yang terbuat dari kayu ulin atau kayu besi.', 'kalimantan utara'),
(22, 'Pakaian Bagajah Gamuling Baular Lulut', 'bagajahGamulingBaularLulut.jpeg', 'Pakaian adat dari Kalimantan Selatan adalah Bagajah Gamuling Baular Lulut. Pakaian ini dipercaya sebagai pakaian pengantin tertua yang ada di Kalimantan Selatan.', 'Salah satu baju tradisional yang terkenal dari Kalimantan Selatan adalah Bagajah Gamuling Baular Lulut. Pakaian ini dipercaya sebagai pakaian pengantin tertua yang ada di Kalimantan Selatan. Dalam perkembangan selanjutnya, busana tradisional di Kalimantan Selatan mengalami banyak modifikasi yang dipengaruhi oleh budaya dan agama baru yang datang.<br /><br />\r\n\r\nPada zaman dahulu, Bagajah Gamuling Baular Lulut merupakan busana yang dikenakan oleh keturunan raja dan bangsawan. Seiring perkembangan zaman, baju ini pun kerap menjadi busana pengantin dalam pernikahan adat Kalimantan Selatan bahkan hingga sekarang.<br /><br />\r\n\r\nBagajah Gamuling Baular Lulut diyakini telah ada sejak abad ke-15 Masehi. Baju ini banyak dipengaruhi oleh unsur agama Hindu yang bercampur dengan budaya Banjar. Percampuran ini menghasilkan satu busana yang eksotis sekaligus indah.<br /><br />\r\n\r\nBusana yang memiliki banyak nilai filosofis ini masih diminati hingga saat sekarang. Banyak yang menggunakan busana klasik dan eksotis ini terutama saat menjadi raja dan ratu sehari. (Nita)', 'kalimantan selatan'),
(23, 'Pakaian Kustin', 'kustin.jpeg', 'Kustin merupakan pakaian adat Kalimantan Timur dari Suku Kutai. Kustin berasal dari bahasa kutai yang berarti busana. Kustin dimaknai sebagai pakaian kebesaran.', 'Kustin merupakan pakaian adat Kalimantan Timur dari Suku Kutai. Kustin berasal dari bahasa kutai yang berarti busana. Kustin dimaknai sebagai pakaian kebesaran. Kustin terbuat yang bahan dasar beludru hitam dipakai saat upacara pernikahan masyarakat golongan menengah ke atas. <br /><br />\r\n\r\nKustin memiliki desain lengan panjang dan kerah tinggi dengan sulaman dari benang emas atau pasmen. Bahan baju Kustin bagi perempuan dan laki-laki sama, tapi corak dan motif yang digunakan berbeda.\r\n', 'kalimantan timur'),
(24, 'Pakaian Lipa Saqbe ', 'lipaSaqbe.jpeg', 'Lipa saqbe merupakan pakaian tenun tradisional yang dikenal di daerah Mandar, Sulawesi Barat. Digunakan untuk upacara adat atau upacara kebesaran lainnya.', 'Lipa saqbe merupakan pakaian tenun tradisional yang dikenal di daerah Mandar, Sulawesi Barat. Lipa saqbe atau sarung tenun sutra Mandar memiliki ciri khas tersendiri yaitu memiliki warna - warna cerah seperti warna merah, kuning dengan garis - garis geometris yang lebar. Sarung tenun ini memiliki pola yang sederhana, namun dalam pembuatannya mencantumkan benang emas dan perak sebagai bahan dasar pembuatan sarung tenun tersebut sehingga akan terlihat indah dan istimewa.<br /><br />\r\n\r\nSarung tenun sutra merupakan salah satu produk sutra yang paling halus di Nusantara. Karena saking halus dan tipisnya kain tersebut bisa dilipat dan dimasukkan ke dalam botol. Sarung tenun sutra Mandar bukan kain yang dapat dikenakan untuk sehari-hari, namun lebih digunakan pada pakaian tradisional untuk upacara adat atau upacara kebesaran lainnya. Pada dasarnya, pewarna yang digunakan pada kain tenun sutra ini berasal dari pewarna alami sehingga tidak boleh dicuci sembarangan.<br /><br />\r\n\r\nLipaq saqbe atau kain sutra mandar saat ini masih diproduksi secara manual sehingga untuk menghasilkan selembar kain lipaq saqbe memerlukan waktu sekitar 2-3 minggu bahkan berbulan - bulan tergantung pada kesulitan motifnya. Untuk memasarkan kain ini , masyarakat Polewali Mandar masih kesulitan karena belum banyak masyarakat yang\r\nmampu membeli kain tradisional tersebut dengan harga yang layak.', 'sulawesi barat'),
(25, 'Pakaian Nggembe', 'nggembe.jpeg', 'Baju Nggembe adalah sebuah baju khas tradisional kaum perempuan dari suku Kaili di Sulawesi Tengah. Baju ini dipakai pada acara adat dan acara khusus suku Kaili.', 'Salah satu contoh pakaian tradisional yang cukup dikenal di Indonesia ialah baju adat Nggembe. Baju adat ini berbeda dengan pakaian tradisional daerah Sulawesi lainnya, karena dikenal dengan bentuknya yang unik. Nggembe berasal dari Sulawesi Tengah, tepatnya dari Suku Kaili. <br /><br />\r\n\r\nPakaian adat ini hanya diperbolehkan untuk dipakai dalam acara resmi, seperti upacara adat atau upacara pernikahan. Baju Nggembe hanya diperuntukkan bagi perempuan suku Kaili. Sedangkan untuk kaum pria suku Kaili, biasanya mengenakan baju Koje. Sama seperti Nggembe, baju Koje juga hanya diperbolehkan untuk dikenakan dalam acara resmi saja. Baju adat Nggembe sangat dikenal dengan bentuknya yang unik. Baju adat Nggembe berbentuk segi empat, bagian lengannya cenderung lebar dengan bentuk kerah leher yang bulat dan panjang. Nggembe dilengkapi dengan rok panjang hingga ke telapak kaki dan biasanya berbentuk rumbai. <br /><br />\r\n\r\nPerempuan suku Kaili yang mengenakan baju adat Nggembe sering memadukan pakaian tradisional ini dengan berbagai perhiasan, seperti potto atau gelang, kalong atau kalung, jaling atau anting serta salandoeng atau selendang. Selain dipadukan dengan perhiasan, pakaian tradisional Nggembe juga dilengkapi dengan hiasan berupa sampo dada atau bagian penutup dada serta pende atau ikat pinggang. Warna dasar baju adat Nggembe biasanya berasal dari warna cerah, seperti merah dan kuning yang dikombinasikan dengan hitam.\r\n', 'sulawesi tengah'),
(26, 'Pakaian Laku Tepu', 'lakuTepu.jpeg', 'Laku Tepu merupakan pakaian adat Provinsi Sulawesi Utara yang berasal dari Suku Sangihe Talaud. Pakaian adat tersebut dikenakan oleh laki-laki dan perempuan. ', 'Laku Tepu merupakan pakaian adat Provinsi Sulawesi Utara yang berasal dari Suku Sangihe Talaud. Pakaian adat tersebut dikenakan oleh laki-laki dan perempuan. Biasanya dipakai saat upacara adat Tulude yang merupakan warisan nenek moyang yang dilaksanakan turun-temurun. Dikutip dari buku Adat dan Upacara Perkawinan Daerah Sulawesi Utara (1978), pakaian adat pada suku bangsa Sangihe, Sulawesi Utara disebut Laku Tepu. <br /><br />\r\n\r\nBentuk pakaian adat tersebut adalah pakaian panjang dengan warna-warna yang dominan, seperti merah, ungu, kuning tua, dan hijau tua. Pakaian untuk laki-laki dan perempuan bentuknya sama, perbedaannya hanya sedikit yaitu pakaian laki-laki panjangnya hingga mencapai pertengahan betis, sedangkan perempuan memanjang dari leher sampi betis. Pakaian adat Laku Tepu berasal dari kata \"laku\" yang memiliki arti pakaian dan \"tepu\" yang berati agak sempit.<br /><br />\r\n\r\nPakaian adat tersebut terbuat dari bahan serat kofo yang telah ditenun memakai kahuwang. Bahan dasar kain kofo adalah serat pisang. Jenis pisang keras dinamakan hote istilah setempat atau abaka istilah Filipina yang di Indonesiakan. Bahan lainnya seperti serat daun nanas atau daun kalung nanasi. <br /><br />\r\n\r\nBahan dasar kain bukanlah hanya benang semata yang dibuat dibuat dari bahan kapas. Serat-serat yang dihasilkan oleh tumbuhan, bahkan hewan banyak yang digunakan sebagai bahan dasar pembuatan kain. Banyak alternatif komoditas serat alam yangmemiliki serat yang dapat diblending (dicampur) dengan serat kapas untuk dibuat tekstil.\r\n', 'sulawesi utara'),
(27, 'Pakaian Babu Nggawi', 'babuNggawi.jpeg', 'Babu Nggawi merupakan pakaian adat khas Sulawesi Tenggara, khususnya dari Suku Tolaki. Dikenakan dalam upacara adat, upacara resmi, seperti pernikahan dll.', 'Babu Nggawi merupakan pakaian adat khas Sulawesi Tenggara, khususnya dari Suku Tolaki. Pakaian tradisional ini sering dikenakan dalam upacara adat, upacara resmi, seperti pernikahan ataupun lainnya. Menurut Rahman Hasima dalam Perlindungan Hukum terhadap Ekspresi Budaya Tradisional Masyarakat Adat Tolaki, pakaian adat suku Tolaki dibagi menjadi dua jenis, yakni Babu Nggawi untuk perempuan dan Babu Nggawi Langgai untuk pria. Tentunya antara Babu Nggawi dan Babu Nggawi Langgai memiliki perbedaan. Untuk Babu Nggawi Langgai, pakaian atasannya mengenakan baju lengan panjang dengan motif di bagian depannya. Bawahannya mengenakan celana panjang dengan warna serupa seperti atasannya. <br /><br />\r\n\r\nSedangkan untuk pakaian Babu Nggawi terdiri atas pakaian atasan yang sering disebut lipa hinoru. Untuk bawahannya mengenakan roo mendaa berbentuk rok panjang hingga mata kaki. Warna lipa hinoru selalu disesuaikan dengan warna roo mendaa. Misalnya jika mengenakan lipa hinoru berwarna merah, maka roo mendaa nya juga berwarna merah. Dalam upacara pernikahan, warna Babu Nggawi biasanya selalu disesuaikan dengan Babu Nggawi Langgai. Dalam jurnal berjudul Karakteristik Tata Rias dan Busana pada Tari Lulo di Sanggar Anasepu Kota Kendari (2019), pakaian atas Babu Nggawi tidak memiliki belahan di bagian depan. Belahan artinya menggunakan kancing atau lainnya, seperti pada kebaya. Babu Nggawi hanya memiliki lubang untuk memasukkan baju dari kepala. Lubang tersebut memiliki belahan hanya hingga bagian atas dada saja. <br /><br />\r\n\r\nCiri khas unik dan utama dari Babu Nggawi terletak pada hiasan manik-manik berwarna emas. Selain lipa hinoru dan roo mendaa, Babu Nggawi juga dilengkapi dengan ikat pinggang atau sulepe. Untuk aksesoris yang sering dipadukan dengan pakaian tradisional ini biasanya berupa anting-anting, kalung, dan selop sebagai alas kaki\r\n', 'sulawesi tenggara'),
(28, 'Pakaian Baju Bodo', 'bodo.jpeg', 'Baju bodo merupakan salah satu baju adat tertua di Indonesia. Baju bodo ini menjadi pakaian adat masyarakat suku Bugis di Sulawesi Selatan.', 'Baju bodo merupakan salah satu baju adat tertua di Indonesia. Baju bodo ini menjadi pakaian adat masyarakat suku Bugis di Sulawesi Selatan.<br /><br />\r\n\r\nBaju bodo ini mengalami perubahan seiring dengan berkembangnya zaman. Awalnya, baju ini menggunakan kain transparan. Lalu bertransformasi menggunakan kain yang lebih tebal setelah masuknya ajaran agama Islam.<br /><br />\r\n\r\nBaju bodo dikenakan untuk kaum wanita dengan desain yang unik. Bentuknya segi empat dan berlengan pendek, serta warnanya beragam. Setiap warna memiliki arti yang berbeda, yakni menjadi simbol usia dan status bagi pemakainya.<br /><br />\r\n\r\nWarna jingga melambangkan pemakai baju bodo ialah anak perempuan berusia sekitar 10 tahun. Sedangkan untuk anak yang berusia sekitar 17 tahun mengenakan warna merah.<br /><br />\r\n\r\nAda lagi warna putih yang melambangkan pemakainya berasal dari kalangan dukun dan pembantu. Kemudian warna ungu melambangkan pemakainya adalah seorang janda. Terakhir warna hijau melambangkan pemakainya berasal dari kalangan bangsawan.<br /><br />\r\n\r\nBaju ini dikenakan dengan sarung motif kotak-kotak dan dipakai dengan cara digulung menggunakan tangan sebelah kiri.', 'sulawesi selatan'),
(29, 'Pakaian Biliu - Makuta', 'biliu-makuta.jpeg', 'Pakaian Biliu dan Makuta merupakan pakaian adat tradisional dari Provinsi Gorontalo. Pakaian adat Biliu dikenakan oleh wanita, sedangkan Makuta kenakan oleh pria.', 'Pakaian Biliu dan Makuta merupakan pakaian adat tradisional dari Provinsi Gorontalo. Pakaian adat Biliu dikenakan oleh perempuan, sedangkan Makuta kenakan oleh laki-laki. Pakaian adat tersebut adalah pakaian pengantin Gorontalo yang memiliki ciri khas dan keunikan. Umumnya pakaian adat tersebut dipakai untuk pernikahan. <br /><br />\r\n\r\nPakaian yang biasa dikenakan oleh penganti perempuan disebut Biliu. Pakaian Biliu terdiri dari hiasan kepala, baju kurung, kalung bersusun, sarung dan ikat pinggang. Biasanya rambut perempuan disanggul dengan bentuk sederhana dan dihiasi kembang emas. <br /><br />\r\n\r\nSementara pakaian pengantin laki-laki disebut Makuta atau Pulawala. Pada pakaian laki-laki tertutup yang dipadukan dengan celana panjang. Di mana dilengkapi penutup kepala dan kain sarung yang dililitkan di pinggang. Kemudian senjata tradisional wamilo yang diselipkan di lilitan sarung.\r\n', 'sulawesi (gorontalo)'),
(30, 'Pakaian Baju Cele', 'cele.jpeg', 'Pakaian adat dari maluku, yaitu Baju Cele, berupa baju kurung yang memiliki panjang hingga pertengahan pinggul. Sering dipakai di dalam berbagia upacara adat di Maluku.', 'Baju cele sering dipakai di dalam berbagia upacara adat di Maluku, mulai dari acara panas pela, pesta negeri, cuci negeri, sampai pelantikan raja. Didasarkan pada adat Ambon, baju cele perempuan adalah kain kebaya yang dikombinasikan dengan kain salele di pinggang. Motif baju cele sendiri cukup beragam, dapat berupa garis-garis geometris atau kotak-kotak kecil. <br /><br />\r\n\r\nPakaian adat Ambon ini umumnya berwarna merah untuk menampilkan keceriaan, keberanian, dan kelincahan. Baju cele sering dikombinasikan dengan kain pelekat yang disalele (disarung) dari luar dan sampai batas lutut dan lenso (sapu tangan di bahu). <br /><br />\r\n\r\nPenyebutan dari pemakaian baju cele perempuan ternyata berbeda-beda, tergantung dari status perempuan yang memakainya. Jika dipakai oleh para jujaro (gadis), maka diberi baju itu akan disebut dengan istilah nona baju cele kaeng atau kain salele. Jika dipakai oleh perempuan yang telah menikah, maka baju itu akan disebut dengan istilah nyora baju cele kain salele.<br /><br />\r\n\r\nPara perempuan Ambon biasanya menambahkan sanggul atau konde ketika mengenakan busana adat ini. Adapun konde yang dipakai adalah konde bulan dan tusukan konde (haspel) yang terbuat dari emas atau perak, agar dapat memberikan kesan mewah.\r\nBaju Cele Laki-laki<br /><br />\r\n\r\nSedangkan baju cele laki-laki terdiri dari kebaya dansa berupa atasan putih berlengan panjang dengan kancing keemasan dan dilapisi jas merah dengan renda dari bahan satin di luarnya.<br /><br />\r\n\r\nTak hanya itu, para lelaki akan mengenakan sebuah sabuk yang bernama ikat poro dengan warna yang senada dengan jas satin merah yang mereka kenakan. Kemudian, bawahannya cukup sederhana, yaitu celana bahan hitam dan sepatu pantofel hitam.', 'maluku');
INSERT INTO `pakaian_adat` (`id_pakaian`, `nama_pakaian`, `gambar_pakaian`, `preview`, `deskripsi`, `daerah`) VALUES
(31, 'Pakaian Manteren Lamo', 'manterenLamo.jpeg', 'Manteren Lamo menjadi pakaian adat yang cukup dikenal masyarakat Maluku Utara dan Indonesia. Manteren Lamo sudah ada semenjak Kesultanan Ternate dan Tidore masih berdiri', 'Manteren Lamo menjadi pakaian adat yang cukup dikenal masyarakat Maluku Utara dan Indonesia. Manteren Lamo sudah ada semenjak Kesultanan Ternate dan Tidore masih berdiri. Saat itu, pakaian tradisional ini menjadi salah satu pakaian kerajaan. Dahulu, Manteren Lamo sering digunakan oleh sultan atau pemimpin Kesultanan Ternate dan Tidore. Jika sultan mengenakan Manteren Lamo, permaisuri kesultanan mengenakan Kimun Gia, sebagai salah satu pakaian tradisional Maluku Utara untuk perempuan. <br /><br />\r\n\r\nHingga saat ini Manteren Lamo dan Kimun Gia masih sering dikenakan, khususnya dalam upacara adat, upacara resmi, seperti pernikahan atau lainnya. Sebagai salah satu pakaian kesultanan saat itu, pakaian Manteren Lamo dan Kimun Gia identik dengan kemewahan. Mengutip dari Mengenal Rumah Adat, Pakaian Adat, Tarian Adat dan Senjata Tradisional (2009), pakaian adat Manteren Lamo yang dikenakan pria terdiri atas jas tertutup dengan warna merah. Warna ini melambangkan kegagahan seorang pria. Pada bagian jas tersebut turut diberikan kancing besar yang bahan dasarnya dari perak, jumlah kancingnya biasanya sekitar sembilan. <br /><br />\r\n\r\nHiasan utama pada Manteren Lamo adalah bordiran berwarna emas yang terletak di bagian ujung tangan, leher dan saku. Untuk aksesoris Manteren Lamo dan Kimun Gia bernuansa kemewahan, karena dulunya pakaian ini menjadi pakaian yang sering dipakai pihak kesultanan. Manteren Lamo sering dipadukan dengan celana panjang berwarna hitam polos hingga mata kaki sebagai bawahannya. Untuk aksesoris Manteren Lamo sering menggunakan penutup kepala.\r\n', 'maluku utara'),
(32, 'Pakaian Ewer', 'ewer.jpeg', 'Pakaian adat atau baju tradisional khas Papua Barat ialah baju Ewer. Pakaian adat ini tergolong unik karena menggunakan bahan jerami sebagai bahan utama pembuatannya.', 'Pakaian adat atau baju tradisional khas Papua Barat ialah baju Ewer. Pakaian adat ini tergolong unik karena menggunakan bahan alam, yakni jerami sebagai bahan utama pembuatannya. Oleh karena menggunakan bahan alam, baju Ewer dibuat melalui proses yang ramah lingkungan. Jerami yang digunakan dalam pembuatan pakaian tradisional ini harus dikeringkan terlebih dahulu selama beberapa waktu. <br /><br />\r\n\r\nBaju adat Papua Barat dibedakan menjadi dua, yakni untuk pria dan untuk perempuan. Penggunaan baju Ewer untuk pria dan perempuan tentunya berbeda. Untuk pria, biasanya baju Ewer hanya dikenakan sebagai bawahan saja tanpa atasan. Bahan utama pembuatannya dari jerami yang telah dikeringkan, panjangnya bervariasi, namun kira-kira selutut dan dikenakan di bagian pinggang. Sedangkan untuk perempuan, baju Ewer terdiri atas bagian atasan dan bawahan. Untuk bawahannya juga terbuat dari jerami yang telah dikeringkan, kurang lebih panjangnya juga selutut dan dikenakan di pinggang. Sedangkan atasannya terbuat dari kain. Seiring perkembangan zaman dan modernisasi, baju Ewer untuk pria dan perempuan bagian atasannya sudah terbuat dari bahan kain. <br /><br />\r\n\r\nMaka bisa disimpulkan jika baju Ewer ini memadukan unsur bahan alam dan kain sebagai bahan utamanya. Untuk aksesorisnya, biasanya pria dan perempuan mengenakan sejenis penutup kepala dengan motif khas Papua Barat. Baju Ewer cukup sering digunakan oleh masyarakat Papua Barat, misalnya dalam upacara adat, upacara resmi atau lainnya.\r\n', 'papua barat'),
(33, 'Pakaian Koteka', 'koteka.jpeg', 'Koteka merupakan pakaian adat khas yang cukup unik dari Papua. Pakaian adat khas dari Papua ini masih sangat erat dipakai oleh penduduk aslinya.', 'Koteka merupakan pakaian adat khas yang cukup unik dari wilayah ujung timur Indonesia, yakni Papua. Koteka yang merupakan pakaian adat khas dari Papua ini masih sangat erat dipakai oleh penduduk aslinya.<br /><br />\r\n\r\nKoteka sering digunakan oleh penduduk asli Papua untuk melakukan upacara-upacara adat maupun dalam kehidupan sehari-hari. Nama koteka berasal dari suku Paniai yang berarti pakaian. Koteka juga biasa disebut dengan Holim, pakaian ini dipakai pria untuk menutupi tubuh bagian bawah atau kemaluan.<br /><br />\r\n\r\nPakaian tersebut biasanya terbuat dari kulit labu air yang bagian biji dan dagingnya dibuang. Sedangkan labu air yang cocok digunakan sebagai koteka yaitu labu air yang sudah tua, tujuannya karena teksturnya lebih keras dan awet setelah dikeringkan.<br /><br />\r\n\r\nSelain itu, dalam penggunaan koteka juga memiliki makna khusus yang cukup unik. Bagi pria yang memiliki tingkat kedudukan tinggi, maka ukuran koteka yang dipakai juga akan semakin besar.<br /><br />\r\n\r\nCara penggunaanya ada 3 makna yang menunjukkan atau melambangkan sesuatu sebagai berikut.<br />\r\n1. Koteka tegak lurus melambangkan pria sejati dan masih perjaka.\r\n2. Koteka miring ke kanan melambangkan laki-laki sejati yang memiliki kejantanan, dan status sosial yang tinggi serta seorang bangsawan.\r\n3. Koteka miring ke kiri melambangkan pria dewasa yang masuk ke dalam golongan menengah serta keturunan panglima perang.', 'papua');

