INSERT INTO `pameran` (`id_karya`, `nama_karya`, `foto_karya`, `deskripsi`, `harga`, `id_user`) VALUES
(1, 'Wayang Kulit', '631c557f6fb25.jpg', 'Karena nenek moyang masyarakat Indonesia adalah penganut animisme dan dinamisme, yang mempercayai bahwa setiap benda memiliki kekuatan dan roh, sehingga pewayangan diwujudkan dalam bentuk arca dan gambar', '250.000', 1),
(2, 'Wayang Kulit 2.0', '631c5917bb7f9.jpg', 'Karena nenek moyang masyarakat Indonesia adalah penganut animisme dan dinamisme, yang mempercayai bahwa setiap benda memiliki kekuatan dan roh, sehingga pewayangan diwujudkan dalam bentuk arca dan gambar', 'NOT FOR SALE', 1),
(3, 'Mengenangmu', '631c9ae883edc.jpg', 'Umumnya sebuah karya lukis ditempatkan pada media kanvas, tetapi pelukis asal Sidoarjo ini justru menggunakan puzzle 1000 piece sebagai media lukisnya.\r\n\r\n Lukisan abstrak dengan warna-warna yang berani ini dipadukan dengan sosok gambar wanita tampak dari belakang mampu menarik perhatian para pengunjung pameran.\r\n\r\n Dari sebagian besar lukisan yang mainstream, lukisan karya M.F. Hazim ini terbilang berani menembus batas-batas keumuman. Dengan warna mencolok yang saling ditabrakkan serta media yang tidak biasa, menunjukkan kreativitas tak terbatas di imaji sang pelukis muda jomblo ini.', 'NOT FOR SALE', 2),
(4, 'Si Mbah Bakul', '631c9b7bd2ccd.jpg', 'Dari kejauhan, sebenarnya lukisan ini terlihat biasa saja seperti kebanyakan lukisan lainnya. Menampilkan sosok nenek tua menjajakan dagangannya di sebuah bakul.\r\n\r\n Namun, saat didekati, ternyata sosok nenek tua tersebut tidak dilukis menggunakan cat, melainkan mozaik sobekan-sobekan kertas bekas majalah / koran yang disusun sedemikian rupa membentuk gambar nenek tua.\r\n\r\n Butuh ketelitian dan ketelatenan yang luar biasa agar mampu menciptakan karya seni seperti ini. Lukisan ini merupakan karya Dedi Saksono Sudibyo, seorang seniman asal Surabaya.', '450.000', 3),
(5, 'Mencari Rumput', '631c9c5a9c34b.jpg', 'Sama seperti lukisan sebelumnya, lukisan berjudul “Mencari Rumput” ini sekilas nampak biasa saja. Namun bila diperhatikan dengan seksama, gradasi warna yang tercipta berbeda dengan lukisan cat lainnya.\r\n\r\n\r\n\r\nBenar saja, lukisan karya Moel Alit asal Wonogiri ini terbuat dari pasir warna alam. \r\n Dalam lukisan tersebut, didominasi warna gelap. Menampilkan sosok pencari rumput di tanah gersang dengan langit kelabu, membuat siapapun yang melihatnya merasakan kegelisahan hati sang pelukis.', 'NOT FOR SALE', 4),
(6, 'Rama Sinta', '631c9ce3827bd.jpg', ' Lukisan yang menggambarkan tokoh pewayangan Rama dan Sinta ini terlihat timbul dan memiliki bayangan.\r\n\r\n\r\n\r\n Rahasianya adalah sosok Rama Sinta dilukis pada media kaca, kemudian diberikan background berjarak kurang lebih 1-2 cm di belakangnya. Karenanya, sosok Rama Sinta nampak memiliki bayangan.', '600.000', 5),
(8, 'Perang Dingin', '632496f02505a.png', 'Ini adalah karyaku yang sangat mantap! Ini adalah karyaku yang sangat mantap! Ini adalah karyaku yang sangat mantap! Ini adalah karyaku yang sangat mantap! Ini adalah karyaku yang sangat mantap! Ini adalah karyaku yang sangat mantap! ', '250.000', 8),
(10, 'The End of The Day', '632a1f7c05c61.jpeg', 'Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! ', 'NOT FOR SALE', 1),
(11, 'Wayang v3', '632a3f526e125.png', 'Ini adalah wayang versi update terbaru ! Ini adalah wayang versi update terbaru ! Ini adalah wayang versi update terbaru ! Ini adalah wayang versi update terbaru ! Ini adalah wayang versi update terbaru ! Ini adalah wayang versi update terbaru ! Ini adalah wayang versi update terbaru ! ', '300.000', 13),
(12, 'Gangongg!', '632c92ca02a54.jpeg', 'Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! Ini adalah karyaku ! ', '500.000', 1);

