--
-- Table structure for table `rumah_adat`
--

CREATE TABLE `rumah_adat` (
  `id` int(11) NOT NULL,
  `nama_rumah` text NOT NULL,
  `foto` varchar(50) NOT NULL,
  `preview` text NOT NULL,
  `deskripsi` longtext NOT NULL,
  `daerah` varchar(30) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Dumping data for table `rumah_adat`
--

INSERT INTO `rumah_adat` (`id`, `nama_rumah`, `foto`, `preview`, `deskripsi`, `daerah`) VALUES
(1, 'Rumah Krong Bade', 'krongbade.jpeg', 'Rumah Krong Bade adalah rumah adat dari Provinsi Aceh. Rumah Krong Bade juga biasa dikenal dengan nama rumoh Aceh.', 'Rumah Krong Bade adalah rumah adat dari Provinsi Aceh. Rumah Krong Bade juga biasa dikenal dengan nama rumoh Aceh. Rumah ini mempunyai tangga depan yang digunakan bagi tamu atau orang yang tinggal untuk masuk ke dalam rumah. Rumah Krong Bade adalah salah satu budaya Indonesia yang hampir punah. <br /><br />\r\n\r\nRumah Krong Bade dari Aceh ini berbentuk memanjang dari timur ke barat menyerupai persegi panjang. Di bagian depan rumah dilengkapi dengan tangga untuk masuk ke dalam rumah. Umumnya, tangga pada  rumah adat Aceh ini jumlahnya ganjil, yaitu sekitar 7 hingga 9 anak tangga. ', 'Sumatera (Aceh)'),
(2, 'Rumah Bolon', 'bolon.jpeg', 'Ruma Bolon adalah rumah adat dari Sumatra Utara, dari suku Batak Toba yang ada di Indonesia. Rumah Bolon berasal dari daerah Sumatra Utara.', 'Ruma Bolon adalah rumah adat dari Sumatra Utara, dari suku Batak Toba yang ada di Indonesia. Rumah Bolon berasal dari daerah Sumatra Utara. Rumah Bolon adalah simbol dari identitas masyarakat Batak yang tinggal di Sumatra Utara. Pada zaman dahulu kala, rumah Bolon adalah tempat tinggal dari 13 raja yang tinggal di Sumatra Utara. 13 Raja tersebut adalah Raja Ranjinman, Raja Nagaraja, Raja Batiran, Raja Bakkaraja, Raja Baringin, Raja Bonabatu, Raja Rajaulan, Raja Atian, Raja Hormabulan, Raja Raondop, Raja Rahalim, Raja Karel Tanjung, dan Raja Mogam. <br /><br />\r\n\r\nAda beberapa jenis rumah Bolon dalam masyarakat Batak yaitu rumah Bolon Toba, rumah Bolon Simalungun, rumah Bolon Karo, rumah Bolon Mandailing, rumah Bolon Pakpak, rumah Bolon Angkola. Setiap rumah mempunyai ciri khasnya masing-masing. Sayangnya, rumah Bolon saat ini jumlah tidak terlalu banyak sehingga beberapa jenis rumah Bolon bahkan sulit ditemukan. Saat ini, rumah bolon adalah salah satu objek wisata di Sumatra Utara.  Rumah Bolon adalah salah satu budaya Indonesia yang harus dilestarikan.', 'Sumatera Utara'),
(3, 'Rumah Gadang', 'gadang.jpeg', 'Rumah Gadang adalah nama untuk rumah adat Minangkabau yang merupakan rumah tradisional dan banyak jumpai di Sumatra Barat, Indonesia. ', 'Rumah Gadang adalah nama untuk rumah adat Minangkabau yang merupakan rumah tradisional dan banyak jumpai di Sumatra Barat, Indonesia. Rumah ini juga disebut dengan nama lain oleh masyarakat setempat dengan nama Rumah Bagonjong atau ada juga yang menyebut dengan nama Rumah Baanjuang.<br /><br />\r\n\r\nRumah dengan model ini juga banyak dijumpai di Sumatra Barat. Namun tidak semua kawasan di Minangkabau (darek) yang boleh didirikan rumah adat ini, hanya pada kawasan yang sudah memiliki status sebagai nagari saja Rumah Gadang ini boleh didirikan. Begitu juga pada kawasan yang disebut dengan rantau, rumah adat ini juga dahulunya tidak ada yang didirikan oleh para perantau Minangkabau. Rumah adat Gadang terbuat dari ijuk dan bentuknya mirip seperti tanduk kerbau, yang melambangkan kemenangan suku Minang dalam perlombaan adu kerbau di Jawa.', 'Sumatera Barat'),
(4, 'Rumah Selaso Jatuh Kembar', 'selaso.jpeg', 'Rumah adat Selaso Jatuh Kembar adalah rumah adat khas Riau yang berupa balai selaso jatuh. ', 'Rumah adat Selaso Jatuh Kembar adalah rumah adat khas Riau yang berupa balai selaso jatuh. Balai atau rumah adat ini difungsikan sebagai tempat berkegiatan bersama, sebagai tempat pertemuan, tetapi tidak digunakan sebagai tempat tinggal pribadi. Rumah Adat Selaso Jatuh Kembar dikenal juga dengan sebutan balai penobatan, balirung sari, balai karapatan dan sebagainya. Dulu bangunan ini sangat ramai karena kerap digunakan oleh warga untuk melaksanakan acara-acara adat lokal, seperti musyawarah, penobatan kepala adat, untuk rapat perihal desa dan bahkan untuk melaksanakan upacara adat. Rumah ini memiliki arti rumah dengan dua selasar. Masyarakat Riau tidak menjadikan Rumah Selaso Jatuh Kembar sebagai tempat tinggal mereka, tetapi hanya menggunakannya untuk acara adat. Akan tetapi, sekarang semua itu telah digantikan oleh masjid.<br /><br />\r\n\r\n\r\nRumah Adat Selaso Jatuh Kembar memiliki bentuk khusus. Bangunan rumah ini memiliki keliling yang selaras antara penyangganya dengan lantai yang lebih rendah. rumah adat ini juga dipercantik dengan berbagai macam ukiran dengan bentuk tumbuhan dan hewan. Masing-masing ukiran memiliki makna dan nama yang berbeda-beda. Rumah adat Selaso Jatuh Kembar memiliki ukiran di bagian tangga yang disebut dengan lebah bergantung atau ombak-ombak karena bentuknya menyerupai ombak atau bisa dilihat juga mirip dengan lebah-lebah yang bergantungan. Ada juga ukiran yang disebut dengan ukiran melambai-lambai yang berada di bagian atas pintu dan daun jendela. Sedangkan ukiran yang diukir di bagian kisi-kisi pintu dan jendela dinamakan semut beriring karena bentuknya terinspirasi dari cara semut berjalan yang beriringan dan ini memiliki makna mendalam. Persoalan makna akan dibahas kemudian. Selain ukiran yang sudah disebutkan masih ada juga ukiran yang disebut dengan ukiran tiang menggantung di bagian tiang Rumah Adat Selaso Jatuh Kembar.', 'Sumatera (Riau)'),
(5, 'Rumah Limas Potong', 'limaspotong.jpeg', 'Limas Potong merupakan salah satu rumah adat atau tradisional khas Melayu di Kepulauan Riau. Rumah adat ini berbentuk panggung', 'Rumah Potong Limas merupakan salah satu dari tiga jenis bangunan tempat tinggal suku Melayu selain Rumah Potong Kantor Kawat dan Rumah Potong Godang. Adapun wilayah persebarannya di wilayah Kepulauan Riau dan Kalimantan Barat. Istilah Potong Limas sendiri mengambil dari bentuk atap rumah tersebut yang berbentuk limas. Hal yang membedakan rumah tradisional ini adalah puncak atapnya yang memilki dua puncak atap. Bentuk rumah potong limas sendiri mirip dengan bentuk rumah Limasan di Pulau Jawa. Rumah Potong Limas biasanya merupakan rumah yang ditinggali oleh perangkat kesultanan.<br /><br />\r\n\r\nRumah potong limas merupakan rumah panggung. Bentuknya memanjang ke arah belakang. Rumah potong limas umumnya memilki ukuran yang lebih besar dan megah ketimbang rumah potong kawat ataupun rumah potong godang. Selain itu, rumah potong limas juga memiliki teknik pembangunan yang khusus. Dari sisi jumlah, rumah potong limas jumlahnya sangat sedikit. Jumlah yang sedikit tersebut diakibatkan karena biaya pembangunan rumah potong limas yang mahal. Oleh karenanya rumah ini biasanya dibangun oleh orang-orang dengan status sosial atau kekayaaan tertentu seperti anggota kesultanan.', 'Sumatera (Kepulauan Riau)'),
(6, 'Rumah Bubungan Lima', 'bubunganlima.jpeg', 'Rumah Bubungan Lima adalah rumah adat dari provinsi Bengkulu. Rumah ini memiliki model seperti rumah panggung yang ditopang oleh beberapa tiang penopang.', 'Rumah Bubungan Lima adalah rumah adat dari provinsi Bengkulu. Rumah ini memiliki model seperti rumah panggung yang ditopang oleh beberapa tiang penopang. Rumah ini bukanlah rumah tinggal seperti pada umumnya. Rumah ini biasanya dipakai untuk acara adat masyarakat Bengkulu.<br /><br />\r\n\r\n\r\nRumah ini terbagi atas tiga bagian yaitu rumah bagian atas, rumah bagian tengah, dan rumah bagian bawah Rumah Bubungan Lima memiliki materi dasar yaitu kayu. Kayu yang dipilih pun bukan kayu sembarangan melainkan kayu yang kuat dan tahan lama. Kayu yang biasanya digunakan un uk membangun Rumah Bubungan Lima adalah Kayu Medang Kemuning. Rumah Bubungan Lima dibangun tinggi agar menghindari pemilik rumah beserta keluarga dari serangan binatang liar dan juga dari bencana alam seperti banjir. Karena tinggi Rumah Bubungan Lima ini, maka orang-orang yang hendak masuk ke dalam rumah pun harus menggunakan tangga. Tangga yang digunakan untuk masuk ke dalam rumah umumnya mempunyai jumlah anak tangga yang ganjil sesuai dengan kepercayaan masyarakat Bengkulu. Rumah Bubungan Lima ini merupakan salah satu Budaya Indonesia yang menjadi objek wisata.', 'Sumatera (Bengkulu)'),
(7, 'Rumah Kajang Lako', 'kajanglako.jpeg', 'Kajang Lako atau Rumah Kajang Lako  adalah rumah tradisional masyarakat Jambi, khususnya Suku Batin.', 'Kajang Lako atau Rumah Tuo yang merupakan rumah tradisional Jambi ini berasal dari rumah milik Suku Batin, salah satu suku yang ada di Provinsi Jambi. Nenek moyang orang Batin berasal dari 60 tumbi (keluarga) yang pindah dari Koto Rayo dan kemudian terbagi ke dalam 5 kelompok kecil yang bermukim di 5 Bathin atau dusun. Dengan kata lain, daerah Marga Bathin 5 itu berarti daerah kumpulan 5 dusun. Kelima dusun tersebut adalah Tanjung Muara Semayo, Dusun Seling Dusun Kapuk, Dusun Pulau Aro, dan Dusun Muara Jernih. Mereka membangun rumah-rumah dengan bentuk memanjang secara terpisah, berjarak sekitar 2 meter, serta menghadap ke jalan. Di belakang rumah dibangun lumbung untuk tempat menyimpan padi hasil panen mereka.<br /><br />\r\n\r\nSampai saat ini perkampungan orang Bathin yang masih terbilang asli di Kampong Lamo, Rantau Panjang, Kabupaten Merangin, Provinsi Jambi. Di perkampungan tersebut terdapat gugusan rumah Kajang Lako yang dibangun dengan menggunakan kayu Besi atau dikenal juga dengan nama Kayu Ulin yang terkenal sangat keras dan awet sebagai material dasarnya. Dari sekian banyak rumah Kajang Lako di Dusun Tuo, ada satu rumah yang diyakini sebagai rumah raja dan merupakan bangunan rumah tertua di tempat itu, usianya diperkirakan mencapai 700 tahun. Rumah tersebut kini ditinggali oleh keluarga Iskandar, keturunan ke-14 Puyan Bungkul atau pendiri Dusun Tuo.', 'Sumatera (Jambi)'),
(8, 'Rumah Nuwo Sesat', 'nuwosesat.jpeg', 'Nuwo Sesat adalah salah satu rumah tradisional yang ada di Provinsi Lampung. Nuwo Sesat berfungsi sebagai tempat pertemuan adat.', 'Nuwo Sesat adalah salah satu rumah tradisional yang ada di Provinsi Lampung. Nuwo Sesat berfungsi sebagai tempat pertemuan adat bagi para purwatin (Penyimbang) pada saat mengadakan pepung adat (Musyawarah). Karena itu rumah tradisional ini juga disebut Balai Agung. <br /><br />\r\n\r\nBagian-bagian dari rumah tradisional ini adalah Anjungan yang merupakan serambi yang digunakan untuk pertemuan kecil, Pusiban adalah ruang dalam yang digunakan sebagai tempat musyawarah resmi, ruang Tetabuhan adalah ruangan tempat menyimpan alat musik tradisional, ruang gajah merem yang digunakan untuk tempat istirahat bagi para penyimbang, dan ijan geladak adalah tangga masuk yang dilengkapi dengan atap. Atap rumah adat ini disebut rurung agung.', 'Sumatera (Lampung)'),
(9, 'Rumah Limas', 'rumlimas.jpeg', 'Rumah Limas adalah rumah tradisional Provinsi Sumatera Selatan. Gaya Rumah Limas seperti rumah panggung, memiliki lima tingkat.', 'Rumah Limas adalah rumah tradisional Provinsi Sumatera Selatan. Gaya Rumah Limas seperti rumah panggung, memiliki lima tingkat yang berfilosofi dengan menyesuaikan geografi, dan kepercayaan masyarakat setempat.<br /><br />\r\n\r\nSesuai dengan namanya, Rumah Limas adalah rumah tradisional berbentuk limas yang dibuat dengan gaya panggung. Bangunan khas daerah Palembang ini dibangun bertingkat. Kumpulan tingkat-tingkatnya disebut masyarakat sebagai Bengkalis yang memiliki makna tersendiri. Luasan Rumah Limas berkisar mulai dari 400 hingga 1000 meter persegi, sering kali dipinjamkan pemilik rumah untuk digunakan sebagai tempat pesta pernikahan dan acara adat.<br /><br />\r\n\r\nHampir seluruh bagian dari Rumah Limas terbentuk dari kayu. Pemilihan kayu dilakukan bukan tanpa sebab, namun menyesuaikan dengan karakter kayu dan kepercayaan masyarakat di Sumatera Selatan. Uniknya, jenis kayu-kayu yang digunakan merupakan kayu unggulan dan dikabarkan hanya tumbuh subur di daerah yang beribukota di Palembang.', 'Sumatera Selatan'),
(10, 'Rumah Rakit', 'rumrakit.jpeg', 'Rumah Rakit adalah rumah adat yang berasal dari Provinsi Bangka Belitung. Rumah rakit merupakan salah satu rumah tertua di provinsi itu', 'Karena Bangka Belitung memiliki banyak yang tergenang air atau di tepi laut, warga setempat harus menyesuaikan diri, yaitu dengan membangun rumah di atas air juga yang dinamakan Rumah Rakit. <br /><br />\r\n\r\nBentuk rumah adat provinsi Bangka belitung terlihat sangat unik karena merupakan perpaduan rumah Melayu dengan aksen arsitektur Tionghoa.<br /><br />\r\n\r\nPembuatan rumah ini menggunakan bambu khusus dan bahan lainnya, yang tentunya kuat dan membuatnya dapat mengapung di atas air. Rumah Rakit ini biasa menjadi tempat tinggal warga.', 'Sumatera (Bangka Belitung)'),
(11, 'Rumah Baduy', 'baduy.jpeg', 'Rumah Adat Badui adalah rumah adat dari provinsi Banten. Hal ini didasarkan pada rumah adat yang dimiliki oleh suku Badui.', 'Rumah Adat Badui adalah rumah adat dari provinsi Banten. Hal ini didasarkan pada rumah adat yang dimiliki oleh suku Badui. Suku Baduy/Badui merupakan kelompok etnis masyarakat adat suku Banten di wilayah Kabupaten Lebak, Banten. Bentuk rumah adat Badui sering di sebut dengan istilah Julang Ngapak<br /><br />\r\n\r\nSecara umum rumah adat Badui memilki gaya bangunan seperti rumah panggung. Bahan yang bisa digunakan untuk bangunan rumah adat Badui adalah bambu. Rumah adat Badui merupakan simbol kesederhanaan dari masyarakatnya. Adapun keutamaan yang ingin didapat dari bangunan rumah ini adalah fungsi perlindungan dan kenyamanan. Selain itu semangat kekeluargaan di suku Badui masih sangat kental, sehingga dalam proses pembangunan rumah adat Badui melibatkan warga sekitar dengan cara gotong royong.', 'Jawa (Banten)'),
(12, 'Rumah Kebaya', 'rumkebaya.jpeg', 'Rumah kebaya merupakan sebuah nama rumah adat suku Betawi. Disebut dengan rumah kebaya dikarenakan bentuk atapnya yang menyerupai pelana yang dilipat ', 'Rumah kebaya merupakan sebuah nama rumah adat suku Betawi. Disebut dengan rumah kebaya dikarenakan bentuk atapnya yang menyerupai pelana yang dilipat dan apabila dilihat dari samping maka lipatan-lipatan tersebut terlihat seperti lipatan kebaya.<br /><br />\r\n\r\nSelain Rumah Kebaya, suku Betawi juga memiliki rumah adat lainnya, seperti Rumah Gudang, Rumah Joglo, dan Rumah Panggung.<br /><br />\r\n\r\nCiri khas dari rumah ini adalah rumah ini memiliki teras yang luas yang berguna untuk menjamu tamu dan menjadi tempat bersantai keluarga. Pada zaman dahulu, masyarakat betawi membuat sumur di depan rumahnya dan pemakaman yang berada disamping rumah. Dan, dinding rumahnya terbuat dari panel-panel yang dapat dibuka dan digeser-geser ke tepinya. Hal ini dimaksudkan agar rumah terasa lebih luas.<br /><br />\r\n\r\nRumah ini dapat dibedakan menjadi 2 bagian dari segi sifatnya, yakni bagian depan bersifat semi publik, sehingga setiap orang dapat melihat betapa asri dan sejuknya rumah tersebut. Dan yang kedua adalah bagian belakang yang bersifat pribadi. Bagian ini hanya boleh dilihat oleh orang-orang dekat dari pihak pemilik rumah.', 'Jawa (DKI Jakarta)'),
(13, 'Rumah Kasepuhan', 'kasepuhan.jpeg', 'Rumah Kasepuhan Cirebon memiliki desain yang sangat unik, mewah dan berbeda dari rumah adat yang berada di Jawa Barat lainnya.', 'Rumat adat dari Jawa Barat ini merupakan peninggalan dari Kerajaan Islam di wilayah tersebut. Rumah yang sering disebut Keraton Kasepuhan ini sebenarnya merupakan perluasan dari Keraton Pakungwati. Tidak heran bila pintu utama keraton terlihat unik dan menawan.<br /><br />\r\n\r\nRumah Kasepuhan Cirebon adalah salah satu rumah adat yang pertama kali dibuat pada sekitar tahun 1529 oleh Pangeran Cakrabuana, yakni putra dari Prabu Siliwangi. Rumah Kasepuhan Cirebon memiliki desain yang sangat unik, mewah dan berbeda dari rumah adat yang berada di Jawa Barat lainnya.', 'Jawa Barat'),
(14, 'Rumah Joglo', 'joglo.jpeg', 'Rumah joglo merupakan rumah adat dari suku Jawa. Rumah ini memiliki banyak jenis dengan makna berbeda-beda.', 'Mungkin Anda sudah sering mendengar rumah adat dari suku Jawa yang sering disebut sebagai Rumah Joglo ini. Biasanya, bagian depan rumah akan ada pendopo untuk menjamu tamu.<br /><br />\r\n\r\nRumah adat Jawa Tengah ini memiliki empat tiang penopang. Selain itu, Anda juga bisa melihat sentuhan kejawen dari suku Jawa di sisi-sisi rumah. Kerangka bangunannya terdiri dari empat tiang utama penyangga struktur. Selain itu ada tumpang sari berupa susunan balok yang ditopang oleh soko guru. Masyarakat di Jawa Tengah menggunakan bentuk rumah persegi panjang dan bujur sangkar.\r\n\r\n', 'Jawa Tengah'),
(15, 'Rumah Panjang', 'panjang.jpeg', 'Rumah Panjang adalah salah satu rumah adat dari daerah Kalimantan Barat. Rumah Panjang adalah ciri khas dari masyarakat Dayak yang tinggal didaerah tersebut.', 'Rumah Panjang (rumah Radank) adalah salah satu rumah adat dari daerah Kalimantan Barat. Rumah Panjang adalah ciri khas dari masyarakat Dayak yang tinggal di daerah Kalimantan Barat. Hal ini dikarenakan rumah panjang adalah gambaran sosial kehidupan masyarakat Dayak di Kalimantan Barat. Rumah panjang juga merupakan pusat kehidupan dari masyarakat Dayak. Saat ini, rumah panjang di Kalimantan Barat dapat dikatakan hampir punah karena jumlahnya yang sedikit. Pada tahun 1960, pemerintah menghancurkan beberapa rumah panjang karena dicurigai menganut paham komunis<br /><br />\r\n\r\nRumah panjang di daerah Kalimantan Barat identik dengan rumah panjang yang ada di Kalimantan Tengah. Hal ini dikarenakan letak geografi Kalimantan Barat dan Kalimantan Tengah yang sangat berdekatan. Keduanya sama-sama dikenal dengan nama Rumah Betang.', 'Kalimantan Barat'),
(16, 'Rumah Lamin', 'lamin.jpeg', 'Rumah Lamin adalah rumah adat dari Kalimantan Timur. Rumah Lamin adalah identitas masyarakat Dayak di Kalimantan Timur. ', 'Rumah Lamin adalah rumah adat dari Kalimantan Timur. Rumah Lamin adalah identitas masyarakat Dayak di Kalimantan Timur. Rumah Lamin mempunyai panjang sekitar 300 meter, lebar 15 meter, dan tinggi kurang lebih 3 meter. Rumah Lamin juga dikenal sebagai rumah panggung yang panjang dari sambung menyambung.<br /><br />\r\n \r\nRumah ini dapat ditinggal oleh beberapa keluarga karena ukuran rumah yang cukup besar. Salah satu rumah Lamin yang berada di Kalimantan Timur bahkan dihuni oleh 12 sampai 30 keluarga. Rumah Lamin dapat menampung kurang lebih 100 orang. Pada tahun 1967, rumah Lamin diresmikan oleh pemerintah Indonesia.', 'Kalimantan Timur'),
(17, 'Rumah Joglo Situbondo', 'situbondo.jpeg', 'Joglo Situbondo adalah rumah adat provinsi Jawa Timur. Meskipun memiliki nama Joglo, tetapi rumah adat ini berbeda dengan rumah tradisional Joglo di Jawa Tengah.', 'Joglo Situbondo adalah rumah adat provinsi Jawa Timur. Meskipun memiliki nama Joglo, tetapi rumah adat ini berbeda dengan rumah tradisional Joglo di Jawa Tengah. Tak dipungkiri juga keduanya memiliki beberapa kemiripan. Kegunaan rumah ini adalah sebagai rumah tempat tinggal, tetapi ada juga yang memanfaatkannya sebagai peninggalan bersejarah. Hal ini dikarenakan rumah ini kental dengan budaya nenek moyang di masa lalu. Sinkretisme agama dan kepercayaan kejawen memberikan pengaruh terhadap bentuk dan tata ruang rumah adat Joglo.<br /><br />\r\n\r\nRumah Joglo Situbondo ini memiliki ciri khas dengan kesederhanaannya, namun memiliki cita rasa seni yang tinggi. Secara ukuran, rumah adat ini tidak terlalu besar.<br /><br />\r\n\r\nRumah tradisional ini memiliki bentuk limasan atau dara gepak. Joglo Situbondo sesuai dengan namanya banyak ditemukan di daerah Situbondo, Jawa Timur. Hal inilah yang menjadikan namanya menjadi Joglo Situbondo. Namun, selain di sana, rumah adat ini juga banyak ditemukan di Ponorogo.', 'Jawa Timur'),
(18, 'Rumah Bangsal Kencono', 'bangsal.jpeg', 'Bangsal Kencono adalah bangsal utama Keraton Ngayogyakarta Hadiningrat. Bangsal ini berbentuk joglo atau pendopo.', 'Bangsal Kencono (bahasa Jawa: ꦧꦁ​ꦱꦭ꧀ꦏꦼꦚ꧀ꦕꦤ, translit. Bangsal Kencana) adalah bangsal utama Keraton Ngayogyakarta Hadiningrat. Bangsal ini berbentuk joglo atau pendopo, dikelilingi halaman yang ditumbuhi oleh tanaman, dan sangkar burung. Di depan Bangsal Kencono terdapat dua patung batu Dwarapala, dua raksasa yang memegang gada (sejenis alat pemukul).<br /><br />\r\n\r\nRumah adat Bangsal kencono Kraton Yogyakarta berfungsi sebagai rumah tempat tinggal para raja Keraton Yogyakarta. Selain dikenal difungsikan sebagai tempat tinggal raja, rumah adat Bangsal Kencono juga memiliki fungsi sebagai ruang pertemuan penting. Dilihat dari desainnya bentuk Rumah Adat Bangsal Kencono memiliki sedikit pengaruh dari seni arsitektur khas Belanda, Portugis dan Cina. Di dalam desain Rumah Adat Bangsal Kencono tampak ada unsur-unsur desain tersebut meski didominasi oleh adat Jawa dari segi ukiran, atap, bentuk tiang dan dinding bangunannya. Atap Rumah Bangsal Kencono bentuknya mirip dengan rumah adat Jawa, Joglo, secara umumnya. Rumah ini memiliki bagian atap yang bubungan tingginya ditopang oleh empat tiang di tengah. Tiang itu disebut Soko Guru.', 'Jawa (DI Yogyakarta)'),
(19, 'Rumah Gapura Candi Bentar', 'gapuracandi.jpeg', 'Gapura Candi Bentar adalah salah satu bentuk keunikan dari arsitektur tradisional yang terdapat di provinsi Bali.', 'Rumah Gapura Candi Bentar sejatinya merujuk pada bangunan gapura yang menjadi gerbang rumah-rumah adat Bali. Gapura tersebut terdiri dari dua buah candi yang serupa dan sebangun dan membatasi sisi kiri dan sisi kanan pintu masuk ke pekarangan rumah. Gapura-gapura tersebut tidak memiliki atap penghubung pada bagian atasnya sehingga kedua sisinya terpisah sempurna, dan hanya terhubung di baagian dalam olehk-anak tangga yang menjadi jalan masuk. Gapura Candi Bentar dalam arsitektur Bali merupakan sebuah perwujudan bangunan yang berfungsi untuk masuk-keluar dari satu sisi ke sisi lainnya (dari luar ke dalam dan atau sebaliknya). Pada awalnya ketika arsitektur Bali masih sesuai dengan keadaan pada masa kerajaan, Gapura Candi Bentar hanya dibangun di lingkungan Puri (Istana Raja) dan Pura (tempat suci agama Hindu). Tidak ditemukan adanya Candi Bentar di perumahan masyarakat kebanyakan.<br /><br />\r\n\r\nBentuknya merupakan gapura, atau candi yang terbelah dua tepat di tengah-tengahnya sehingga menjadi bentukan yang simetri. Baik di puri mau pun di pura, Candi Bentarmenempati posisi di areal paling luar, menjadi pembuka jalansekaligus penerimabagi mereka yang akan mengunjungitempat tersebut. Para Undagi yang mengerjaakan bangunan ini sudah memiliki kepekaan yang tinggi terhadap lingkungannya, sehingga hasil yang dicapai sesuai dengan peruntukannya.Undagi memahami betul, di mana dan kapan Candi Bentar harus tampil megah, tampil normal (akrab), kokoh dan sebagainya.<br /><br />\r\n\r\nDi Pura yang merupakan Kahyangan Jagat seperti Pura Ulun Danu Batur (di Kintamani, Bangli), atau di Pura Besakih (Karangasem), tampak bahwa Gapura Candi Bentar berdiri kokoh, besar, tinggi atau dengan kata lain: megah. Areal Pura yang luas dan topografi yang tidak rata (rendah di arah luar, dan meninggi menuju ke areal Pura yang lebih di dalam), ikut mendukung kemegahan yang terwujud. Dalam teori modern, para undagi telah memperhitungkan dan menerapakan beberapa aspek estetika, dalam hal ini skala dan proporsi.', 'Nusa Tenggara (Bali)'),
(20, 'Rumah Adat Dalam Loka', 'dalamloka.jpeg', 'Rumah Adat Dalam Loka adalah kediaman raja-raja yang berasal dari Kabupaten Sumbawa, Nusa Tenggara Barat.', 'Rumah Adat Dalam Loka adalah kediaman raja-raja yang berasal dari Kabupaten Sumbawa, Nusa Tenggara Barat. Rumah dalam Loka atau istana Sumbawa ini merupakan peninggalan sejarah dari kerajaan Sumbawa. Istana Dalam Loka dibangun pada tahun 1885 oleh Sultan Muhammad Jalalludin III (1983-1931). <br /><br />\r\n\r\nRumah adat Dalam Loka merupakan desain asli rumah kediaman raja-raja Sumbawa. Kuatnya pengaruh budaya Islam yang masuk di wilayah ini pada masa itu telah membuat hampir seluruh aspek adat dan kesukuan masyarakat Sumbawa larut dalam nilai-nilai syariah Islam.<br /><br />\r\n\r\nRumah Adat Dalam Loka berbentuk rumah panggung dengan luas bangunan 904 M2. Istana Dalam Loka terlihat sangat megah. Istana yang dibangun dengan bahan kayu ini memiliki filosofi “adat berenti ko syara, syara barenti ko kitabullah”, yang berarti semua aturan adat istiadat maupun nilai-nilai dalam sendi kehidupan tau Samawa (masyarakat Sumbawa) harus bersemangatkan pada syariat Islam.', 'Nusa Tenggara Barat'),
(21, 'Rumah Bubungan Tinggi', 'bubungan.jpeg', 'Rumah Bubungan Tinggi atau Rumah Cacak Burung adalah salah satu jenis rumah Baanjung yaitu rumah tradisional suku Banjar di Kalimantan Selatan', 'Rumah Bubungan Tinggi atau Rumah Cacak Burung adalah salah satu jenis rumah Baanjung yaitu rumah tradisional suku Banjar di Kalimantan Selatan dan bisa dibilang merupakan ikonnya Rumah Banjar karena jenis rumah inilah yang paling terkenal karena menjadi maskot rumah adat khas provinsi Kalimantan Selatan. Di dalam kompleks keraton Banjar dahulu kala bangunan rumah Bubungan Tinggi merupakan pusat atau sentral dari keraton yang menjadi istana kediaman raja (bahasa Jawa: kedhaton) yang disebut Dalam Sirap (bahasa Jawa: ndalem) yang dahulu tepat di depan rumah tersebut dibangun sebuah Balai Seba pada tahaun 1780 pada masa pemerintahan Panembahan Batuah.<br /><br />\r\n\r\nRumah Bubungan Tinggi mirip Rumah tardisonal Betawi yang disebut Rumah Bapang, namun pada Rumah Bubungan Tinggi dibangun dengan konstruksi panggung dan memiliki anjung pada kiri dan kanan bangunannya yang sangat mirip dengan rumah adat Maanyan.\r\n\r\n', 'Kalimantan Selatan'),
(22, 'Rumah Betang', 'betang.jpeg', 'Rumah Betang merupakan tempat tinggal suku Dayak yang ada di seluruh Pulau Kalimantan, di Barat, Timur, Selatan, Tengah maupun Utara.', 'Rumah betang adalah rumah adat khas Kalimantan yang terdapat diberbagai penjuru Kalimantan dan dihuni oleh masyarakat Dayak terutama di daerah hulu sungai yang biasanya menjadi pusat permukiman suku Dayak.<br /><br />\r\n\r\nDi Kalimantan Barat, rumah betang biasa disebut rumah panjang, rumah radakng, atau rumah panjai. Di Kalimantan Tengah, ada yang menyebutnya lewu. Di Kalimantan Timur, ada yang menyebutnya lou atau lamin. Di Kalimantan Utara, rumah betang dikenal dengan lamin atau baloi. Sedangkan di Kalimantan Selatan di sebut Balai.', 'Kalimantan'),
(23, 'Rumah Baloy', 'baloy.jpeg', 'Rumah adat terkenal dari masyarakat Kalimantan Utara disebut Rumah Baloy. Rumah adat ini merupakan hasil kebudayaan seni arsitektur dari masyarakat suku Tidung, Kalimantan Utara.', 'Rumah adat terkenal dari masyarakat Kalimantan Utara disebut Rumah Baloy. Rumah adat ini merupakan hasil kebudayaan seni arsitektur dari masyarakat suku Tidung, Kalimantan Utara. Seperti suku lainnya, suku Tidung ini mempunyai kebudayaan dan model rumah adat sendiri. Walaupun rumah adat ini masih menggunakan sejumlah tiang tinggi pada bagian bawahnya, bentuk bangunan rumah adat ini terlihat lebih modern dan modis. <br /><br />\r\n\r\nBerdasarkan dugaan para peneliti, besar kemungkinan rumah adat ini adalah hasil pengembangan arsitektur Dayak dari Rumah Panjang (Rumah Lamin) seperti yang dihuni oleh suku Dayak Kenyah di Kalimantan Timur.\r\n\r\n', 'Kalimantan Utara'),
(24, 'Rumah Dulohupa', 'dulohupa.jpeg', 'Dulohupa adalah rumah adat atau rumah tradisional Indonesia yang berasal dari Kelurahan Limba, Kecamatan Kota Selatan, Kota Gorontalo, Provinsi Gorontalo.', 'Dulohupa adalah rumah adat atau rumah tradisional Indonesia yang berasal dari Kelurahan Limba, Kecamatan Kota Selatan, Kota Gorontalo, Provinsi Gorontalo . Penduduk Gorontalo menyebut Dulohupa dengan nama Yiladia Dulohupa Lo Ulipu Hulondhalo. <br /><br />\r\n\r\nDulohupa memiliki bentuk rumah panggung dengan badan terbuat dari papan dan struktur atapnya bernuansa daerah Gorontalo. Sebagai lambang dari rumah adat Gorontalo, Dulohupa memiliki hiasan berupa pilar-pilar kayu, sedangkan sebagai simbol tangga adat atau yang disebut juga dengan Tolitihu, Dulohupa memiliki dua buah tangga yang masing-masing berada di sebelah kanan dan kiri rumah. Saat ini, Dulohupa dilengkapi dengan taman bunga, bangunan tempat penjualan cendera mata, serta bangunan yang menyimpan kereta kerajaan yang disebut dengan Talanggeda.', 'Sulawesi (Gorontalo)'),
(25, 'Rumah Boyang', 'boyang.jpeg', 'Rumah Boyang merupakan rumah adat yang berasal dari Provinsi Sulawesi Barat. Rumah Boyang memiliki gaya arsitektur yang unik', 'Rumah Boyang merupakan rumah adat yang berasal dari Provinsi Sulawesi Barat. Rumah Boyang memiliki gaya arsitektur yang unik, berbentuk rumah panggung yang tersusun dari material kayu dan ditopang tiang-tiang penyangga. Rumah ini menjadi tempat tinggal Suku Mandar yang merupakan suku asli dari Sulawesi Barat.<br /><br />\r\n\r\nRumah boyang terdiri dari dua jenis, yaitu \"boyang adaq\" dan \"boyang beasa\". Boyang adaq merupakan tempat tinggal bagi bangsawan, sedangkan boyang beasa merupakan tempat tinggal rakyat biasa. Pada boyang adaq diberi ornamen yang melambangkan identitas tertentu yang mendukung tingkat status sosial penghuninya. Di antaranya memiliki tumbaq layar (penutup bubungan) yang memiliki tiga sampai tujuh susun, semakin banyak susunannya semakin tinggi derajat kebangsawanannya. Selain itu boyang adaq memiliki dua susun tangga, susunan pertama terdiri atas tiga anak tangga, sedangkan susunan kedua terdiri atas sembilan atau sebelas anak tangga. Kedua susunan tangga tersebut di antarai oleh pararang. Ciri-ciri yang dimiliki boyang beasa tidak semegah boyang adaq, karena hanya memiliki satu susun penutup bubungan dan satu susun anak tangga.', 'Sulawesi Barat'),
(26, 'Rumah Souraja', 'souraja.jpeg', 'Rumah Souraja merupakan salah satu warisan budaya rumah adat Suku Kaili Sulawesi Tengah. Rumah Souraja adalah rumah panggung seluas 368 meter persegi yang konstruksinya terbuat dari kayu.', 'Souraja atau disebut juga Banua Oge adalah rumah adat atau rumah tradisional Indonesia yang berasal dari Kelurahan Lere, Kecamatan Palu Barat, Kota Palu, Provinsi Sulawesi Tengah. Rumah adat ini dibangun pada abad ke-19 masehi atas prakarsa Raja Yodjokodi dan berfungsi sebagai tempat tinggal keluarga raja dan juga sebagai pusat pemerintahan kerajaan.<br /><br />\r\n\r\nSouraja ini merupakan sisa-sisa bangunan peninggalan Kerajaan Palu waktu itu yang dibangun oleh Raja Palu yang kepemilikannya selalu berganti. Souraja adalah Istana Raja Palu, karena sejak didirikannya bangunan ini ditempati oleh Raja-Raja Palu dan keluarganya silih berganti oleh keluarganya. Kepemilikan bangunan ini pun berlaku secara turun-temurun.', 'Sulawesi Tengah'),
(27, 'Rumah Walewangko', 'walewangko.jpeg', 'Rumah adat Walewangko merupakan rumah adat suku Minahasa yang tinggal di Sulawesi Utara. Dikenal juga dengan sebutan rumah pewaris', 'Rumah Pewaris atau Walewangko adalah rumah adat daerah Minahasa, Provinsi Sulawesi Utara. Minahasa dahulu dikenal dengan nama Tanah Malesung yang merupakan daerah Semenanjung tempat persinggahan Bangsa Portugis dan Spanyol. Oleh karena tanahnya yang subur, Bangsa Portugis dan Spanyol tertarik dengan daerah tersebut. Sementara itu, raja pertama Manado adalah keturunan Bangsa Spanyol, yaitu Muntu Untu. Dari raja pertama inilah diyakini masyarakat Minahasa mengenal rumah adat. Pada masa pemerintahan raja pertama ini, sentuhan teknologi sederhana mulai dikenal, misalnya beberapa alat pertukangan mulai masuk dan dikenal masyarakat. Pada bentuk fisik rumah adat yang dua tiang penyangganya tidak boleh disambung. Jadi, sudah dapat dibayangkan bahwa masyarakat kala itu, ketika mengambil kayu sebagai bahan rumah adat, sudah menggunakan perkakas pertukangan.<br /><br />\r\n\r\nRumah Pewaris juga dijadikan sebagai tempat penyimpanan hasil panen warga setempat. Rumah tersebut bentuknya adalah rumah panggung. Pembuatan rumah panggung pada zaman dahulu bertujuan menghindari serangan musuh dan binatang buas. Rumah pewaris memiliki 26 tiang penyangga yang menggunakan bahan kayu pada lantai dasar dan lantai 1 menggunakan bahan kayu dan beton.', 'Sulawesi Utara'),
(28, 'Rumah Buton', 'buton.jpeg', 'Rumah Malige merupakan rumah adat suku Buton Sulawesi Tenggara yang berbentuk rumah panggung.', 'Rumah adat Buton atau Istana Buton merupakan bangunan di atas tiang, dan seluruhnya dari bahan kayu. Bangunannya terdiri dari empat tingkat atau empat lantai. Ruang lantai pertama lebih luas dari lantai kedua. Sedangkan lantai keempat lebih besar dari lantai ketiga, jadi makin ke atas makin kecil atau sempit ruangannya, tetapi di lantai keempat sedikit lebih melebar. Seluruh bangunan tanpa memakai paku dalam pembuatannya, melainkan memakai pasak atau paku kayu. Tiang-tiang depan terdiri dari 5 buah yang berjajar ke belakang sampai delapan deret, hingga jumlah seluruhnya adalah 40 buah tiang. Tiang tengah menjulang ke atas dan merupakan tiang utama disebut tutumbu yang artinya tumbuh terus. Tiang-tiang ini terbuat dari kayu wala dan semuanya bersegi empat. <br /><br />\r\n\r\nUntuk rumah rakyat biasa, tiangnya berbentuk bulat. Biasanya tiang-tiang ini puncaknya terpotong. Dengan melihat jumlah tiang sampingnya dapat diketahui siapa atau apa kedudukan si pemilik. Rumah adat yang mempunyai tiang samping 4 buah berarti rumah tersebut terdiri dari 3 petak merupakan rumah rakyat biasa. Rumah adat bertiang samping 6 buah akan mempunyai 5 petak atau ruangan, rumah ini biasanya dimiliki oleh pegawai sultan atau rumah anggota adat Kesultanan Buton. Sedangkan rumah adat yang mempunyai tiang samping 8 buah berarti rumah tersebut mempunyai 7 ruangan dan ini khusus untuk rumah Sultan Buton.\r\n\r\n', 'Sulawesi Tenggara'),
(29, 'Rumah Tongkonan', 'tongkonan.jpeg', 'Tongkonan adalah rumah adat orang Toraja, yang merupakan tempat tinggal, kekuasaan adat, dan perkembangan kehidupan sosial budaya orang Toraja.', 'Tongkonan (ᨈᨚᨃᨚᨊ) adalah rumah adat orang Toraja, yang merupakan tempat tinggal, kekuasaan adat, dan perkembangan kehidupan sosial budaya orang Toraja. Arsitektur tongkonan dikenal dengan bentuknya yang khas melalui struktur bawah, tengah dan atas yang memiliki keindahan estetika struktur dan konstruksinya. Mekanika sistem struktur membentuk suatu sistem estetika arsitektural. <br /><br />\r\n\r\nTongkonan tidak lagi dijadikan rumah tempat tinggal tetapi sudah tidak dihuni lagi dikarenakan setiap keluarga yang mendiami Tongkonan pada umumnya telah membangun rumah tinggal sendiri. Rumah adat Tongkonan yang sarat dengan ukiran mengandung makna yaitu melambangkan status sosial pemilik Tongkonan menempati lapisan atas.', 'Sulawesi Selatan'),
(30, 'Rumah Musalaki', 'musalaki.jpeg', 'Rumah Musalaki adalah rumah adat atau rumah tradisional yang banyak dijumpai di provinsi Nusa Tenggara Timur, Indonesia. ', 'Rumah Musalaki adalah rumah adat atau rumah tradisional yang banyak dijumpai di provinsi Nusa Tenggara Timur, Indonesia. Rumah ini sendiri menjadi lambang dari provinsi Nusa Tenggara Timur. Rumah adat ini sendiri merupakan tempat tinggal khusus bagi kepala suku dari beberapa suku di provinsi Nusa Tenggara Timur. Karena sudah menjadi lambang dari provinsi, saat ini desain bangunan pemerintahan seperti kelurahan, kecamatan, hingga kabupaten di Nusa Tenggara Timur mayoritas mengadopsi konsep dari rumah Musalaki, serta di beberapa wilayah rumah ini sudah dihuni oleh masyarakat pada umumnya.<br /><br />\r\n\r\nSesuai dengan namanya, fungsi utama dari rumah Musalaki adalah sebagai tempat tinggal bagi ketua adat atau kepala suku, khususnya bagi suku Ende Lio. Selain berfungsi sebagai rumah tinggal kepala suku, rumah adat ini juga sering digunakan sebagai tempat ritual upacara adat, kegiatan musyawarah adat, dan berbagai macam kegiatan adat lainnya.', 'Nusa Tenggara Timur'),
(31, 'Rumah Baileo', 'baileo.jpeg', 'Baileo adalah rumah adat Maluku dan Maluku Utara, Indonesia. Baileo merupakan representasi kebudayaan Maluku dan memiliki fungsi yang sangat penting bagi kehidupan masyarakat.', 'Baileo adalah rumah adat Maluku dan Maluku Utara, Indonesia. Baileo merupakan representasi kebudayaan Maluku dan memiliki fungsi yang sangat penting bagi kehidupan masyarakat. Baileo adalah identitas setiap negeri di Maluku selain Masjid atau Gereja. Baileo berfungsi sebagai tempat penyimpanan benda-benda suci, tempat upacara adat, sekaligus sebagai balai warga. Ciri utama baileo adalah ukurannya besar, dan memiliki bentuk yang berbeda jika dibandingkan dengan rumah-rumah lain di sekitarnya.<br /><br />\r\n\r\nBentuk ornamen atau hiasan di rumah adat Beileo memiliki hubungan dengan adat istiadat dan kehidupan sehari-hari masyarakat Maluku. Negeri-negeri di Maluku memiliki arsitektur Baileo yang berbeda, namun fungsinya sama. Baileo dibuat dengan bahan yang kuat, dan dilengkapi dengan ornamen khas Maluku. Baileo tak berdinding, hal ini dimaksudkan agar roh nenek moyang dapat leluasa masuk dan keluar baileo. Baileo merupakan rumah panggung, yakni posisi lantainya berada di atas permukaan tanah. Lantai yang tinggi ini mempunyai makna bahwa agar roh-roh nenek moyang memilii tempat dan derajat yang tinggi dibandingkan masyarakat. Di rumah adat Baileo terdapat banyak ukiran dan ornamen yang bergambar dua ekor ayam yang berhadapan dan diapit oleh dua ekor anjing di sebelah kiri dan kanan. Ukiran tersebut memiliki makna kedamaian dan kemakmuran. Ukiran lainnya adalah bulan, bintang, dan matahari yang berada di atap dengan warna merah, kuning, dan hitam. Ukiran tersebut bermakna kesiapan Baileo (sebagai balai) dalam menjaga keutuhan adat beserta hukum adatnya.', 'Maluku'),
(32, 'Rumah Sasadu', 'sasadu.jpeg', 'Suku Sahu di Maluku Utara memiliki rumah tradisional bernama Sasadu. Rumah Sasadu berfungsi pusat pemerintahan adat Suku Sahu.', 'Sasadu merupakan rumah adat suku bangsa Sahu di Halmahera Barat yang juga merupakan suku bangsa asli dan tertua yang ada di daerah tersebut. Di rumah ini, masyarakat adat Sahu biasa berkumpul dalam pertemuan-pertemuan. Di Halmahera Barat, rumah ini lazim ditemui di setiap desa. Penggunaan Sasadu sebagai lokasi pertemuan masyarakat biasanya terkait dengan diselenggarakannya berbagai acara, misalnya ritual atau upacara adat seperti perayaan panen dan pemilihan ketua adat, dan menyambut tamu yang datang. Meski demikian dapat pula Sasadu digunakan hanya untuk sekadar bersantai tanpa ada acara khusus.<br /><br /> \r\n\r\nSecara etimologi, Sasadu berasal dari kata sadu yang dalam bahasa Sahu tidak punya arti apapun, sedangkan dalam bahasa Ternate artinya adalah menimba, dan sado berarti lengkap, genap bilangannya. Sasadu dibangun di bagian tengah kampung atau desa dengan lokasi yang tidak jauh jalan. Hal ini dimaksudkan agar Sasadu bisa dijangkau dengan mudah sehingga orang-orang dari seluruh penjuru kampung bisa mendatanginya untuk berkumpul.\r\n\r\n', 'Maluku Utara'),
(33, 'Rumah Kariwari', 'kariwari.jpeg', 'Rumah Kariwari adalah salah satu rumah adat khas Papua, lebih tepatnya adalah rumah adat dari Suku Tobati-Enggros yang bermukim di sekitar Teluk Yotefa dan Danau Sentani, Jayapura. ', 'Rumah Kariwari adalah salah satu rumah adat khas Papua, lebih tepatnya adalah rumah adat dari Suku Tobati-Enggros yang bermukim di sekitar Teluk Yotefa dan Danau Sentani, Jayapura. Berbeda dengan bentuk rumah adat Papua lainnya - seperti honai yang berbentuk bulat - rumah Kariwari berbentuk limas segi delapan. Rumah Kariwari biasanya terbuat dari, bambu, kayu besi dan daun sagu hutan. Rumah Kariwari terdiri dari dua lantai dan tiga kamar atau tiga ruangan, dengan fungsi yang masing-masing berbeda. Arsitektur rumah Kariwari juga dianggap sangat khas dan menjadi salah satu simbol penting bagi budaya Papua sehingga menjadi ikonik di anjungan Papua yang terdapat di Taman Mini Indonesia Indah.<br /><br />\r\n\r\nRumah Kariwari tidak seperti halnya rumah honai yang bisa ditinggali oleh siapa saja, bahkan tidak bisa pula menjadi rumah tinggal kepala suku - tidak seperti rumah honai yang memiliki fungsi politik dan hukum. Rumah Kariwari lebih spesifik sebagai tempat edukasi dan ibadah, oleh karena itu posisi rumah Kariwari dalam masyarakat Suku Tobati-Enggros dianggap sebagai tempat yang sakral dan suci. ', 'Papua'),
(34, 'Rumah Kaki Seribu', 'seribukaki.jpeg', 'Rumah Kaki Seribu adalah rumah adat asli dari penduduk Suku Arfak yang menetap di Kabupaten Manokwari, Papua Barat.', 'Rumah Kaki Seribu adalah rumah adat asli dari penduduk Suku Arfak yang menetap di Kabupaten Manokwari, Papua Barat.<br /><br />\r\n\r\nRumah adat tersebut dijuluki demikian karena menggunakan banyak tiang penyangga di bawahnya, sehingga jika dilihat memiliki banyak kaki seperti hewan kaki seribu. Sedangkan untuk bagian atapnya dibuat dari daun jerami atau daun sagu. Sementara untuk tiangnya menggunakan kayu, yang terdiri dari kayu berukuran tinggi dan pendek. Fungsi dari tiang kayu tersebut adalah untuk melindungi penduduk dari serangan musuh dan ancaman ilmu hitam. Rumah adar kaki seribu berjenis rumah panggung dan memiliki corak khas Manokwari. Rumah ini dalam bahasa lokal disebut Mod Aki Aksa (Igkojei).<br /><br />\r\n\r\nRumah adat Kaki Seribu pada umumnya dipakai oleh penduduk yang tinggal di daerah pegunungan dan berhawa dingin. Rumah ini dibuat berukuran tinggi untuk menghindari serangan hewan buas. Rumah ini juga tidak memiliki jendela, hal ini dimaksudkan agar suhu di dalam rumah tetap hangat.<br /><br />\r\n\r\nRumah Adat Kaki Seribu merupakan warisan turun-temurun dari penduduk Suku Arfak. Pembangunannya berdasarkan atas filosofi hidup masyarakat lokal, sehingga rumah adat tersebut hanya bisa ditemukan di daerah Kabupaten Pegunungan Arfak dan sekitarnya.', 'Papua Barat'),
(35, 'Rumah Honai', 'honai.jpeg', 'Rumah honai atau rumah onai merupakan rumah tradisional masyarakat Papua Pegunungan khususnya Suku Dani.', 'Rumah honai atau rumah onai merupakan rumah tradisional masyarakat Papua Pegunungan khususnya Suku Dani. Rumah Honai berbentuk bulat sederhana dengan pintu kecil dan tidak dilengkapi jendela. Tinggi rumah sekitar 2,5 meter yang terbagi menjadi dua bagian yaitu lantai bawah dan lantai atas. Lantai bawah biasa digunakan untuk tidur, sedangkan lantai atas digunakan untuk menjalankan aktivitas sehari-hari seperti makan, bersantai dan membuat kerajinan. Pada lantai bawah, dibagian tengah terdapat hipere yaitu tempat api unggun yang dipergunakan untuk memasak atau sekadar menghangatkan tubuh.<br /><br />\r\n\r\nKesederhanaan rumah honai bukanlah tanpa tujuan, struktur rumah yang kecil memberikan efek hangat. Selain itu rumah honai yang sederhana memudahkan pemiliknya untuk berpindah-pindah. Terdapat tiga jenis rumah honai, yaitu rumah Honai(khusus laki-laki), rumah Ebai(khusus perempuan), dan rumah Wamai(khusus binatang)', 'Papua'),
(36, 'Rumah Jew', 'jew.jpeg', 'Rumah Jew atau dikenal sebagai Rumah Bujang merupakan salah satu rumah adat yang berasal dari Suku Asmat, khususnya dari ibu kota provinsi Papua yaitu Agats.', 'Rumah Jew atau dikenal sebagai Rumah Bujang merupakan salah satu rumah adat yang berasal dari Suku Asmat, khususnya dari ibu kota provinsi Papua yaitu Agats. Rumah Jew yang memiliki beberapa nama lain yaitu Je,Jeu,Yeu, atau Yai ini merupakan rumah panggung berbentuk persegi panjang yang terbuat dari kayu dan dinding beserta atapnya terbuat dari daun pohon sagu atau pohon nipah yang telah dianyam. Hal unik yang terdapat dalam Rumah Jew ini adalah sama sekali tidak menggunakan paku melainkan menggunakan akar rotan sebagai penghubung. <br /><br />\r\n\r\nDisebut sebagai Rumah Bujang karena dalam rumah inilah tempat berkumpulnya laki-laki yang belum berkeluarga atau yang masih berstatus bujang. Anak-anak dibawah umur 10 tahun, wanita tidak perbolehkan masuk kedalam Rumah Jew ini.', 'Papua');

