CREATE TABLE `seni_musik` (
  `id` int(11) NOT NULL,
  `nama_musik` varchar(55) NOT NULL,
  `gambar` varchar(55) NOT NULL,
  `preview` text NOT NULL,
  `deskripsi` longtext NOT NULL,
  `daerah` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Dumping data for table `seni_musik`
--

INSERT INTO `seni_musik` (`id`, `nama_musik`, `gambar`, `preview`, `deskripsi`, `daerah`) VALUES
(1, 'Musik Gambang Kromong', 'gambangkromong.jpeg', 'Gambang kromong merupakan kesenian musik yang berasal dari Betawi. Biasanya gambang kromong dimainkan saat mengiringi pertunjukan lenong serta tari cokek.', 'Gambang kromong merupakan kesenian musik yang berasal dari Betawi. Biasanya gambang kromong dimainkan saat mengiringi pertunjukan lenong serta tari cokek. Namun, gambang kromong juga bisa membawakan berbagai lagu vokal serta instrumen, tanpa pengiring lenong dan tari cokek. Kesenian musik ini sudah ada sejak 1930-an dan masih terus berkembang hingga saat ini.\r\n<br/><br/>\r\nNama gambang kromong diambil dari alat musik yang bernama gambang dan kromong. Gambang merupakan alat musik tradisional yang sering disebut gambang kayu. Sedangkan kromong adalah bonang lima nada. Tidak hanya itu, gambang kromong juga diiringi dengan alat musik lainnya, seperti kongahyan, sukong, tehyan, gong, kempul, gong enam, ningnong serta kecrek. Musik gambang kromong memiliki kaitan yang erat dengan masyarakat Tionghoa. Karena musik ini sangat populer di kalangan masyarakat Tionghoa, pada sekitar tahun 1930-an. Kesenian musik ini sudah menjadi bagian kehidupan masyarakat Tionghoa, kala itu. Gambang kromong sering ditampilkan pada upacara pernikahan, perayaan Cap Go Meh, dan lain sebagainya.\r\n', 'jawa (betawi)'),
(2, 'Musik Keroncong', 'keroncong.jpeg', 'Musik keroncong merupakan genre musik yang lahir dari perpaduan budaya Barat serta Timur. Genre musik ini cukup digemari oleh masyarakat Indonesia. ', 'Musik keroncong merupakan genre musik yang lahir dari perpaduan budaya Barat serta Timur. Genre musik ini cukup digemari oleh masyarakat Indonesia. \r\n<br/><br/>\r\nAwal mula kepopuleran musik keroncong adalah pada awal abad ke-20. Saat itu belum ada metode perekaman musik atau industri rekaman. Walau industri rekaman belum muncul namun saa itu musik keroncong populer lewat berbagai pentas yang diselenggarakan. Pentas musik keroncong kala itu sangat menarik perhatian masyarakat dari berbagai kalangan untuk membeli tiket dan menonton pertunjukannya. \r\n<br/><br/>\r\nSaat itu musik keroncong lebih dikenal sebagai tradisi musik rakyat dari Kampung Tugu. Karena disesuaikan dengan lokasi penemuannya, yakni di Kampung Tugu. Pada awal penemuannya, musik keroncong menjadi primadona masyarakat peranakan Indo-Eropa kelas bawah. Musik yang dibawakan dengan gitar besar, gitar kecil, seruling, piul, dan rebana berhasil memikat hati mereka.\r\n', 'jawa (jakarta)'),
(3, 'Musik Gong Luang ', 'gongluang.jpeg', 'Gamelan Gong Luang merupakan bentuk gamelan yang tergolong tua, dengan repertoar gending yang masih ada, diperuntukkan dalam pengiring upacara pengabenan (Pitra Yadnya)', 'Gamelan Gong Luang merupakan bentuk gamelan yang tergolong tua, dengan repertoar gending yang masih ada, diperuntukkan dalam pengiring upacara pengabenan (Pitra Yadnya). Beberapa repertoar itu dikhususkan dalam prosesi upacara ngaben, seperti dalam prosesi pemandian jenazah (layon), pembungkusan, dan sampai pada pamitan (mepamit). Gamelan Gong Luang yang semula keberadaannya sangat vital bagi kehidupan masyarakat setempat, karena gamelan ini dimainkan selain untuk mengiringi upacara Pitra Yadnya dan Dewa Yadnya, juga difungsikan untuk iringan seni pertunjukkan lainnya seperti topeng, arja dan tari-tarian lepas. \r\n<br/><br/>\r\nDalam dua dekade terakhir ini gamelan tua ini mulai mengalami pergeseran dengan semakin jarangnya Gong Luang difungsikan dalam keperluan tersebut, perkembangannya sebatas pada pengiring upacara pengabenan saja, sudah tidak difungsikan untuk keperluan seni pertunjukkan lain. Gong Luang di Banjar Apuan Singapadu – Gianyar dewasa ini sudah jauh bergeser bila dibandingkan dengan keberadaannya di tahun 1980-an. Walawpun bentuk barungan serta tabuh-tabuhnya masih relatif sama, fungsi dan makna dari gamelan Gong Luang sudah mengalami banyak pergeseran.', 'nusa tenggara (bali)'),
(4, 'Musik Karang Dodou', 'karangdodou.jpeg', 'Musik Karang Dodou merupakan jenis musik tradisional yang berasal dari Tanah Siang, Barito Utara di Provinsi Kalimantan Tengah.', 'Musik Karang Dodou merupakan jenis musik tradisional yang berasal dari Tanah Siang, Barito Utara di Provinsi Kalimantan Tengah. Musik Karang Dodou biasanya dimainkan sebagai iringan Ritual Adat seperti memberi nama Bayi yang baru dilahirkan dan mengobati orang yang sedang sakit. Lagu-lagu yang dibawakan dalam musik Karang Dodou berisikan lirik mantera dan doa-doa. Instrumen alat musik yang dimainkan dalam musik Karang Dodou antara lain Gong besar dan Kecil, Kendang dan Kecapi. ', 'kalimantan tengah'),
(5, 'Musik Sasando Gong', 'sasandogong.jpeg', 'Musik Sasando Gong merupakan jenis musik tradisional yang berasal dari Pulau Rote di kawasan Provinsi Nusa Tenggara Timur.', 'Musik Sasando Gong merupakan jenis musik tradisional yang berasal dari Pulau Rote di kawasan Provinsi Nusa Tenggara Timur. Sasando Gong biasanya dimainkan sebagai pengiring untuk hiburan rakyat dan menyambut tamu yang berkunjung ke Pulau Rote. Musik Sasando Gong memiliki 2 tangga nada yaitu Slendro dan Pelog. Instrumen utama musik ini adalah dengan memainkan alat musik petik yang terbuat dari bambu, daun lontar dan kawat halus, dengan nada pentatonic berciri khas melodi etnis Pulau Rote yang diberi nama Alat Musik Tradisional Sasando.', 'nusa tenggara timur'),
(6, 'Musik Angklung Buhun', 'angklungbuhun.jpeg', 'Angklung Buhun adalah kesenian yang merupakan pelengkap dari upacara ritual padi; seperti ngaseuk atau Seren Taun.', 'Angklung Buhun adalah kesenian yang merupakan pelengkap dari upacara ritual padi; seperti ngaseuk atau Seren Taun. Upacara adat seren taun merupakan salah satu tradisi dalam masyarakat Sunda Banten yang kental dengan nuansa magis dan sakral. Nuansa sakral ini terbentuk oleh tahapan ritual yang khidmat dalam iringan suara instrumen musik yang mengiringinya. Di antara instrumen musik pengiring prosesi adat seren taun, terdapat alat musik bambu yang disebut Angklung Buhun.Dari segi bentuk, angklung buhun memiliki perbedaan mencolok dari angklung pada umumnya. Ukurannya lebih besar, dengan bahan bamboo apus. Suaranya pun lebih keras, dan terkesan kasar karena lobang resonansi yang relative lebih besar dari Angklung Padaeng / Ujo. Perbedaan lainnya; pada pernak-pernik yang terdapat di sisi atas bingkai angklung ini. Angklung buhun biasanya dilengkapi dengan batang padi yang diikat secara berkelompok atau rumbai-rumbai daun Aren muda.', 'jawa barat'),
(7, 'Musik Krumpyung', 'krumpyung.jpeg', 'Musik Krumpyung berasal dari Yogyakarta. Bahan untuk alat musiknya berasal dari bambu, seperti angklung yang nada bunyinya seperti gambang atau gong bumbung tiup.', 'Musik Krumpyung ini berasal dari Yogyakarta. Bahan yang dipakai untuk alat musiknya berasal dari bambu, sama seperti angklung yang nada bunyinya seperti gambang atau gong bumbung tiup. Alat musik Krumpyung ini biasa dimainkan bersama alat musik lain, seperti gejog lesung. Gejog lesung adalah suara penumbuk padi dengan irama, yang biasa dipakai untuk mengiringi nyanyian vokal seperti tembang-tembang.', 'jawa tengah (yogyakarta)'),
(8, 'Musik Krombi', 'krombi.jpeg', 'Musik tradisional Papua ini menggunakan bambu berlubang yang diberi tali sayatan rotan. Arti sebenarnya dari alat musik Krombi ini adalah alat musik petik.', 'Musik tradisional Papua ini menggunakan bambu berlubang yang diberi tali sayatan rotan. Hal unik dari alat musik Krombi ini adalah pada arti nama sebenarnya, yaitu alat musik petik. Namun nyatanya, alat musik ini tidak dimainkan dengan cara dipetik, melainkan dengan cara ditepuk.', 'papua'),
(9, 'Musik Huda', 'huda.jpeg', 'Musik Huda merupakan musik tradisional dari Minangkabau, yaitu Huda. Musik tradisional ini memiliki nuansa islami unik, karena dipadukan dengan budaya setempat.', 'Musik Huda merupakan musik tradisional dari Minangkabau, yaitu Huda. Musik tradisional ini memiliki nuansa islami unik, karena dipadukan dengan budaya setempat. Selain itu juga karena musik dimainkan dengan tiga jenis musik yang serupa namun tak sama. Termasuk Salaulaik Dulang, alat musik asli dari Tanah Minang.', 'sumatera barat (minangkabau)'),
(10, 'Musik Cilokak', 'cilokak.jpeg', 'Musik Cilokak dari Lombok. Musik tradisional Cilokak dimainkan memakai beberapa jenis alat musik lain, seperti gambus, drum, seruling, biola, gong, dll.', 'Musik Cilokak dari Lombok. Musik tradisional Cilokak dimainkan memakai beberapa jenis alat musik lain, seperti gambus, drum, seruling, biola, gong, dan lain-lain.', 'nusa tenggara barat'),
(11, 'Musik Tubuh Salimpat', 'tubuhsalimpat.jpeg', 'Musik tradisional bernama Tubuh Salimpat ini berasal dari Jambi. Musik ini dimainkan dengan alat musik kerenceng, gambus, dan rebana.', 'Musik tradisional bernama Tubuh Salimpat ini berasal dari Jambi. Musik ini dimainkan dengan alat musik kerenceng, gambus, dan rebana.', 'sumatera (jambi)'),
(12, 'Musik Panting', 'panting.jpeg', 'Musik Panting adalah kesenian musik asli dari Suku Banjar di Kalimantan Selatan. Alat musik utama dalam musik Panting adalah alat musik petik yang disebut panting. ', 'Musik Panting adalah kesenian musik asli dari Suku Banjar di Kalimantan Selatan. Alat musik utama dalam musik Panting adalah alat musik petik yang disebut panting. Panting dimainkan bersama dengan suling, biola, kendang, kempul, gong, marawis, ketipung dan tamborin. Musik Panting termasuk dalam musik jenis kordofon dengan tangga nada diatonik. Musik Panting selalu dimainkan pada saat Tari Japin ditampilkan.\r\n<br/><br/>\r\nPanting terbuat dari kayu nangka, Rengas, Laban (Vitex pinnata), Kemuning [4] kulit hewan, dan tali senar yang beragam. Bentuknya menyerupai gitar dengan ukuran yang lebih kecil dan ramping tanpa lekukan. Panting dapat dimainkan secara perorangan maupun beberapa orang. Panting kini sudah sangat jarang ditemui karena ditelan oleh Zaman.', 'kalimantan selatan '),
(13, 'Musik Tingkilan', 'tingkilan.jpeg', 'Tingkilan merupakan salah satu jenis kesenian musik oleh masyarakat Kutai di Provinsi Kalimantan Timur, Indonesia.', 'Tingkilan merupakan salah satu jenis kesenian musik oleh masyarakat Kutai di Provinsi Kalimantan Timur, Indonesia. Musik ini lahir seiring dengan masuknya Islam ke Kutai dan sedikit banyak memiliki kesamaan bunyi dengan kesenian rumpun Melayu lainnya. Tingkilan lantas menyebar melalui proses akulturasi dengan kebudayaan setempat, membuat musik ini kini terbagi menjadi tiga jenis (Hulu Mahakam, Tengah, dan Pantai) yang memiliki karakternya masing-masing. Kata tingkilan itu sendiri berarti menyindir lewat pantun dan musik. Makna ini masih bertahan, walaupun struktur musiknya itu sendiri telah mengalami berbagai bentuk perubahan.\r\n<br/><br/>\r\nTerdapat dua pandangan yang berbeda mengenai dari mana arti kata tingkilan berasal:\r\n<br/><br/>\r\nPertama, dari kata tingkil yang berarti sindir dalam bahasa kutai. Penambahan akhiran -an menjadikannya bermakna sindiran. Tingkilan dalam arti ini merupakan sindiran berbentuk pantun, berisi kritik dan saran, serta disampaikan dengan nyanyian yang diiringi alat musik gambus dan ketipung. Sindiran bagi orang-orang Kutai bisa berbentuk nasihat, kritik, dan teguran yang mencolok. Jadi, tingkilan merupakan media untuk menegur, menyapa atau sebagai media interaksi melalui lagu. Peningkil mempunyai kemampuan untuk menciptakan pantun secara spontan yang berisi sindiran. Pantun-pantun nya terucap begitu saja saat peningkil melantunkan lagu dan bermain gambus. Adapun isi sindiran pantun tersebut biasanya tentang cinta, keadilan, harapan, atau sesuai permintaan pemesan/pengundang/yang punya hajatan.\r\n<br/><br/>\r\nKedua, Tingkilan berasal dari bahasa kutai, di mana terdiri dari 2 (dua) kosakata yaitu Ting & Kil, Ting artinya suara sebuah senar yang dipetik, sedangkan Kil adalah pekerjaan memetik senar gambus dan adanya akhiran an disitu adalah symbol perbuatan orang yang memainkan atau biasa disebut Ningkil (dalam arti perbuatan). Dalam bahasa hakikat atau filsafat Kutai Kartanegara, Ting itu berarti: cepat atau secepat kilat sedangkan Kil itu berarti: Ketangkasan atau kemampuan. Tingkilan dalam bahasa filsafat kutai kartanegara adalah kemampuan yang lebih atau ketinggian ilmu. Dapat dijabarkan lagi arti kemampuan lebih atau ketinggian ilmu itu adalah ketaatan, santun, rendah hati menuju pada iman dan taqwa.', 'kalimantan timur'),
(14, 'Musik Gaghahanggase', 'gaghahanggase.jpeg', 'Musik Gaghahanggase merupakan jenis musik tradisional yang berasal dari Kepulauan Talaud di kawasan Provinsi Sulawesi Utara.', 'Musik Gaghahanggase merupakan jenis musik tradisional yang berasal dari Kepulauan Talaud di kawasan Provinsi Sulawesi Utara. Jenis musik tradisional ini biasanya dimainkan untuk kegiatan upacar adat dan hiburan rakyat Etnis Sangihe Talaud. Gaghahanggase merupakan jenis musik paduan dari alat musik diatonis dan non-diatonis. Musik Gaghahanggase menggunakan instrumen alat musik tradisional seperti Tambur, Seheng, Kentel, Tateng korang, Tangonggong, Behohang, dan Kalikotong yang mengiring Vokal yang dinyanyikan oleh Vokal Laki-laki dan Perempuan. ', 'sulawesi utara'),
(15, 'Musik Goong Renteng', 'goongrenteng.jpeg', 'Musik Goong Renteng atau Gamelan Renteng adalah jenis musik tradisional khas Suku Sunda di Jawa Barat.', 'Musik Goong Renteng atau Gamelan Renteng adalah jenis musik tradisional khas Suku Sunda di Jawa Barat. Musik Goong Renteng merupakan jenis musik tradisional yang berumur cukup tua dan dikenal sejak abad ke-16.  Musik Tradisional Goong Renteng biasanya dimainkan untuk memperingati Maulid Nabi Muhammad SAW dan menyambut tamu kehormatan adat. Goong Renteng memiliki 2 tangg nada yaitu Salendro dan Pelog. Instrumen alat musik yang digunakan untuk memainkan jenis musik tradisional Goong Renteng antara lain Cempres, Kongkoang, Paneteg, Goong, dan Kendang.', 'jawa barat'),
(16, 'Musik Senandung Jolo', 'senandungjolo.jpeg', 'Musik Senandung Jolo adalah jenis musik tradisional yang berasal dari Dusun Tanjung, Provinsi Jambi.', 'Musik Senandung Jolo adalah jenis musik tradisional yang berasal dari Dusun Tanjung, Provinsi Jambi. Musik Senandung Jolo biasanya dimainkan untuk iringan ritual adat kegiatan menanam padi atau Nugal Jolo dan hiburan rakyat pada acara-acara masyarakat. Senandung Jolo merupakan nyanyian yang bersyair pantun dan diiringin oleh musik tradisional khas Jambi. Intrumen alat musik yang digunakan untuk memainkan musik Senandung Jolo antara lain Tetawak, Rebano, Gendang Panjang, Gong dan Beduk.', 'sumatera (jambi)');

