--
-- Dumping data for table `seni_tari`
--

INSERT INTO `seni_tari` (`id`, `nama_tari`, `gambar`, `preview`, `deskripsi`, `daerah`) VALUES
(1, 'Tari Saman', 'saman.jpeg', 'Tari Saman merupakan tarian yang berasal dari suku Gayo dan biasanya ditampilkan dalam suatu perayaan penting di suatu peristiwa adat.', 'Tari Saman merupakan tarian yang berasal dari suku Gayo dan biasanya ditampilkan dalam suatu perayaan penting di suatu peristiwa adat. Syair pada tariannya juga mempergunakan Bahasa-bahasa Gayo. Selain itu tarian ini juga kerap ditampilkan saat merayakan hari raya kelahiran Nabi Muhammad SAW.\r\n<br /><br />\r\nBeberapa literatur menyatakan Syekh Saman yaitu seorang ulama yang berasal dari Suku Gayo di Aceh Tenggaralah yang kemudian menemukan dan mendirikan tari Saman hingga akhirnya ditetapkan oleh UNESCO sebagai Daftar Representatif Budaya Takbenda  pada 24 November 2011 silam.\r\n<br /><br />\r\nSebelum Tari Saman dimulai dilakukan pembukaan dimana tampil pemuka adat atau seorang tua cerdik pandai yang akan mewakili masyarakat setempat (keketar) dengan nasihat-nasihat yang berguna bagi para penonton dan pemainnya sekaligus.\r\n<br /><br />\r\nSyair dan lagu pengungkapannya sendiri dilakukan secara Bersama-sama dan berkesinambungan, penari saman biasanya terdiri dari pria-pria muda yang menggunakan pakaian adat. Penyajian tarian ini juga kemudian dipertandingkan antar grup dan grup tamu. Penilaian pada tari saman akan dititik beratkan pada kemampuan setiap grup dalam mengikuti berbagai gerakan, lagu (syair) dengan bentul tari yang disajikan oleh pihak lawan. Sebab tari saman dapat dikreasikan dengan gerakan yang berbeda-beda.', 'sumatera (aceh)'),
(2, 'Tari Tor Tor', 'tortor.jpeg', 'Tari Tortor adalah jenis tarian tradisional dari suku Batak yang berasal dari provinsi Sumatra Utara. Tari tortor menjadi bagian penting dalam adat suku Batak.', 'Tari Tortor adalah jenis tarian tradisional dari suku Batak yang berasal dari provinsi Sumatra Utara, meliputi daerah kabupaten Tapanuli Utara, Humbang Hasundutan, Toba, Samosir, dan Tapanuli Tengah. Dan saat ini, tari tortor menjadi bagian penting dalam adat suku Batak, baik dalam acara adat pernikahan ataupun pentas seni di seluruh Indonesia. Melalui tarian ini lah masyarakat adat Batak menyampaikan harapan dan seluruh doa-doanya. Peragaan sikap dan perasaan melalui tortor selalu menggambarkan kondisi dan situasi yang dialami.\r\n<br /><br />\r\nTidak semua orang dapat mengadakan tari Tortor, melainkan hanya orang tua yang seluruh anaknya yang sudah menikah atau sudah lepas tanggung jawab dalam hal mengurus anak-anak mereka. Melakukan tarian Tortor semestinya telah disetujui oleh keluarga dan kepala adat.', 'sumatera utara'),
(3, 'Tari Piring', 'piring.jpeg', 'Tari piring adalah tarian tradisional Minangkabau yang menampilkan atraksi menggunakan piring. Gerakannya diambil dari langkah dalam silat Minangkabau atau silek.', 'Tari piring adalah tarian tradisional Minangkabau yang menampilkan atraksi menggunakan piring. Para penari mengayunkan piring di tangan mengikuti gerakan-gerakan cepat yang teratur, tanpa satu pun piring terlepas dari tangan. Gerakannya diambil dari langkah dalam silat Minangkabau atau silek.\r\n<br /><br />\r\nTari ini dipopulerkan oleh Huriah Adam. Saat ini, tari piring dipertunjukkan untuk penyambutan tamu terhormat atau pembukaan upacara adat. Bersama dengan tari saman, pendet, dan jaipong, tari ini menjadi tarian populer Indonesia yang kerap ditampilkan di ajang promosi pariwisata dan kebudayaan Indonesia.\r\n<br /><br />\r\nSecara tradisional, tari ini berasal dari Solok, Sumatra Barat. Menurut legenda awal kemunculannya, Tari Piring ini berfungsi sebagai tarian dalam upacara kesuburan. Tarian ini juga menjadi salah satu bentuk seni tradisional yang banyak sekali menyimpan nilai-nilai estetis yang cukup tinggi dan mengandung nilai-nilai kebudayaan leluhur yang sangat mendalam. Tari ini juga merupakan ritual ucapan rasa syukur masyarakat setempat kepada dewa-dewa setelah mendapatkan hasil panen yang melimpah ruah. Ritual dilakukan dengan membawa sesaji dalam bentuk makanan yang diletakkan di dalam piring sembari melangkah dengan gerakan yang dinamis.\r\n<br /><br />\r\nSetelah masuknya agama Islam ke Minangkabau, tari piring tidak lagi digunakan sebagai ritual ucapan rasa syukur kepada dewa-dewa. Akan tetapi, tari tersebut digunakan sebagai sarana hiburan bagi masyarakat banyak yang ditampilkan pada acara-acara keramaian.', 'sumatera barat (minangkabau)'),
(4, 'Tari Taruk Langgai', 'taruklanggai.jpeg', 'Tari Turuk Langgai yang berasal dari Mentawai adalah tari yang kuat ikatannya dengan alam. mencerminkan mengenai perilaku hewan dan mengandung nilai luhur penting.', 'Salah satu daerah yang memiliki warisan budaya yang sangat khas di wilayah Sumatra adalah Pulau Mentawai. Selain memiliki keindahan alam, Mentawai juga memiliki beberapa warisan budaya yang wajib kita lestarikan bersama. Salah satu warisan budaya tersebut adalah seni tari, tepatnya adalah tari Turuk Langgai. \r\n<br /><br />\r\nTari Turuk Langgai yang berasal dari Mentawai adalah salah satu seni tari yang kuat ikatannya dengan alam. Tari khas suku Mentawai ini mencerminkan mengenai perilaku hewan dan mengandung nilai luhur yang penting bagi masyarakat Mentawai. Karena sudah sejak zaman dahulu masyarakat Mentawai sudah menggantungkan kehidupan mereka dengan cara berburu di laut atau menjadi nelayan. Hal ini juga dipengaruhi oleh letak geografis dari pulau Mentawai sendiri.\r\n<br /><br />\r\nSejak awal kemunculannya, Tari Turuk Laggai menjadi bagian dari upacara pengobatan, yang melibatkan dukun (Sikerei). Prosesinya dilakukan pada malam hari, karena mereka percaya bahwa komunikasi antara roh gaib dengan para Sikerei akan berjalan lebih lancar. Seiring berkembang zaman, akhirnya sekarang tarian ini dijadikan hiburan.', 'sumatera (mentawai)'),
(5, 'Tari Ronggeng Blantek', 'ronggengblantek.jpeg', 'Ronggeng blantek merupakan tari kreasi baru yang diangkat dari teater rakyat Betawi, yaitu topeng blantek. Topeng ialah sebutan untuk seni peran, blantek ialah suara musik', 'Ronggeng blantek merupakan tari kreasi baru yang diangkat dari teater rakyat Betawi, yaitu topeng blantek. Topeng merupakan sebutan untuk seni peran atau lawakan, sedangkan nama blantek diambil dari suara musik pengiring yang selalu berbunyi “blang blang tek tek”.\r\n<br /><br />\r\nPerkembangan, Potensi dan Tantangannya, topeng blantek pada mulanya dibawakan para pemula yang sedang belajar main topeng atau lenong. Iringan musiknya rebana biang. Ada juga yang diiringi gamelan sederhana. Malah kaleng yang dipukul bagai kromong. “Bunyi yang blentang blantek itulah kemudian melahirkan sebutan blantek,”\r\n<br /><br />\r\nDahulu topeng blantek sendiri adalah pertunjukan teater rakyat yang biasa dipentaskan untuk menghibur para tuan tanah. Topeng blantek biasanya menceritakan tentang kehidupan masyarakat Betawi yang dikemas dengan lawakan.\r\n<br /><br />\r\nPertunjukan topeng blantek biasanya dibuka atau diawali dengan sebuah tarian ronggeng. Dalam perkembangannya tarian ini terlepas dan koreografernya mengemas bentuk kreasi tersendiri menjadi tari ronggeng blantek yang dipertunjukkan secara terpisah.\r\n<br /><br />\r\n“Tari blantek (ronggeng blantek), diangkat dari pertunjukan teater Belanda, yaitu topeng blantek, di mana dalam memulai suatu pertunjukan, sebagai pembukaan, diawali dengan sebuah pertunjukan tari (ronggeng blantek)', 'jawa (betawi)'),
(6, 'Tari Jaipong', 'jaipong.jpeg', 'Tari Jaipong merupakan sebuah tarian tradisional yang berasal dari daerah Karawang, Jawa Barat. Tarian ini berkembang di era tahun 1960 an. ', 'Tari Jaipong merupakan sebuah tarian tradisional yang berasal dari daerah Karawang, Jawa Barat. Tarian ini berkembang di era tahun 1960 an. Awalnya tari ini dikenal masyarakat dengan nama Tari Banjet. Sebuah pertunjukan kesenian tari yang ditampilkan dengan gerakan tari dan diiringi alunan musik berupa instrumen gamelan.\r\n<br /><br />\r\nDulu tarian ini dijadikan sebagai hiburan bagi masyarakat. Tarian jaipong adalah sebuah inovasi yang dibuat oleh seorang seniman yang berasal dari daerah Karawang bernama H. Suanda.\r\n<br /><br />\r\nHaji Suanda merupakan salah satu seniman berbakat yang berasal dari daerah Karawang. Beliau mempunyai bakat yang luar biasa. Serta memiliki keahlian menguasai sejumlah kesenian tradisional Indonesia dari berbagai daerah, Terutamanya daerah Karawang. Beberapa kesenian daerah yang dikuasainya diantaranya yaitu Wayang Golek, Pencak Silat, Ketuk Tilu, dan Topeng Banjet.\r\n<br /><br />\r\nKemudian, H. Suanda membuat sebuah inovasi. Beliau menciptakan inovasi berupa menggabungkan beberapa macam tarian yang dikuasainya menjadi satu. Tarian tersebut terdiri dari Tari Banjet, Tari Pencak Silat, Tari Ketuk Tilu, Tari Wayang Golek, dan Tari Topeng. Hasil dari pencampuran tersebut yakni munculnya sebuah karya seni daerah yang unik dan digemari oleh masyarakat.\r\n<br /><br />\r\nPada saat pertunjukan kesenian daerah tersebut digelar belum diberikan nama tari jaipong. Iringan musik yang dipakai dalam pementasan itu menggunakan alat musik yang diantaranya adalah Gendang, Degung, Gong, dan alat musik yang diketuk lainnya. Perpaduan berbagai jenis alat musik tersebut melahirkan sebuah musik pengiring tarian menjadi sangat energik dan unik.\r\n<br /><br />\r\nSelain iringan alat musik, pada setiap pementasan kesenian tari ini juga diiringi oleh nyanyian dari seorang sinden. Kemudian dari pertunjukan tersebut, menarik perhatian dari seorang seniman yang berasal dari daerah Sunda bernama Gugum Gumbira untuk mempelajarinya.\r\n<br /><br />\r\nKala itu Gugum Gumbira sudah lihai pada tarian ini. Lalu, beliau menyusun ulang semua gerakan pada tarian itu, hingga akhirnya terciptalah sebuah tarian bernama Jaipong. Pada sejak itulah, tarian ini mulai diperkenalkan oleh masyarakat Bandung.', 'jawa barat (karawang)'),
(7, 'Tari Topeng', 'topeng.jpeg', 'Tari Topeng adalah tarian yang penarinya mengenakan topeng. Topeng telah ada di dunia sejak zaman pra-sejarah. Menjadi bagian dari upacara adat atau penceritaan', 'Tari Topeng adalah tarian yang penarinya mengenakan topeng. Topeng telah ada di dunia sejak zaman pra-sejarah. Secara luas digunakan dalam tari yang menjadi bagian dari upacara adat atau penceritaan kembali cerita-cerita kuno dari para leluhur. Diyakini bahwa topeng berkaitan erat dengan roh-roh leluhur yang dianggap sebagai interpretasi dewa-dewa. Pada beberapa suku, topeng masih menghiasi berbagai kegiatan seni dan adat sehari-hari.\r\n<br /><br />\r\nCerita klasik Ramayana dan cerita Panji yang berkembang sejak ratusan tahun lalu menjadi inspirasi utama dalam penciptaan topeng di Jawa. Topeng-topeng di Jawa dibuat untuk pementasan sendratari yang menceritakan kisah-kisah klasik tersebut.\r\n\r\n\r\n', 'jawa barat (cirebon)'),
(8, 'Tari Bedhaya', 'bedhaya.jpeg', 'Tari Bedhaya Ketawang adalah tarian kebesaran yang hanya di pertunjukan ketika penobatan serta peringatan kenaikan tahta raja di Kasunanan Surakarta.', 'Tari Bedhaya Ketawang adalah tarian kebesaran yang hanya di pertunjukan ketika penobatan serta peringatan kenaikan tahta raja di Kasunanan Surakarta. Tarian ini merupakan tarian sakral yang suci bagi masyarakat dan Kasunanan Surakarta. Nama Tari Bedhaya Ketawang diambil dari kata bedhaya yang berarti penari wanita di istana, dan ketawang yang berarti langit, yang identik sesuatu yang tinggi, kemuliaan dan keluhuran.\r\n<br /><br/ >\r\nMenurut sejarahnya, tarian ini berawal ketika Sultan Agung memerintah kesultanan Mataram tahun 1613 – 1645. Pada suatu saat Sultan Agung melakukan ritual semedi lalu beliau mendengar suara senandung dari arah langit, Sultan agung pun terkesima dengan senandung tersebut. Lalu beliau memanggil para pengawalnya dan mengutarakan apa yang terjadi. Dari kejadian itulah Sultan Agung menciptakan tarian yang diberi nama bedhaya ketawang. Ada pula versi lain yang mengatakan bahwa dalam pertapaannya Panembahan Senapati bertemu dan memadu kasih dengan Ratu Kencanasari atau Kangjeng Ratu Kidul yang kemudian menjadi cikal bakal tarian ini.\r\n<br /><br/ >\r\nNamun setelah perjanjian Giyanti pada tahun 1755, dilakukan pembagian harta warisan kesultanan mataram kepada Pakubuwana III dan Hamengkubuwana I. Selain pembagian wilayah, dalam perjanjian tersebut juga ada pembagian warisan budaya. Tari Bedhaya Ketawang akhirnya di berikan kepada kasunanan Surakarta dan dalam perkembangannya tarian ini tetap dipertunjukan pada saat penobatan dan upacara peringatan kenaikan tahta sunan Surakarta.\r\n<br /><br/ >\r\nTari Bedhaya Ketawang ini menggambarkan hubungan asmara Kangjeng Ratu Kidul dengan raja mataram. Semua itu diwujudkan dalam gerak tarinya. Kata – kata yang terkandung dalam tembang pengiring tarian ini menggambarkan curahan hati Kangjeng Ratu Kidul kepada sang raja. Tarian ini biasanya di mainkan oleh sembilan penari wanita. Menurut kepercayaan masyarakat, setiap pertunjukan Tari Bedhaya Ketawang ini dipercaya akan kehadiran kangjeng ratu kidul hadir dan ikut menari sebagai penari kesepuluh.\r\n<br /><br/ >\r\nSebagai tarian sakral, ada beberapa syarat yang harus di miliki setiap penarinya. Syarat yang paling utama yaitu para penari harus seorang gadis suci dan tidak sedang haid. Jika sedang haid maka penari harus meminta ijin kepada Kangjeng Ratu Kidul lebih dahulu dengan melakukan caos dhahar di panggung sanga buwana, keraton Surakarta. Hal ini di lakukan dengan berpuasa selama beberapa hari menjelang pertunjukan. Kesucian para penari sangat penting, karena konon katanya, saat latihan berlangsung, Kangjeng Ratu Kidul akan datang menghampiri para penari jika gerakannya masih salah.', 'jawa (yogyakarta, surakarta)'),
(9, 'Tari Serimpi', 'serimpi.jpeg', 'Tari serimpi berasal dari tradisi keraton Mataram yang waktu itu masih berada di Daerah Istimewa Yogyakarta. Tari serimpi adalah tarian yang sangat sakral dan mistis', 'Tari serimpi berasal dari tradisi keraton Mataram yang waktu itu masih berada di Daerah Istimewa Yogyakarta. Sehingga, bisa dikatakan bahwa tari serimpi berasal dari Daerah Istimewa Yogyakarta. Tari serimpi adalah tari klasik yang hanya ditampilkan di wilayah keraton saja. Saat itu, tari serimpi adalah salah satu tarian yang sangat sakral dan mistis.  \r\n<br/><br/>\r\nJadi, tari serimpi berasal dari tradisi keraton ini hanya dimainkan di dalam lingkungan keraton untuk acara kenegaraan dan memperingati kenaikan tahta sultan. Tari serimpi ditampilkan oleh empat orang penari sebagai simbol empat arah mata angin dan empat unsur dunia yakni api, udara, air, dan tanah.  Tari ini ditampilkan dengan gerakan lemah gemulai dan diiringi alunan gamelan. Tema tari serimpi adalah pertentangan antara nafsu dan akal serta benar dan salah. Dalam perkembangannya, tari serimpi banyak ditampilkan untuk menyambut tamu agung.  Sementara, fungsi tarian srimpi sendiri pada masa lalu dipertunjukkan sebagai salah satu ritual sakral dalam acara-acara tertentu seperti pisowanan agung dan peringatan hari penting dalam keraton. \r\n\r\n', 'jawa (yogyakarta)'),
(10, 'Tari Gambyong', 'gambyong.jpeg', 'Gambyong merupakan salah satu bentuk tarian Jawa klasik yang berasal-mula dari wilayah Surakarta dan biasanya dibawakan untuk pertunjukan atau menyambut tamu.', 'Gambyong merupakan salah satu bentuk tarian Jawa klasik yang berasal-mula dari wilayah Surakarta dan biasanya dibawakan untuk pertunjukan atau menyambut tamu. Gambyong bukanlah satu tarian saja melainkan terdiri dari bermacam-macam koreografi, yang paling dikenal adalah Tari Gambyong Pareanom (dengan beberapa variasi) dan Tari Gambyong Pangkur (dengan beberapa variasi). Meskipun banyak macamnya, tarian ini memiliki dasar gerakan yang sama, yaitu gerakan tarian tayub/tlèdhèk. Pada dasarnya, gambyong dicipta untuk penari tunggal, namun sekarang lebih sering dibawakan oleh beberapa penari dengan menambahkan unsur blocking panggung sehingga melibatkan garis dan gerak yang serba besar.', 'Jawa (solo)'),
(11, 'Tari Reog', 'reog.jpeg', 'Reog adalah tarian tradisional dalam arena terbuka yang berfungsi sebagai hiburan rakyat, mengandung unsur magis, penari utamanya orang brkepala singa berbulu merak', 'Reog adalah tarian tradisional dalam arena terbuka yang berfungsi sebagai hiburan rakyat, mengandung unsur magis, penari utama adalah orang berkepala singa dengan hiasan bulu merak, ditambah beberapa penari bertopeng dan berkuda lumping dan Reog asli dari Indonesia [1] Reog merupakan salah satu seni budaya yang berasal dari Jawa Timur bagian barat-laut, dan Ponorogo dianggap sebagai kota asal Reog yang sebenarnya. Gerbang kota Ponorogo dihiasi oleh sosok warok dan gemblak, dua sosok yang ikut tampil pada saat Reog dipertunjukkan. Reog adalah salah satu budaya daerah di Indonesia yang masih sangat kental dengan hal-hal yang berbau mistik dan ilmu kebatinan yang kuat.\r\n<br/><br/>\r\nReog Sebuah seni pertunjukan tua yang bertahan dari gempuran zaman. Memiliki nilai seni sekaligus nilai-nilai luhur. Reog Ponorogo adalah bentuk kesenian yang tumbuh berabad-abad lalu. Reog merupakan salah satu seni budaya yang berasal dari Jatim bagian barat-laut dan Ponorogo dianggap sebagai kota asal Reog yang sebenarnya. Gerbang kota Ponorogo dihiasi oleh sosok warok dan gemblak, dua sosok yang ikut tampil pada saat Reog dipertunjukkan. Reog adalah salah satu budaya daerah di Indonesia yang masih sangat kental dengan hal-hal yang berbau mistik dan ilmu kebatinan yang kuat. Tarian tradisional dalam arena terbuka yang berfungsi sebagai hiburan rakyat, mengandung unsur magis, penari utama adalah orang berkepala singa dengan hiasan bulu merak, ditambah beberapa penari bertopeng dan berkuda lumping. Ada dua ragam bentuk reog Ponorogo yang dikenal saat ini, yakni Reog Obyog dan Reog Festival. Reog modern biasanya dipentaskan dalam beberapa acara seperti pernikahan, khitanan, dan hari-hari besar Nasional. Seni Reog Ponorogo terdiri dari beberapa rangkaian 2 sampai 3 tarian pembukaan. Reog obyog Sering pentas di pelataran atau jalan tanpa mengikuti pakem tertentu. Biasanya mengisi acara hajatan, bersih desa, hingga pementasan semata untuk menghibur. Sedangkan Reog Festival sudah mengalami modifikasi dan ditampilkan sesuai pakem dalam acara tahunan Festival Reog yang diadakan Pemerintah Kota Ponorogo sejak 1997. Berdasarkan lokakarya pengusulan ICH UNESCO tanggal 15-16 Februari 2022, Reog Ponorogo masuk Daftar Warisan Budaya Tak benda (WBTb) UNESCO (United Nations Educational, Scientific and Cultural Organization).', 'jawa (ponorogo)'),
(12, 'Tari Jaran Kepang', 'jaranKepang.jpeg', 'Jaran kepang atau jathilan adalah salah satu dari berbagai jenis tarian kuda lumping di Indonesia. Tari jaran kepang jathilan berasal dari Ponorogo.', 'Jaran kepang atau jathilan adalah salah satu dari berbagai jenis tarian kuda lumping di Indonesia. Tari jaran kepang jathilan berasal dari Ponorogo. Tarian ini merupakan bagian dari satu kesatuan kelompok utuh Reog.\r\n<br/><br/>\r\nJaran kepang atau jathilan digambarkan sebagai pasukan gagah berani yang menunggangi kuda.\r\n<br/><br/>\r\nKata jathil berasal dari bahasa Jawa yaitu jarane jan thil-thilan yang berarti kuda yang menari tidak beraturan. Di beberapa kesempatan memang penari jathilan ini kerasukan, tetapi untuk penari pada masa sekarang para penari jaran kepang atau jathilan pada pertunjukan Reog tidak kerasukan sehingga tidak melakukan berbagai atraksi berbahaya seperti halnya dengan tarian kuda lumping dari daerah lain.\r\n<br/><br/>\r\nDi masa lalu, jaran kepang atau jathilan ditarikan oleh laki-laki. Namun pada perkembangannya pada masa sekarang, tari jaran kepang atau jathilan justru ditarikan oleh wanita. Tarian ini menggambarkan kekuatan prajurit berkuda namun ditarikan indah dalam gemulai gerakan penarinya. Di masa lalu, tarian ini sering kali terpisah dengan pertunjukan Reog. Tarian ini banyak dilakukan di pedesaan sebagai kesenian hiburan bagi rakyat. Namun di Ponorogo khususnya, tarian jaran kepang atau jathilan menjadi kesatuan yang tidak terpisahkan dari pertunjukan Reog.', 'jawa (ponorogo)'),
(13, 'Tari Kecak', 'kecak.jpeg', 'Tari kecak adalah seni tari yang berasal dari Bali. Seni tari kecak ini dilakukan oleh puluhan penari pria yang duduk melingkar sambil menyerukan \"cak,cak,cak\".', 'Tari kecak adalah seni tari yang berasal dari Bali. Seni tari kecak ini dipertunjukkan oleh puluhan penari laki-laki yang duduk berbaris dengan pola melingkar dan dengan irama tertentu menyerukan \"cak, cak, cak\" serta mengangkat kedua lengan.\r\n<br/><br/>\r\nDalam buku karya Resi Septiana Dewi yang berjudul \"Keanekaragaman Seni Tari Nusantara\", dalam menarikan tari kecak para penari duduk melingkar dan mengenakan kain khas Bali yang bermotif kotak-kotak seperti papan catur yang ditaruh di pinggang. Beberapa penari juga memerankan tokoh-tokoh seperti Rama, Shinta, Rahwana hingga Hanoman.\r\n', 'nusa tenggara (bali)'),
(14, 'Tari Pendet', 'pendet.jpeg', 'Tari Pendet awalnya merupakan tari pemujaan di pura, tempat ibadah umat Hindu di Bali, Indonesia. Tarian ini melambangkan penyambutan turunnya dewata ke alam dunia.', 'Tari Pendet pada awalnya merupakan tari pemujaan yang banyak diperagakan di pura, tempat ibadah umat Hindu di Bali, Indonesia. Tarian ini melambangkan penyambutan atas turunnya dewata ke alam dunia. Lambat-laun, seiring perkembangan zaman, para seniman Bali mengubah Pendet menjadi \"ucapan selamat datang\", meski tetap mengandung anasir yang sakral-religius. Pencipta/koreografer bentuk modern tari ini adalah I Wayan Rindi.\r\n<br/><br/>\r\nPendet merupakan pernyataan dari sebuah persembahan dalam bentuk tarian upacara. Tidak seperti halnya tarian-tarian pertunjukkan yang memerlukan pelatihan intensif, Pendet dapat ditarikan oleh semua orang, pemangkus pria dan wanita, dewasa maupun gadis.\r\n<br/><br/>\r\nTarian ini diajarkan sekadar dengan mengikuti gerakan dan jarang dilakukan di banjar-banjar. Para gadis muda mengikuti gerakan dari para wanita yang lebih senior yang mengerti tanggung jawab mereka dalam memberikan contoh yang baik.\r\n<br/><br/>\r\nTari Putri ini memiliki pola gerak yang lebih dinamis daripada Tari Rejang yang dibawakan secara berkelompok atau berpasangan. Biasanya ditampilkan setelah Tari Rejang di halaman pura dan biasanya menghadap ke arah suci (pelinggih) dengan mengenakan pakaian upacara dan masing-masing penari membawa sangku, kendi, cawan, dan perlengkapan sesajen lainnya', 'nusa tenggara (bali)'),
(15, 'Tari Sajojo', 'sajojo.jpeg', 'Tari Sajojo adalah tarian tradisional yang berasal dari daerah Papua. Tarian ini sering dijadikan penampilan acara adat, budaya, maupun sekadar hiburan saja.', 'Tari Sajojo adalah tarian tradisional yang berasal dari daerah Papua. Tarian ini sering dijadikan penampilan di berbagai acara, baik acara adat, budaya, maupun sekadar hiburan saja. Tarian ini sangat terkenal di Papua. Tarian ini bisa ditarikan oleh berbagai jenis kalangan, baik pria maupun wanita, tua maupun muda, karena tarian ini termasuk tarian pergaulan\r\n<br/><br/>\r\nBudaya tari ini tidak diketahui secara pasti asal usulnya. Menurut beberapa sumber, tarian ini sudah mulai ada sejak tahun 1990-an. Nama tari Sajojo ini diambil dari judul lagu yang mengiringinya yaitu \"Sajojo\" yang diciptakan oleh orang jawa dikarenakan lagu lirik lagu tersebut bukanlah diambil dari salah satu bahasa dari ratusan bahasa yang ada di papua sekarang. Lagu \"Sajojo\" merupakan lagu daerah dari Papua yang menceritakan tentang sebuah kisah perempuan cantik dari desa. Perempuan yang dicintai ayah dan ibu berikut para laki-laki desa. Perempuan yang didambakan laki- laki untuk bisa berjalan-jalan bersamanya. Meskipun gerakan tari ini tidak terlalu menggambarkan lirik lagu tersebut, tetapi iramanya yang penuh keceriaan dalam lagu tersebut sangat cocok dengan gerakan Tari Sajojo.', 'papua'),
(16, 'Tari Tempurung', 'tempurung.jpeg', 'Tari Tempurung adalah tarian daerah Sulawesi Utara yang memiliki nama lokal untuk cangkang kelapa. Pada pementasannya, banyak dekorasi dibuat dari tempurung. ', 'Tari Tempurung adalah tarian daerah Sulawesi Utara yang memiliki nama lokal untuk cangkang kelapa. Pada pementasannya, banyak dekorasi dibuat dari tempurung. Diketahui tempurung sangat bermanfaat di tempat terpencil. Ada yang menggunakannya sebagai mangkuk, nampan, cangkir, dan bahkan alat musik tradisional, seperti ditunjukkan pada tarian ini.\r\n<br /><br/ >\r\nCerita yang dicerminkan pada tarian ini berupa penghargaan keluarga petani untuk panenan kopra. Biasanya, tarian ini dilakukan oleh pasangan pria dan wanita.', 'sulawesi utara'),
(17, 'Tari Tide Tide', 'tidetide.jpeg', 'Tari Tide-tide merupakan tarian tradisional khas Halmahera Utara. Daerah Halmahera Utara merupakan kabupaten yang terletak di Provinsi Maluku Utara.', 'Tari Tide-tide merupakan tarian tradisional khas Halmahera Utara. Daerah Halmahera Utara merupakan kabupaten yang terletak di Provinsi Maluku Utara. Tarian ini merupakan tarian pergaulan sejenis tari ronggeng. \r\n<br/><br/>\r\nTari Tide-tide dipengaruhi budaya Islam dan Melayu. Hasil alkuturasi budaya ini sudah ada di Maluku Utara sejak abad ke XIV. Wujudnya dapat dilihat dari orkes pengiring tari, yaitu tifa, rebana, dan suling. Setelah bangsa Eropa (Portugis) masuk, Tari Tide-tide mengalami perubahan terutama pada orkes pengiring. Seperti jika dahulu, orkes pengiring menggunakan rebana diganti dengan biola, lalu suling diganti harmonika. Pengaruh portugis juga terlihat dalam penggunaan sapu tangan sebagai ornamen tarian. Tari Tide adalah sebuah maha karya yang dihasilkan oleh buah pemikiran manusia tentang gambaran sosial budaya masyarakat setempat. Pemikiran tersebut dituangkan dalam gerakan dan lantunan musik pengiring. \r\n<br/><br/>\r\nTari Tide-tide sudah ada sejak zaman dahulu. Menurut cerita masyarakat setempat, tarian muncul dari sebuah nyanyian atau lantunan. Hingga kini, tarian terus dilestarikan pada generasi penerus yang ada di Tobelo, kecamatan yang terdapat di Halmahera Utara.\r\n', 'maluku utara');

