--
-- Table structure for table `upacara_adat`
--

CREATE TABLE `upacara_adat` (
  `id` int(11) NOT NULL,
  `nama_upacara` varchar(55) NOT NULL,
  `gambar` varchar(55) NOT NULL,
  `preview` text NOT NULL,
  `deskripsi` longtext NOT NULL,
  `daerah` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Dumping data for table `upacara_adat`
--

INSERT INTO `upacara_adat` (`id`, `nama_upacara`, `gambar`, `preview`, `deskripsi`, `daerah`) VALUES
(1, 'Upacara Peusijuek', 'peusijuek.jpeg', 'Peusijuek adalah sebuah prosesi adat dalam budaya masyarakat Aceh yang masih dipraktikan hingga saat ini.', 'Tradisi peusijuek ini dilakukan pada hampir semua kegiatan adat dalam kehidupan masyarakat di Aceh. Misalnya ketika memulai sebuah usaha, menyelesaikan persengketaan, terlepas atau selesai dari musibah, menempati rumah baru, merayakan kelulusan, memberangkatkan dan menyambut kedatangan haji, kembalinya keluarga dari perantauan dan masih banyak lagi.\r\n<br /><br />\r\nPada kalangan masyarakat pedesaan di Aceh peusijuek merupakan prosesi adat yang cukup biasa dilakukan bahkan untuk hal-hal yang kecil sekalipun misalnya ketika membeli kendaraan baru atau ketika hendak menabur benih padi di sawah. Sementara bagi masyarakat perkotaan yang lebih modern tradisi peusijuek ini hanya dilakukan dalam kegiatan-kegiatan adat saja misalnya dalam prosesi adat perkawinan.\r\n<br /><br />\r\nRitual peusijuek ini mirip dengan tradisi tepung tawar dalam budaya Melayu. Di Aceh yang melakukan acara peusijuek adalah tokoh agama maupun adat yang dituakan ditengah masyarakat. Bagi kaum lelaki yang melakukan peusijuek adalah tokoh pemimpin agama Teungku (Ustadz) sedangkan bagi wanitanya adalah Ummi atau seorang wanita yang dituakan ditengah masyarakat. Diutamakan yang melakukan peusijuek ini adalah mereka yang memahami dan menguasai hukum agama sebab prosesi peusijuek ini diisi dengan acara mendoakan keselamatan dan kesejahteraan bersama sesuai dengan agama Islam yang dianut secara umum oleh masyarakat Aceh.', 'Provinsi Sumatera (Aceh)'),
(2, 'Upacara Mangokkal Holi', 'holi.jpeg', 'Mangongkal holi adalah sebuah tradisi membongkar kembali tulang-belulang dan menempatkannya kembali ke suatu tempat, tepatnya di sebuah tugu.', 'Mangokkal holi adalah salah satu kekayaan kebudayaan masyarakat Batak Toba yang hingga saat ini masih dilestarikan. Bentuk dari mangokkal holi ini adalah upacara. Tidak ada catatan yang pasti mengenai awal mula upacara mangokkal holi ini. Akan tetapi, secara tradisi dikatakan bahwa upacara ini akan ada jika ternyata arwah dari salah seorang dalam satu keluarga datang kepada salah seorang anggota keluarga yang masih hidup, baik lewat mimpi ataupun lewat penglihatan, lalu memohon untuk memindahkan tulang-belulangnya ke tempat yang lebih layak.\r\n\r\n<br /><br />\r\n\r\nUpacara Adat Batak Mangongkal Holi dilakukan oleh sekelompok marga untuk mendirikan monumen atau kuburan bagi nenek moyang mereka. Kuburan yang baru bisanya berbentuk monumen Tugu sebagai penanda asal muasal nenek moyang atau leluhur dari rumpun marga tersebut. Teknis dan proses pelaksanaan tradisi mangongkal holi dilakukan dengan mengangkat tulang belulang leluhur (disebut Oppung) yang telah lama meninggal. Dalam silsilah marga batak dimulai dari leluhur pertama, kedua, ketiga dst.\r\n\r\n<br /><br />\r\n\r\nSatu kuburan (Monumen Tugu) bisa menampung lebih dari empat atau lebih Jasad Leluhur dan di masukkan kedalam, tergantung besarnya Monumen dan banyak nya silsilah marga yang hendak dimasukkan Holi (tulang Belulang).\r\n\r\n<br /><br />\r\n\r\nJika suatu hari seorang anak Batak di perantauan di tanya dari mana asal marganya, Monumen Tugu leluhur ini lah sebagai penanda asal muasal marganya dan adanya Holi leluhur didalam kuburan yang berbentuk monumen atau tugu tersebut. Sebagai Contoh monumen tugu marga Sidauruk yang berada di pulau Samosir Sumatera Utara.', 'Provinsi Sumatera Utara (Batak)'),
(3, 'Upacara Tabuik', 'tabuik.jpeg', 'Tabuik adalah perayaan lokal dalam rangka memperingati Asyura, yang dilakukan oleh masyarakat Minangkabau di daerah pantai Sumatra Barat, khususnya di Kota Pariaman.', 'Festival ini termasuk menampilkan kembali Pertempuran Karbala, dan memainkan drum tassa dan dhol. Tabuik merupakan istilah untuk usungan jenazah yang dibawa selama prosesi upacara tersebut. Walaupun awal mulanya merupakan upacara Syi\'ah, akan tetapi mayoritas penduduk di Pariaman dan daerah lain yang melakukan upacara serupa, kebanyakan penganut Sunni. Di Bengkulu dikenal pula dengan nama Tabot.\r\n<br /><br />\r\nUpacara melabuhkan tabuik ke laut dilakukan setiap tahun di Pariaman pada 10 Muharram sejak 1831. Upacara ini diperkenalkan di daerah ini oleh Pasukan Tamil Muslim Syi\'ah dari India, yang ditempatkan di sini dan kemudian bermukim pada masa kekuasaan Inggris di Sumatra bagian barat.\r\n<br /><br />\r\nRitual pembuatan tabuik dimulai dengan pengambilan tanah dari sungai pada tanggal 1 Muharram. Tanah tersebut diletakkan dalam periuk tanah dan dibungkus dengan kain putih, kemudian disimpan dalam lalaga yang terdapat di halaman rumah tabuik. Lalaga adalah tempat berukuran 3x3 meter yang dipagari dengan parupuk, sejenis bambu kecil. Tanah yang dibungkus dengan kain putih adalah perumpamaan kuburan Husain. Tempat Ini akan diatapi dengan kain putih berbentuk kubah. Tanah tersebut akan dibiarkan disana sampai dimasukkan ke dalam tabuik pada tanggal Muharram.\r\n<br /><br />\r\nPada tanggal 5 Muharram dilakukan proses menebang batang pisang dengan cara sekali tebas pada malam hari. Ini melambangkan perumpamaan keberanian salah satu putra Imam Husain yang menuntut balas kematian bapaknya. Prosesi dilanjutkan pada tanggal 7 dan 8 muharram yang disebut Maatam dan Maarak sorban. Maatam merupakan personifikasi membawa jari-jari Husain yang berserakan ditebas pasukan Raja Yazid. Sedangkan Maarak Sorban melambangkan diaraknya bekas sorban untuk menyiarkan keberanian Husain memerangi musuh.\r\n<br /><br />\r\nPada tanggal 10 Muharram pagi, diadakan prosesi Tabuik naik pangkat, yaitu pemasangan bagian atas tabuik. Kemudian Tabuik diarak hingga akhirnya dibuang ke laut.', 'Provinsi Sumatera Barat'),
(4, 'Upacara Balimau Kasai', 'kasai.jpeg', 'Balimau adalah tradisi mandi menggunakan jeruk nipis yang berkembang di kalangan masyarakat Minangkabau dan biasanya dilakukan pada kawasan tertentu', 'Diwariskan secara turun temurun, tradisi ini dipercaya telah berlangsung selama berabad-abad.\r\n<br /><br />\r\nKawasan Lubuk Minturun populer dalam tradisi balimau di Padang\r\nLatar belakang dari balimau adalah membersihkan diri secara lahir dan batin sebelum memasuki bulan Ramadan, sesuai dengan ajaran agama Islam, yaitu menyucikan diri sebelum menjalankan ibadah puasa. Secara lahir, menyucikan diri adalah mandi yang bersih. Zaman dahulu tidak setiap orang bisa mandi dengan bersih, baik karena tidak ada sabun, wilayah yang kekurangan air, atau bahkan karena sibuk bekerja maupun sebab yang lain. Saat itu pengganti sabun di beberapa wilayah di Minangkabau adalah limau (jeruk nipis), karena sifatnya yang melarutkan minyak atau keringat di badan. Upacara serupa dengan nama yang sama Balimau ini juga terdapat pula di provinsi Lampung', 'Sumatera (Minangkabau)'),
(5, 'Upacara Tepuk Tepung Tawar', 'tawar.jpeg', 'Tepuk tepung tawar adalah salah satu bagian prosesi yang sakral dalam upacara adat budaya melayu.', 'Tepuk tepung tawar biasanya dilakukan pada acara pelantikan pejabat atau tokoh adat dan daerah, sunatan, khususnya acara pernikahan. Nama tepung tawar ini sendiri diambil dari salah satu bahan yang ikut dalam ramuan tepung tepung tawar itu, yaitu berupa tepung beras yang dicahar dengan air. Tepung tawar dilakukan sebagai perlambang mencurahkan rasa kegembiraan dan sebagai rasa syukur atas keberhasilan, hajat, acara atau niat yang akan dilaksanakan baik terhadap benda yang bergerak (manusia) maupun benda mati yang tidak bergerak.\r\n<br /><br/ >\r\nProsesi tepuk tepung tawar dilakukan dengan menepuk-nepukkan bedak pada punggung telapak tangan dan telapak tangan dan merenjis-renjiskan (memercikkan) air mawar pada orang yang akan di tepuk tepung tawari, dan dilengkapi dengan menabur-naburkan bunga rampai, beras putih, dan beras kuning ke seluruh badan orang yang bersangkutan atau yang ditepung tawari, kemudian diakhiri dengan doa oleh alim ulama.', 'Sumatera (Kepuluan Riau)'),
(6, 'Upacara Perang Ketupat', 'perangketupat.jpeg', 'Perang ketupat adalah acara adat di mana para peserta perang saling melempar ketupat sebagai senjata dalam perang ketupat. terdapat di Bangka Belitung', 'Perang ketupat Di Bangka Belitung, tepatnya di pulau Bangka sering disebut dengan ruah tempilang. Acara ini diselenggarakan setiap masuk Tahun Baru Islam (1 Muharam) di Pantai Tempilang, Tempilang, Bangka Barat. Pada saat acara ini berlangsung, penduduk sekitar pantai Tempilang yang menyelenggarakan acara ini akan membuka pintu rumah sebesar-besarnya untuk menyambut tamu-tamu yang berkunjung ke desa mereka.\r\n<br /><br />\r\nPerang ketupat adalah acara inti dari semua prosesi dari acara.Tujuan utama digelar perang ketupat sebagai kesejahteraan masyarakat. Semua orang-orang berkumpul di Pantai Tempilang, kemudian pada saat meriam dinyalakan bertanda acara dimulai. Orang-orang saling melempar ketupat ke setiap orang yang mereka temui. Acara ini cukup digemari oleh kaum muda di daerah Bangka. Banyak pemuda yang sengaja datang dari jauh, atau malah pulang dari perantauan untuk menghadiri acara ini.', 'Sumatera (Bangka Belitung)'),
(7, 'Upacara Besale', 'besale.jpeg', 'Besale adalah upacara atau ritual yang dilakukan oleh Suku Anak Dalam atau Orang Rimba untuk menghormati nenek moyang, mengharapkan keberkahan.', 'tak hanya itu upacara ini diharapkan dapat menjauhkan dari segala malapateka. Upacara Besale merupakan tradisi yang dilakukan turun-temurun dari nenek moyang hingga sekarang. Upacara Besale bertujuan untuk menyembuhkan orang yang sakit akibat roh jahat. Masyarakat Anak Dalam percaya bahwa penyakit yang diderita oleh keluarga atau kerabat merupakan pertanda dewa telah menurunkan malapetaka. Upacara Besale juga merupakan upaya masyarakat Anak Dalam untuk menjaga keseimbangan antara kehidupan yang nyata dengan yang gaib.\r\n<br /><br />\r\nBesale dilaksanakan pada malam hari, dipandu oleh seorang pawang atau dukun yang dipercaya memiliki ilmu berkomunikasi dengan alam gaib. Dukun atau pawang tersebut merupakan ketua adat yang sangat disegani oleh masyarakat Suku Anak Dalam. Pawang tersebut dapat menari dan bernyanyi dengan membawakan jampi-jampi penyembuhan terhadap orang sakit. Pawang atau dukun tersebut mengenakan pakaian yang serba putih, terdiri dari celana panjang putih, punutup kepala putih yang dililitkan di kepala, serta tudung putih. Dalam upacara Besale, masyarakat Anak Dalam memberikan sesajian kepada para dewa agar diberi kebaikan dan dijauhkan dari malapetaka. Sesajian tersebut terdiri dari caco, juwadah, bubur merah, tepung gandum, ayam panggang, telur, gelamai, bawang merah, dan lain sebagainya. Selain itu, sesajian juga terdiri dari kemenyan, dan 100 macam bunga. Perlengkapan upacara Besale adalah lonceng yang terbuat dari kuningan, dan dua mangkuk yang diisi dengan air jampi-jampi. Kain putih dicelupkan ke dalam air jampi-jampi kemudian diteteskan di mata orang yang sakit. Peralatan lainnya adalah rumah-rumahan kecil yang dibuat dari kayu, dan burung-burung yang dibuat menggunakan daun kelapa. Burung-burung anyaman daun kelapa tersebut berjumlah 19 ekor, dan disimpan di setiap rumah-rumahan. Bait lagu yang biasanya dinyanyikan dalam upacara Besale adalah: Betinjak dibungin baru sebiji Dijanjam baru setitik Angin baru serembus Beteduh di lanigt selebar payung\r\n\r\nPada saat upacara, dukun dilarang makan. Dukun menari-nari mengelilingi orang yang sakit atau boleh duduk dan berbaring di bawah rumah-rumahan yang telah dibuat. Dengan bernyanyi dan diiringi tabuhan gendang, dukun mengibaskan bunga pinang yang telah dicelupkan di air jampi-jampi kepada orang yang sakit.', 'Sumatera (Jambi)'),
(8, 'Upacara Sedekah Rame', 'rame.jpeg', 'Sedekah Rame merupakan salah satu upacara tradisional Melayu Lahat yang menetap di daerah Sumatra Selatan.', 'Disebut demikian, karena kegiatan dalam upacara itu diselenggarakan secara bersama-sama oleh masyarakat setempat, terutama bagi para anggotanya yang memiliki lahan persawahan. Nama upacara tersebut apabila diterjemahkan ke dalam bahasa Indonesia umum sepadan dengan \"Sedekah bersama-sama\". Upacara ini utamanya diselenggarakan oleh para petani dalam rangka kegiatan pertanian, mulai dari penyiangan sawah, pembibitan, dan penanaman sampai panen. Upacara ini dilakukan di daerah Sumatra Selatan yang bernama Tanah Badahe Setue yang letaknya berada di tengah-tengah sawah dengan cara membakar menyan, membuat ritual kepada roh, dan menyalahkan api unggun. Maksud dan tujuan upacara ini ialah agar masyarakat memperoleh perlindungan dari Tuhan Yang Maha Esa atas tanaman yang mereka tanam. Upacara ini juga dilaksanakan dengan satu harapan agar para petani memperoleh hasil panen yang lebih baik dan lebih banyak. Upacara ini merupakan ucapara yang sangat terkenal dikalangan petani, secara khusus di Sumatra Selatan.', 'Sumatera Selatan'),
(9, 'Upacara Bakar Gunung Api', 'gunungapi.jpeg', 'menyusun batok kelapa seperti tusuk sate sehingga membentuk gunungan, lalu membakarnya itulah maksud dari tradisi ini. ', 'Tradisi ini dilakukan oleh Suku Serawak, dipercaya bahwa batok kelapa menyimbolkan ucapan rasa syukur kepada Tuhan Yang Maha Esa dan juga doa bagi arwah keluarga agar hidup tentram di akhirat.Suku Serawai di Bengkulu melakukan tradisi Bakar Gunung Api sejak ratusan tahun. Tradisi ini dilakukan saat malam takbiran setelah shalat Isya. Batok kelapa disusun seperti tusuk sate sehingga tinggi menjulang kemudian dibakar sehingga api membumbung tinggi. Tradisi ini sebagai simbol\r\nUcapan syuur kepada Tuhan dan juga doa kepada arwah keluarga.\r\nApi dipercaya menghubungkan manusia dengan leluhur. Inilah yang dipercaya warga Bengkulu, hingga melahirkan tradisi Ronjok Sayak atau Bakar Gunung Api. Konon, Ronjok Sayak sudah dilakukan Suku Serawai sejak ratusan tahun lalu.\r\nTradisi itu dilakukan pada malam takbiran di depan rumah setiap warga. Ronjok Sayak menggunakan media batok kelapa yang disusun seperti tusuk sate hingga menjulang. Pembakaran batok itu dilakukan warga kampung secara serentak, usai melaksanakan salat isya.', 'Sumatera (Bengkulu)'),
(10, 'Upacara Ngebabali', 'ngebabali.jpeg', 'Upacara Ngebabali merupakan salah satu upacara tradisional yang berasal dari Lampung. Upacara ini bertujuan untuk melindungi hutan.', 'Pada masyarakat Lampung Barat. Ngebabali dilaksanakan saat membuka sawah atau perladangan baru disaat membersihkan lahan untuk ditanami atau pada saat mendirikan rumah dan kediaman yang baru atau juga untuk membersihkan tempat angker yang mempunyai aura gaib jahat. ', 'Sumatera (Lampung)'),
(11, 'Upacara Mapas', 'mapas.jpeg', 'Setelah beberapa hari melahirkan biasanya di keluarga Betawi akan terlihat sibuk untuk mempersiapkan berbagai belanjaan seperti sayuran, buah, dan lauk pauk.', 'Upacara adat mapas diadakan pada waktu ibu baru saja melahirkan dan ibu yang menyusui bayinya. Pada saat upacara mapas, si ibu yang baru saja melahirkan diharuskan memakan sayur papasan, yang isinya terdiri atas berbagai macam sayur-mayur yang banyak mengandung vitamin. Beberapa sayur yang biasanya ada ialah, kangkung, bayam, daun genjer, dan banyak lagi. Upacara ini mempunyai maksud agar ibu yang melahirkan memperoleh gizi yang cukup sehingga kesehatannya berangsur-angsur membaik. Disamping itu upacara ini sebagai bukti bahwa ibu yang baru melahirkan mempunyai kasih sayang yang besar terhadap anak yang baru dilahirkan. Bila kondisi badan ibu sehat, ia dapat mengasuh dan memberikan air susu yang cukup untuk si bayi sehingga bayi itu sehat. Tempat upacara mapas dilakukan di rumah tinggal keluarga yang melangsungkan. Upacara mapas ini diwariskan secara turun menurun dari generasi yang satu ke generasi berikutnya. Sekarang upacara ini sudah jarang terdengar, tetapi kegiatan makan sayur mayor masih terus dilanjutakn karena kesehatan sang ibu yang baru melahirkan harus dijaga. Para dokter pun menganjurkan kepada ibu hamil dan menyusui untuk menjaga kesehatan agar bayi dapat tumbuh sehat dan kuat.', 'Jawa (DKI Jakarta)'),
(12, 'Upacara Sisingaan', 'sisingaan.jpeg', 'Sisingaan atau Gotong Singa merupakan salah satu jenis seni pertunjukan rakyat khas Sunda dan berasal dari wilayah Kabupaten Subang.', 'Menggunakan media tandu yang dingkat oleh 4 orang dan diatasnya ditunggangi 1 orang atau pada umumnya oleh seorananak kecil. Kesenian ini dimainkan dengan musik, sehingga para pengangkat tandu melakukanya dengan gerakan tarian.\r\n<br /><br />\r\nSisingaan tersebut banyak yang berbagai jenis sisingaan yaitu naga, elang, banteng, siluman, dan lain-lain. Lalu cara bermain sisingaan harus membutuhan 4 orang untuk mengangkat sisingaan tersebut terus di iringin dengan musik lalu mulai ikuti pergerakan sisingaan dengan hanya menggoyang kaki dan badan, lalu bergoyang sambil berjalan menuju panggung pernikahan. kalau sudah sampai ke tempat pernikahan lalu diam di tempat yang lega lalu beratraksi, tapi atraksi tersebut harus ekstrem supaya penonton tersebut tertarik melihat aktrasi sisingaan,\r\n<br /><br />\r\nPertunjukan Sisingaan pada dasarnya dimulai dengan tetabuhan musik yang dinamis. Lalu diikuti oleh permainan Sisingaan oleh penari pengusung sisingaan, lewat gerak antara lain: pasang/kuda-kuda, bangkaret, masang/ancang-ancang, gugulingan, sepakan dua, langkah mundur, kael, mincid, ewag, jeblag, putar taktak, gendong singa, nanggeuy singa, angkat jungjung, ngolecer, lambang, pasagi tilu, melak cau, nincak rancatan, dan kakapalan. sebagai seni helaran, sisingaan bergerak terus mengelilingi kampung, desa, atau jalanan kota. Sampai akhirnya kembali ke tempat semula. Di dalam perkembangannya, musik pengiring lebih dinamis, dan melahirkan musik Genjring Bonyok dan juga Tardug.', 'Provinsi Jawa Barat'),
(13, 'Upacara Seren Taun', 'serentaun.jpeg', 'Seren Taun adalah upacara adat panen padi masyarakat Sunda yang dilakukan setiap tahun.', 'Upacara ini berlangsung khidmat dan semarak di berbagai desa adat Sunda. Upacara adat sebagai syukuran masyarakat agraris ini diramaikan ribuan masyarakat sekitarnya, bahkan dari beberapa daerah di Jawa Barat dan mancanegara. Beberapa desa adat Sunda yang menggelar Seren taun tiap tahunnya\r\n<br /><br />\r\nRangkaian ritual upacara Seren taun berbeda-beda dan beraneka ragam dari satu desa ke desa lainnya, akan tetapi intinya adalah prosesi penyerahan padi hasil panen dari masyarakat kepada ketua adat. Padi ini kemudian akan dimasukkan ke dalam leuit (lumbung) utama dan lumbung-lumbung pendamping. Pemimpin adat kemudian memberikan indung pare (induk padi/bibit padi) yang sudah diberkati dan dianggap bertuah kepada para pemimpin desa untuk ditanam pada musim tanam berikutnya.\r\n<br /><br />\r\nDi beberapa desa adat upacara biasanya diawali dengan mengambil air suci dari beberapa sumber air yang dikeramatkan. Biasanya air yang diambil berasal dari tujuh mata air yang kemudian disatukan dalam satu wadah dan didoakan dan dianggap bertuah dan membawa berkah. Air ini dicipratkan kepada setiap orang yang hadir di upacara untuk membawa berkah. Ritual berikutnya adalah sedekah kue, warga yang hadir berebut mengambil kue di dongdang (pikulan) atau tampah yang dipercaya kue itu memberi berkah yang berlimpah bagi yang mendapatkannya. Kemudian ritual penyembelihan kerbau yang dagingnya kemudian dibagikan kepada warga yang tidak mampu dan makan tumpeng bersama. Malamnya diisi dengan pertunjukan wayang golek.\r\n<br /><br />\r\nPuncak acara seren taun biasanya dibuka sejak pukul 08.00, diawali prosesi ngajayak (menyambut atau menjemput padi), lalu diteruskan dengan tiga pergelaran kolosal, yakni tari buyung, angklung baduy, dan angklung buncis-dimainkan berbagai pemeluk agama dan kepercayaan yang hidup di Cigugur.\r\n<br /><br />\r\nRangkaian acara bermakna syukur kepada Tuhan itu dikukuhkan pula melalui pembacaan doa yang disampaikan secara bergantian oleh tokoh-tokoh agama yang ada di Indonesia. Selanjutnya, dilaksanakan kegiatan akhir dari Ngajayak, yaitu penyerahan padi hasil panen dari para tokoh kepada masyarakat untuk kemudian ditumbuk bersama-sama. Ribuan orang yang hadir pun akhirnya terlibat dalam kegiatan ini, mengikuti jejak para pemimpin, tokoh masyarakat, maupun rohaniwan yang terlebih dahulu dipersilakan menumbuk padi. Puluhan orang lainnya berebut gabah dari saung bertajuk Pwah Aci Sanghyang Asri (Pohaci Sanghyang Asri).\r\n<br /><br />\r\nDalam upacara Seren taun dilakukan berbagai keramaian dan pertunjukan kesenian adat. Ritual seren taun itu sendiri mulai berlangsung sejak tanggal 18 Rayagung, dimulai dengan pembukaan pameran Dokumentasi Seni dan Komoditas Adat Jabar. Setiap hari dipertunjukkan pencak silat, nyiblung (musik air), kesenian dari Dayak Krimun, Indramayu, suling rando, tarawelet, karinding, dan suling kumbang dari Baduy.', 'Jawa (Banten)'),
(14, 'Upacara Ruwatan', 'ruwatan.jpeg', 'Ruwatan rambut gimbal adalah upacara pemotongan rambut pada anak-anak berambut gimbal yang dilakukan oleh masyarakat di daerah Dataran Tinggi Dieng, Jawa Tengah.', 'Ritual ruwatan yang diadakan pada tanggal satu Suro menurut Kalender Jawa ini bertujuan untuk membersihkan atau membebaskan anak-anak berambut gimbal dari sukerta/sesuker (kesialan, kesedihan, atau malapetaka).\r\n<br /><b />\r\nKepercayaan bahwa anak-anak berambut gimbal adalah keturunan Kiai Kolodete atau titipan Kanjeng Ratu Kidul (Nyai Roro Kidul) menjadi mitos turun-temurun dalam kehidupan masyarakat Dieng. Mereka juga percaya bahwa rambut gimbal hanya boleh dipotong bila anak yang bersangkutan sudah menghendaki/memintanya dan harus dilakukan melalui ritual ruwat atau ruwatan yang dipimpin tetua adat setempat. Uniknya, ruwatan ini hanya dapat dilakukan setelah orang tua memenuhi permintaan \"apa pun\" yang diajukan oleh sang anak. Konon jika pemotongan rambut gimbal tidak dilakukan melalui ritual sakral, rambut gimbal akan kembali tumbuh dan si anak cenderung sakit-sakitan.\r\n<br /><b />\r\nTradisi ruwatan rambut gimbal menarik perhatian masyarakat umum, terlebih orang-orang dari luar daerah Dieng. Seiring berjalannya waktu, tradisi ritual yang unik ini digelar sebagai pertunjukan budaya. Kemudian hari, ruwatan rambut gimbal yang dilakukan secara massal menjadi bagian penting dari Festival Budaya Dieng (Dieng Culture Festival) yang digelar setiap tahun. Tahun 2016 Kementerian Pendidikan dan Kebudayaan telah menetapkan tradisi \"Ruwatan Rambut Gimbal\" sebagai salah satu Warisan Budaya Takbenda Indonesia dari Jawa Tengah dengan domain Adat Istiadat Masyarakat, SItus, dan Perayaan-Perayaan.', 'Provinsi Jawa Tengah'),
(15, 'Upacara Sekaten', 'sekaten.jpeg', 'Festival Sekaten merupakan rangkaian kegiatan tahunan sebagai peringatan Maulid Nabi yang diadakan oleh dua keraton di Jawa yakni Surakarta dan Yogyakarta.', 'Rangkaian perayaan secara resmi berlangsung dari tanggal 5 dan berakhir pada tanggal 12 Mulud penanggalan Jawa (dapat disetarakan dengan Rabiul Awal penanggalan Hijriah). Beberapa acara penting perayaan ini adalah dimainkannya gamelan pusaka di halaman Masjid Agung masing-masing keraton, pembacaan riwayat hidup Nabi Muhammad dan rangkaian pengajian di serambi Masjid Agung dan, puncaknya adalah dengan diadakannya perayaan Grebeg Maulud sebagai bentuk syukur pihak istana dengan keluarnya sejumlah gunungan untuk diperebutkan oleh masyarakat.\r\n<br /><br />\r\nPerayaan ini dimeriahkan pula oleh pasar malam (biasa disebut \"Sekatenan\") yang dilangsungkan di alun-alun Utara masing-masing Keraton yang dimana berlangsung selama sekitar 40 hari, dimulai pada awal bulan Sapar (Safar).\r\n<br /><br />\r\nMenurut Puger (2002), awal mula dan maksud perayaan Sekaten dapat ditarik sejak mulainya kerajaan-kerajaan Islam di tanah Jawa, yaitu zaman Kesultanan Demak. Sekaten diadakan sebagai salah satu upaya menyiarkan agama Islam. Karena orang Jawa saat itu menyukai gamelan, pada hari raya Islam yaitu pada hari lahirnya Nabi Muhammad di halaman Masjid Agung Demak dimainkanlah gamelan, sehingga warga masyarakat berduyun-duyun datang di halaman masjid untuk mendengarkan gamelan dan sekaligus khutbah-khutbah mengenai keislaman.\r\n<br /><br />\r\nTradisi arak-arakan semacam sekaten, menurut satu cerita rakyat yang digali oleh Saddhono, telah dilakukan pada masa Kerajaan Majapahit. Kerajaan Demak, sebagai pelanjut dari \"wahyu\" kerajaan, mencoba meneruskan tradisi tersebut atas saran dari Wali Sanga\r\n<br /><br />\r\nPada hari pertama, upacara diawali saat malam hari dengan iring-iringan abdi dalem (punggawa kraton) bersama-sama dengan dua set gamelan Jawa Kyai Nogowilogo dan Kyai Gunturmadu. Iring-iringan ini bermula dari pendapa Ponconiti menuju masjid Agung di Alun-alun Utara dengan dikawal oleh prajurit Kraton. Kyai Nogowilogo akan menempati sisi utara dari Masjid Agung, sementara Kyai Gunturmadu akan berada di Pagongan sebelah selatan masjid. Kedua set gamelan ini akan dimainkan secara bersamaan sampai dengan tanggal 11 bulan Mulud, selama 7 hari berturut-turut. Pada malam hari terakhir, kedua gamelan ini akan dibawa pulang ke dalam Kraton.', 'Jawa (D.I. Yogyakarta.)'),
(16, 'Upacara Kasada', 'kasada.jpeg', 'Hari Raya Yadnya Kasada atau Pujan Kasada adalah sebuah hari upacara sesembahan berupa sesajen kepada Sang Hyang Widhi dalam manifestasinya sebagai Batara Brahma', 'Istilah yadnya dipopulerkan setelah orang Tengger menganut agama Hindu Dharma. Setiap bulan Kasada hari-14 dalam penanggalan Tengger diadakan upacara sesembahan atau sesajen untuk Sang Hyang Widhi dan para leluhur (Dewa Kusuma), yang diyakini berasal dari kisah Rara Anteng (Putri Raja Majapahit) dan Jaka Seger (Putra Brahmana)\r\n<br /><br />\r\nPasangan Roro Anteng dan Jaka Seger menyanggupinya dan kemudian didapatkannya 25 orang putra-putri, namun naluri orang tua tetaplah tidak tega bila kehilangan putra-putrinya. Pendek cerita, pasangan Rara Anteng dan Jaka Seger ingkar janji, Dewa menjadi marah dengan mengancam akan menimpakan malapetaka, kemudian terjadilah prahara keadaan menjadi gelap gulita kawah Gunung Bromo menyemburkan api.\r\n<br /><br />\r\nRaden Kusuma, anak bungsunya lenyap dari pandangan terjilat api dan masuk ke kawah Bromo, bersamaan hilangnya Kesuma terdengarlah suara gaib, \"Saudara-saudaraku yang kucintai, aku telah dikorbankan oleh orang tua kita dan Sang Hyang Widhi menyelamatkan kalian semua. Hiduplah damai dan tenteram, sembahlah Sang Hyang Widhi. Aku ingatkan agar kalian setiap bulan Kasada pada hari ke-14 mengadakan sesaji kepada Sang Hyang Widhi di kawah Gunung Bromo\". Kebiasaan ini diikuti secara turun temurun oleh masyarakat Tengger dan setiap tahun diadakan upacara Kasada di Poten lautan pasir dan kawah Gunung Bromo.\r\n<br /><br />\r\nSuku Tengger tidak seperti pemeluk agama Hindu pada umumnya, memiliki candi-candi atau Pura sebagai tempat peribadatan, namun bila melakukan peribadatan bertempat di punden, sanggar, pedanyang, dan poten. Namun setelah menganut agama Hindu Dharma, tiap desa di kawasan Tengger telah memiliki pura masing-masing dan biasanya dibangun dibawah sanggar agung desa.', 'Provinsi Jawa Timur'),
(17, 'Upacara Ngaben', 'ngaben.jpeg', 'Ngaben merupakan salah satu upacara yang dilakukan oleh umat Hindu di Bali dan tergolong sebagai upacara Pitra Yadnya.', 'Sawa Wedana adalah upacara ngaben dengan melibatkan jenazah yang masih utuh (tanpa dikubur terlebih dahulu) . Biasanya upacara ini dilaksanakan dalam kurun waktu 3-7 hari terhitung dari hari meninggalnya orang tersebut. Pengecualian biasa terjadi pada upacara dengan skala Utama, yang persiapannya bisa berlangsung hingga sebulan. Sementara pihak keluarga mempersiapkan segala sesuatu untuk upacara maka jenazah akan diletakkan di balai adat yang ada di masing-masing rumah dengan pemberian ramuan tertentu untuk memperlambat pembusukan jenazah. Dewasa ini pemberian ramuan sering digantikan dengan penggunaan formalin. Selama jenazah masih ditaruh di balai adat, pihak keluarga masih memperlakukan jenazahnya seperti selayaknya masih hidup, seperti membawakan kopi, memberi makan disamping jenazah, membawakan handuk dan pakaian, dll sebab sebelum diadakan upacara yang disebut Papegatan maka yang bersangkutan dianggap hanya tidur dan masih berada dilingkungan keluarganya.\r\n<br /><br />\r\nAsti Wedana adalah upacara ngaben yang melibatkan kerangka jenazah yang pernah dikubur. Upacara ini disertai dengan upacara ngagah, yaitu upacara menggali kembali kuburan dari orang yang bersangkutan untuk kemudian mengupacarai tulang belulang yang tersisa. Hal ini dilakukan sesuai tradisi dan aturan desa setempat, misalnya ada upacara tertentu di mana masyarakat desa tidak diperkenankan melaksanakan upacara kematian dan upacara pernikahan maka jenazah akan dikuburkan di kuburan setempat yang disebut dengan upacara Makingsan ring Pertiwi (Menitipkan di Ibu Pertiwi).\r\n<br /><br />\r\nSwasta adalah upacara ngaben tanpa memperlibatkan jenazah maupun kerangka mayat, hal ini biasanya dilakukan karena beberapa hal, seperti: meninggal di luar negeri atau tempat jauh, jenazah tidak ditemukan, dll. Pada upacara ini jenazah biasanya disimbolkan dengan kayu cendana (pengawak) yang dilukis dan diisi aksara magis sebagai badan kasar dari atma orang yang bersangkutan.', 'Nusa Tenggara (Bali)'),
(18, 'Upacara U\'a Pua', 'uapua.jpeg', 'Ua Pua disebut dalam bahasa melayu “Sirih Puan” yaitu satu rumpun tangkai bunga telur warna-warni yang ditempatkan ke dalam sebuah wadah segi empat.', 'Jumlah bunga telur berjumlah 99 (Sembilan Puluh Sembilan) tangkai yang sesuai dengan lafal Asma’ul Husna. Kemudian di tengah-tengahnya ada Kitab Suci umat Islam yaitu Al-Qur’an.\r\n<br /><br />\r\nU’a Pua ditempatkan ditengah sebuah Rumah Mahligai (Bima: Uma Lige) yang berbentuk segi empat berukuran 4×4 meter/segi. Bentuk dari Uma Lige dari keempat sisinya terbuka. Atap bersusun dua, sehingga para penari lenggo Mbojo yang terdiri dari empat orang gadis, dan penari lenggo melayu yang terdiri dari empat orang perjaka, beserta para penghulu melayu dan pengikutnya yang berada di atas dapat dilihat oleh seluruh mayarakat.\r\n<br /><br />\r\nUma Lige tersebut diusung oleh 44 orang pria yang berbadan kekar sebagai simbol dari keberadaan 44 dari Mbojo yang terbagi menurut 44 jenis keahlian dan ketrampilan yang dimilikinya sebagai bagian dari struktur Pemerintahan keSultanan Bima. Pawai start dari kampung melayu menuju Istana Bima untuk diterima oleh Sultan Bima dengan Amanah yang harus dikerjakan bersama yaitu memegang teguh ajaran Agama Islam.\r\n<br /><br />\r\nTujuannya adalah\r\n-Untuk memuliakan hari kelahiran Nabi Muhammad SAW.\r\n-Untuk mengenang kembali sejarah masuknya agama Islam di Tanah Bima dan sekaligus -sebagai wahana penghormatan atas jasa-jasa para penghulu Melayu beserta seluruh kaum keluarga yang telah menyebarkan agama Islam di Tanah Bima.\r\n-Meningkatkan pemahaman dan pengamalan Ajaran Islam yang bersumber dari Kitab Suci -Al-Qur’an dalam kehidupan sehari-hari masyarakat Bima dan ditunjukan dengan penyerahan Kitab Suci Al-Qur’an kepada Sultan sebagai pemimpin untuk dilaksanakan secara bersama-sama dengan seluruh rakyat.', 'Provinsi Nusa Tenggara Barat'),
(19, 'Upacara Adat Reba', 'adatreba.jpeg', 'Pesta Adat Reba, kegiatan kebudayaan masyarakat di NTT yang diselenggarakan dalam rangka menyambut pergantian tahun. ', 'Salah satu ciri khas dari festival budaya ini adalah memakan ubi bersama-sama dengan diiringi tarian adat suku bena bernama Besa Uwi. Pesta Reba biasanya diselenggarakan pada bulan Desember hingga Februari. Namun, puncak acara Pesta Reba akan diselenggarakan pada pertengahan Januari, yakni pada tanggal 14-16 Januari di setiap tahunnya.\r\n<br /><br />\r\nReba adalah tahun baru dalam kalender etnis Ngada di Kabupaten Ngada. Reba adalah pesta adat terbesar, pesta syukur atas kasih kebaikan dan penyelenggaraan Tuhan (Dewa Zeta Nitu Zale) yang dinikmati orang Ngada lewat hasil pertanian, peternakan, dan lainnya.\r\n<br /><br />\r\nReba dirayakan setahun sekali pada bulan Januari atau Februari tergantung petunjuk \'kepo wesu\' atau pemegang adat yang menentukan masa perayaannya. Dalam pesta Reba, rasa syukur manusia atas kebaikan Tuhan disimbolkan lewat Uwi (ubi tapi bukan singkong). Uwi diyakini roti kehidupan manusia pada masa \'in Illo tempore-nya\' orang Ngada. Sehingga uwi dalam ritus Reba adalah simbol yang utama, yang diungkapkan secara puitis sebagai berikut:\r\n<br /><br />\r\n\"Uwi meze go lewa laba. Lobo wi so’i Dewa. Kabu nga role nitu, ladu wai poso. Koba rako lizu. Uwi sedu peka rua wali. Kutu koe, dhano ana ko\'e. Sui moki, moki bhai moli\".\r\n<br /><br />\r\nTerjemahannya kira-kira begini: Ubi sebesar gong, sepanjang gendang. Pucuk menjulang kepada Tuhan. Akar tertanam memeluk Dewa Bumi, kayu penyangga setinggi gunung. Rambatnya mencapai langit. Ubi tetap bertumbuh tunas. Meski digali babi landak, tetap selalu ada. Diserang babi hutan, juga tak akan habis.\r\n<br /><br />\r\nSeperti Uwi, makanan yang bertahan lama, Reba tidak punah. Manusia pendukungnya tetap berkembang biak bersama alam lingkungan dan terus menghidupi Reba, dari generasi ke generasi.\r\n<br /><br />\r\nYang menarik, Reba tidak saja menjadi kesempatan istimewa bagi orang Ngada untuk berkumpul dalam rumah adat masing-masing. Reba juga menjadi kesempatan berahmat karena segala permusuhan, perselisihan dalam keluarga harus berakhir saat itu juga.', 'Provinsi Nusa Tenggara Timur'),
(20, 'Upacara Uluh Matei', 'uluhmatei.jpeg', 'Tiwah atau Tiwah Lale atau Magah Salumpuk Liau Uluh Matei ialah upacara kematian yang dilakukan oleh suku Dayak di Kalimantan Tengah.', 'Upacara Tiwah sendiri merupakan upacara sakral terbesar dalam Suku Dayak. Hal ini dikarenakan upacara Tiwah melibatkan sumber daya yang banyak dan waktu yang cukup lama. Upacara ini dilakukan bertujuan untuk mengantarkan jiwa atau roh manusia yang telah meninggal dunia menuju tempat yang dituju yaitu Lewu Tatau Dia Rumpang Tulang, Rundung Raja Dia Kamalesu Uhate, Lewu Tatau Habaras Bulau, Habusung Hintan, Hakarangan Lamiang atau Lewu Liau yang letaknya di langit ke tujuh. Pada tahun 2014, upacara Tiwah telah dimasukan ke dalam penetapan Warisan Budaya Takbenda Indonesia yang dilakukan oleh Kementerian Pendidikan dan Kebudayaan\r\n<br /><br />\r\nBagi masyarakat Dayak Ngaju yang umumnya memeluk kepercayaan lokal yakni Kaharingan, kematian merupakan hal akhir yang dijalani manusia di bumi, dan juga awal untuk mencapai dunia keabadian yang menjadi tempat asal manusia. Dunia kekal abadi tersebut adalah dunia roh tempat manusia mencapai titik kesempurnaanya. Dalam mitos suku Dayak Ngaju, awalnya manusia tidak mengenal kematian. Hal tersebut dikarenakan kehidupan duniawi adalah sesuatu yang kekal. Namun, suatu ketika manusia berbuat kesalahan dan akhirnya kekekalan hidup duniawinya dicabut oleh dewata. Manusia yang meninggal akan melanjutkan perjalanannya ke dunia para arwah. Manusia yang telah berganti wujud menjadi arwah ini disebut dengan Lio/Liau/Liaw. Liau oleh masyarakat Dayak Ngaju wajib diantar ke dunia arwah yakni alam tertinggi yang disebut Lewu Liaw atau Lewu Tatau. Proses pengantaran ini melalui serangkaian upacara kematian, yakni upacara Tiwah.\r\n<br /><br />\r\nPenyelenggaran upacara Tiwah bagi masyarakat Dayak Ngaju dianggap sesuatu yang wajib secara moral dan sosial. Pihak keluarga yang ditinggalkan merasa memilki kewajiban untuk mengantar arwah sanak saudara yang meninggal ke dunia roh. Selain itu, dalam kepercayaan Dayak Ngaju, arwah orang yang belum diantar melalui upacara Tiwah akan selalu berada di sekitar lingkungan manusia yang masih hidup. Keberadaan mereka dianggap membawa gangguan berupa munculnya peristiwa gagal panen, penyakit, dan bahaya-bahaya lainnya.\r\n', 'Provinsi Kalimantan Tengah'),
(21, 'Upacara Aruh Baharin', 'aruhbaharin.jpeg', 'Aruh Baharin adalah upacara adat serta keagamaan Kaharingan digelar masyarakat Suku Dayak Dusun Halong di Desa Kapul, Kec. Halong, Kab. Balangan, Prov. KalSel', 'Setelah musim panen padi ladang (pahumaan) usai. Dalam tradisi mereka, beras hasil panen (baras hanyar) belum boleh dimakan sebelum menggelar upacara Aruh Baharin. Biasanya, upacara adat ini dipusatkan di balai adat agama kaharingan atau di tempat-tempat khusus yang sengaja dibuat untuk keperluan ritual keagamaan dan adat\r\n<br /><br />\r\nPelaksanaan upacara adat yang digelar selama tujuh hari tujuh malam ini memiliki tiga tahapan.Pertama, tahapan persiapan. Pada tahapan ini, kaum laki-laki dan perempuan berbagi tugas. Kaum laki-laki bertugas membuat dan menghias tempat pemujaan, mencari kayu bakar, dan memasak nasi. Sedangkan kaum perempuan bertugas membersihkan beras, membuat ketupat, memasak lemang, dan memasak sayur untuk keperluan upacara.\r\n<br /><br />\r\nTahapan kedua adalah pemanggilan arwah leluhur. Tujuannya agar para amah leluhur ikut menghadiri dan merestui upacara adat Aruh Baharin. Untuk memanggil arwah para leluhur, digelar beberapa ritual yang dipimpin oleh para balian (pemuka adat Suku Dayak) yang dimulai pada malam ketiga hingga malam keenam. Ritual-ritual tersebut antara lain, pertama, ritual Balai Tumarang. Ritual ini bertujuan memanggil para arwah yang pemah berkuasa di daerah tersebut, termasuk arwah raja-raja dari Pulau Jawa. Kedua, ritual Sampan Dulang atau ritual Kelong. Ritual ini bertujuan memanggil arwah Balian Jaya atau Nini Uri, yang dipercayai sebagai leluhur orang Dayak. Ketiga, ritual Hyang Lembang. Tujuannya adalah memanggil arwah raja-raja dari Kesultanan Banjar pada masa lampau. Keempat, ritual Dewata Ritual ini menggambarkan keberhasilan Datu Mangku Raksa Jaya menembus alam dewa dengan cara bertapa. Kelima, ritual Hyang Dusun. Ritual ini mengisahkan raja-raja Dayak yang mampu menguasai sembilan benua atau sembilan pulau.\r\n<br /><br />\r\nTahapan ketiga adalah puncak upacara adat aruh baharin. Puncak upacara ditandai dengan penyembelihan beberapa ekor kerbau, kambing, dan ayam yang dipimpin oleh para balian. Sebagian dari daging hewan tersebut dimasak untuk dimakan bersama-sama dan sebagiannya lagi untuk sesaji. Sebelum dilakukan pemberkatan oleh para balian dan kemudian dilarungkan ke sungai Balangan, sesaji tersebut terlebih dahulu diludahi oleh anggota masyarakat adat yang menjadi penyelenggara upacara. Dalam upacara ini, meludahi sesaji sebagai simbol agar dijauhkan dari malapetaka.', 'Provinsi Kalimantan Selatan'),
(22, 'Upacara Dahau', 'dahau.jpeg', 'Festival Dahau adalah sebuah festival yang diselenggarakan di kota Sendawar, Kabupaten Kutai Barat, Provinsi Kalimantan Timur, Indonesia.', 'Festival ini digelar dalam rangka memperingati hari jadi Kabupaten Kutai Barat setiap tahunnya, Biasanya festival ini digelar pada akhir Oktober hingga awal November. Budaya dan adat yang berkembang di Kutai Barat ditampilkan dalam festival ini dan melibatkan enam sub etnis di Kutai Barat, yakni Dayak Tunjung, Benuaq, Bahau, Kenyah, dan Aoheng serta Melayu.', 'Provinsi Kalimantan Timur');

